INSERT INTO `danhmuc_2` (`id`, `danhmuc1_id`, `name`, `name_url`, `img`, `position`, `content_short`, `title`, `keyword`, `description`) VALUES
(2, 2, 'Du lịch Sapa', 'du-lich-sapa', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-trong-nuoc/sapa/mocchau.jpg', 1, '<p style="text-align: justify;">\r\n	Nằm ph&iacute;a T&acirc;y Bắc tổ quốc,&nbsp;Sapa&nbsp;ẩn chứa bao điều kỳ diệu của cảnh sắc thi&ecirc;n nhi&ecirc;n, con người. Thị trấn trong m&acirc;y hấp dẫn du kh&aacute;ch với quang cảnh n&uacute;i non h&ugrave;ng vĩ c&ugrave;ng nhiều trải nghiệm độc đ&aacute;o kh&aacute;m ph&aacute; cuộc sống của đồng b&agrave;o d&acirc;n tộc thiểu số.</p>\r\n<p style="text-align: justify;">\r\n	Sapa l&agrave; một huyện v&ugrave;ng cao của tỉnh L&agrave;o Cai, một v&ugrave;ng đất khi&ecirc;m nhường, lặng lẽ nhưng ẩn chứa bao điều kỳ diệu của cảnh sắc thi&ecirc;n nhi&ecirc;n. Phong cảnh thi&ecirc;n nhi&ecirc;n của Sapa được kết hợp với sức s&aacute;ng tạo của con người c&ugrave;ng với địa h&igrave;nh của n&uacute;i đồi, m&agrave;u xanh của rừng, như bức tranh c&oacute; sự sắp xếp theo một bố cục h&agrave;i ho&agrave; tạo n&ecirc;n một v&ugrave;ng c&oacute; nhiều cảnh sắc thơ mộng hấp dẫn. Từ trung t&acirc;m th&agrave;nh phố bạn c&oacute; thể ph&oacute;ng tầm mắt của m&igrave;nh thỏa th&iacute;ch ngắm nh&igrave;n những đỉnh n&uacute;i ng&uacute;t ng&agrave;n&hellip;</p>\r\n', 'Du lịch Sapa | Tour du lịch sapa | Mix Tourist', 'du lịch sapa, tour du lịch sapa', 'Cùng MixTourist tham gia các tour du lịch Sapa để khám phá cảnh quan, lễ hội, văn háo và ẩm thực Sapa  Việt Nam'),
(3, 2, 'Du lịch Đông Bắc', 'du-lich-dong-bac', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-trong-nuoc/sapa/mocchau.jpg', 2, '<p style="text-align: justify;">\r\n	H&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Đ&ocirc;ng Bắc sẽ đưa bạn đến với những thắng cảnh non cao h&ugrave;ng vĩ của H&agrave; Giang, hay th&aacute;c nước cuồn cuộn v&agrave; tuyệt đẹp của Cao Bằng v&agrave; kh&ocirc;ng qu&ecirc;n gh&eacute; qua thăm khung cảnh thi&ecirc;n nhi&ecirc;n thơ mộng, hữu t&igrave;nh tại Hồ Ba Bể (Bắc Kạn). Đ&ocirc;ng Bắc c&ograve;n nổi tiếng với những m&oacute;n ăn đặc sản v&ugrave;ng cao, những lễ hội d&acirc;n gian v&agrave; m&ugrave;a hoa D&atilde; Quỳ nở rực hồng phương bắc.</p>\r\n', 'Du lịch Đông Bắc | Tour du lịch Đông Băc | MixTourist', 'Du lịch đông bắc, tour du lịch đông bắc, ', 'Tour du lịch Đông Bắc của MixTourist sẽ đưa bạn đến với những thắng cảnh non cao hùng vĩ, những thác nước cuồn cuộn hay khung cảnh thiên nhiên thơ mộng, hữu tình'),
(4, 2, 'Du lịch Tây Bắc', 'du-lich-tay-bac', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/848166.jpg', 3, NULL, 'Du lịch Tây Bắc', 'Du lịch Tây Bắc', 'Du lịch Tây Bắc'),
(5, 2, 'Du lịch Hà Nội', 'du-lich-ha-noi', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/848166.jpg', 4, NULL, 'Du lịch Hà Nội | Tour du lịch Hà Nội', 'du lịch hà nội, tour du lịch hà nội', 'Du lịch thủ đô Hà Nội ngàn năm văn hiến cùng Mix Tourist'),
