INSERT INTO `tour` (`id`, `tour_quoc_te`, `DanhMuc1Id`, `DanhMuc2Id`, `danhmuc_multi`, `promotion`, `packages`, `name`, `name_url`, `count_down`, `code`, `img`, `price_sales`, `price`, `price_2`, `price_3`, `price_4`, `price_5`, `price_6`, `durations`, `departure`, `destination`, `departure_time`, `vehicle`, `hotel`, `summary`, `highlights`, `schedule`, `price_list`, `content`, `list_img`, `title`, `keyword`, `description`, `inclusion`, `exclusion`) VALUES
(11, 1, 10, 22, '22', 1, 1, 'Tour du lịch Myanmar du xuân tết 2017', 'tour-du-lich-myanmar-du-xuan-tet-2017', '2017-01-20 10:10:00', '', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/myanmar/du-lich-ghep-tet-myanmar-mixtourist.jpg', '13900000', '13500000', '', '', '', '', '', '5 Ngày 4 Đêm', '1', 'Yangon, Kyaikhtiyo, Bago', '31/1/2017', 'Máy bay, Ô tô', 3, '<p style="text-align: justify;">\r\n	&ldquo;<em>Xứ sở ch&ugrave;a v&agrave;ng</em>&rdquo; l&agrave; danh hiệu nhiều du kh&aacute;ch từng đặt ch&acirc;n đến&nbsp;Myanmar&nbsp;ưu &aacute;i d&agrave;ng tặng cho v&ugrave;ng đất n&agrave;y.&nbsp;<strong><a href="http://tourdulichmyanmar.vn/tour/with/Tour-du-lich-Myanmar-5N-4d/229" target="_blank">Tour du lịch Myanmar</a></strong>,&nbsp;tour du lịch đến với xứ sở ch&ugrave;a v&agrave;ng&nbsp; lu&ocirc;n ẩn chứa sức h&uacute;t kh&oacute; cưỡng kh&ocirc;ng chỉ bởi vẻ cổ k&iacute;nh của những di t&iacute;ch t&ocirc;n gi&aacute;o quy m&ocirc; v&agrave; tinh tế về kiến tr&uacute;c m&agrave; c&ograve;n bởi vẻ đẹp thi&ecirc;n nhi&ecirc;n hoang sơ, b&igrave;nh y&ecirc;n hiếm c&oacute;. C&ugrave;ng&nbsp;<strong>Mix Tourist</strong>&nbsp;trải nghiệm&nbsp;<strong><a href="http://tourdulichmyanmar.vn/tour/with/Tour-du-lich-Myanmar-5N-4d/229" target="_blank">tour du lịch Myanmar 5 ng&agrave;y 4 đ&ecirc;m</a></strong>, qu&yacute; vị chắc chắn sẽ c&oacute; được những cung bậc cảm x&uacute;c thực sự mới lạ v&agrave; kh&oacute; qu&ecirc;n.</p>\r\n', '<p style="text-align: justify;">\r\n	✔ Kh&aacute;m ph&aacute; <strong>th&agrave;nh phố Yangon</strong> &ndash; th&agrave;nh phố mang vẻ đẹp vừa hiện đại, vừa cỏ k&iacute;nh với rất nhiều thắng cảnh: ch&ugrave;a Sule, ch&ugrave;a Shwedagon, điện Karaweik, ch&ugrave;a Chaukhtutgyi, tượng phật Kyaikpun, ch&ugrave;a Kyaikhkauk &ndash; ng&ocirc;i ch&ugrave;a nổi tr&ecirc;n mặt nước &hellip;<br />\r\n	✔ <strong>Ch&ugrave;a Shwedagon</strong> được biết đến l&agrave; ch&ugrave;a v&agrave;ng nổi tiếng thế giới, l&agrave; nơi lưu giữ 8 sợi t&oacute;c của Đức Phật &ndash; bảo vật thi&ecirc;ng li&ecirc;ng nhất của Phật gi&aacute;o.<br />\r\n	✔ <strong>Th&agrave;nh phố Bago</strong> - th&agrave;nh phố mang dấu t&iacute;ch về thời k&igrave; ho&agrave;ng kim của vua Bayinnaung &ndash; khi &ocirc;ng hai lần chinh phục Ayutthaya.<br />\r\n	✔ <strong>Ch&ugrave;a H&ograve;n Đ&aacute; V&agrave;ng</strong> hay c&ograve;n gọi l&agrave; <strong>Ch&ugrave;a Kyaithtiyo</strong>, được x&acirc;y dựng tr&ecirc;n một tảng đ&aacute; to, h&igrave;nh quả trứng, nằm ngay bờ một v&aacute;ch đ&aacute; dựng đứng...</p>\r\n', '<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;">NG&Agrave;Y 01&nbsp; H&Agrave; NỘI &ndash; YANGON</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong> 20h30&nbsp;Hướng dẫn vi&ecirc;n&nbsp; đ&oacute;n qu&yacute; kh&aacute;ch tại điểm hẹn khởi h&agrave;nh đi &nbsp;s&acirc;n bay Nội b&agrave;i l&agrave;m thủ tục bay&nbsp;EK389&nbsp;đi Yangon l&uacute;c 22h45</p>\r\n<p style="text-align: justify;">\r\n	<strong>00h20:</strong>&nbsp;Đến s&acirc;n bay Yangon, HDV đia phương ch&agrave;o đ&oacute;n qu&yacute; kh&aacute;ch về trung t&acirc;m th&agrave;nh phố. Nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.</p>\r\n<p style="text-align: justify;">\r\n	Tối:. Nghỉ đ&ecirc;m kh&aacute;ch sạn tại Yangon</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;">NG&Agrave;Y 02&nbsp; YANGON - BAGO - KYAIKHTIYO &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ( Ăn S&aacute;ng, Trưa, Tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng: </strong>Sau bữa s&aacute;ng, đo&agrave;n sẽ khởi h&agrave;nh đi Bago - c&aacute;ch Yangon 80km. Tr&ecirc;n đường đến Bago qu&yacute; kh&aacute;ch gh&eacute; thăm c&ocirc;ng tr&igrave;nhTượng đ&agrave;i Thouk Chan&nbsp;&ndash; nơi an nghỉ của 27000 phần mộ liệt sỹ qu&acirc;n đồng minh trong chiến tranh thế giới thứ II. Sau đ&oacute; đo&agrave;n tiếp tục đến th&agrave;nh phố Bago v&agrave; gh&eacute; thăm ch&ugrave;a&nbsp;Tượng phật 4 mặt Kyaitpun, tiếp tục chương tr&igrave;nh, đo&agrave;n sẽ được dự nghi thức cung tiến bữa trưa cho c&aacute;c nh&agrave; sư tại&nbsp;Trường thiền viện Kyat Khat Wine&nbsp;&ndash; nơi đang c&oacute; hơn 700 nh&agrave; sư ngay ng&agrave;y ng&agrave;y tu niệm. Du kh&aacute;ch d&ugrave;ng bữa tại nh&agrave; h&agrave;ng địa phương với c&aacute;c m&oacute;n ăn đặc sản của Myanmar.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong> Đo&agrave;n tiếp tục thăm quan&nbsp;Ch&ugrave;a Phật nằm Shwei Tha Lyaung&nbsp;&ndash; Ng&ocirc;i ch&ugrave;a c&oacute; hơn 1000 năm lịch sử v&agrave; lắng nghe truyền thuyết về uy quyền, sức mạnh của đức phật. Đo&agrave;n tiếp tục thăm quan ch&ugrave;a v&agrave;ng&nbsp;Shwei Mor Do&nbsp;v&agrave; Cung điện ho&agrave;ng gia&nbsp;Kanbawza thadi&nbsp;&ndash; Kinh đ&ocirc; của Myanmar v&agrave;o thế kỷ thứ 15,16.</p>\r\n<p style="text-align: justify;">\r\n	Sau đ&oacute;, qu&yacute; kh&aacute;ch khởi h&agrave;nh đi Golden Rock, dừng ch&acirc;n v&agrave; nghỉ đ&ecirc;m tại&nbsp;Resort Eternity.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;">NG&Agrave;Y 03 &nbsp; KYAIKHTIYO &ndash; H&Ograve;N Đ&Aacute; V&Agrave;NG - &nbsp;YANGON&nbsp;( Ăn S&aacute;ng, Trưa, Tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Sau bữa s&aacute;ng, đo&agrave;n khởi h&agrave;nh l&ecirc;n n&uacute;i, tới trạm dừng ch&acirc;n&nbsp;Kin Pun&nbsp;(c&aacute;ch Yangon 194 km), Qu&yacute; kh&aacute;ch l&ecirc;n n&uacute;i bằng &ldquo;xe chuy&ecirc;n dụng của ch&iacute;nh phủ để l&ecirc;n&nbsp;Ch&ugrave;a H&ograve;n Đ&aacute; V&agrave;ng&nbsp;hay c&ograve;n gọi l&agrave;&nbsp;Ch&ugrave;a Kyaithtiyo&nbsp;&ndash;&nbsp;một tuyệt t&aacute;c kỳ diệu được kết hợp bởi sức s&aacute;ng tạo v&ocirc; hạn của con người v&agrave; tạo h&oacute;a, Ch&ugrave;a Kyaithtiyo l&agrave; một trong những ng&ocirc;i ch&ugrave;a nổi tiếng v&agrave; cổ k&iacute;nh nhất của đất nước Myanmar &ndash; được nhiều người tin rằng được x&acirc;y dựng hơn 2500 năm trước, khi Đức Phật c&ograve;n sống.&nbsp;Tại đ&acirc;y, Qu&yacute; kh&aacute;ch sẽ được chi&ecirc;m ngưỡng một tảng đ&aacute; thi&ecirc;ng nằm cheo leo tr&ecirc;n bờ v&aacute;ch đ&aacute; v&agrave; được bao bọc bởi h&agrave;ng ngh&igrave;n l&aacute; v&agrave;ng d&aacute;t mỏng.&nbsp;Tương truyền rằng, tảng đ&aacute; c&oacute; thể đứng vững như vậy l&agrave; nhờ c&oacute; 1 sợi t&oacute;c của Phật Tổ được đặt trong th&aacute;p thờ cao 7,3 m nằm tr&ecirc;n khối đ&aacute;. Qu&yacute; kh&aacute;ch c&ograve;n c&oacute; cơ hội thưởng ngoạn cảnh ho&agrave;ng h&ocirc;n b&igrave;nh y&ecirc;n đầy m&agrave;u sắc tr&ecirc;n đỉnh n&uacute;i.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>Sau bữa trưa đo&agrave;n khởi h&agrave;nh trở lại th&agrave;nh phố Yangon,&nbsp;đo&agrave;n đi thăm&nbsp;Ch&ugrave;a V&agrave;ng Shwedagon&nbsp;&ndash;&nbsp;một trong những chốn linh thi&ecirc;ng bậc nhất của đất nước Myanmar. D&ugrave; ban ng&agrave;y hay ban đ&ecirc;m, ng&ocirc;i ch&ugrave;a lu&ocirc;n ph&aacute;t ra những tia s&aacute;ng v&agrave;ng lấp l&aacute;nh v&igrave; chỉ ri&ecirc;ng t&ograve;a th&aacute;p trung t&acirc;m đ&atilde; được chạm khắc v&ocirc; c&ugrave;ng tinh vi với h&agrave;ng chục tấn v&agrave;ng d&aacute;t mỏng h&agrave;ng ng&agrave;n vi&ecirc;n kim cương, đ&aacute; qu&yacute; c&ugrave;ng h&agrave;ng ngh&igrave;n chiếc chu&ocirc;ng v&agrave;ng. Trong ch&ugrave;a c&ograve;n lưu giữ 8 sợi t&oacute;c của Đức Phật Th&iacute;ch Ca - bảo vật linh thi&ecirc;ng nhất của Phật gi&aacute;o.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối: </strong>Qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng địa phương. Nghỉ đ&ecirc;m kh&aacute;ch sạn Central Hotel *** hoặc tương đương</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;">NG&Agrave;Y 04 : THAM QUAN YANGON&nbsp;( Ăn S&aacute;ng, Trưa, Tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Sau bữa s&aacute;ng tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch đi tham quan&nbsp;Ch&ugrave;a Chaukhtatgyi, nơi c&oacute; tượng Phật nằm khổng lồ d&agrave;i 72m, cao 16m nằm uy nghi giữa trung t&acirc;m th&agrave;nh phố,&nbsp;Qu&yacute; kh&aacute;ch đi thăm quan một v&ograve;ng quay trung t&acirc;m của th&agrave;nh phố Yangon, qu&yacute; kh&aacute;ch c&oacute; thể chụp những tấm h&igrave;nh kỷ niệm tại khu&nbsp;vực Ch&ugrave;a Sule,&nbsp;T&ograve;a nh&agrave; thị ch&iacute;nh th&agrave;nh phố,&nbsp;t&ograve;a &aacute;n tối cao&nbsp;v&agrave; rất nhiều t&ograve;a nh&agrave; mang đậm kiến tr&uacute;c phương T&acirc;y được x&acirc;y dựng từ thời kỳ thuộc địa. tham quan một số cửa h&agrave;ng chế t&aacute;c v&agrave; b&aacute;n c&aacute;c m&oacute;n qu&agrave; tặng từ ngọc v&agrave; đ&aacute; qu&yacute;, những sản vật m&agrave; thi&ecirc;n nhi&ecirc;n ban tặng cho mảnh đất Myanmar</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong>&nbsp;Sau khi d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng địa phương, đo&agrave;n tự do mua sắm tại&nbsp;Chợ Bogyoke Aung San(Chợ đ&oacute;ng cửa v&agrave;o ng&agrave;y nghỉ lễ v&agrave; thứ 2) &ndash; đ&acirc;y l&agrave; ng&ocirc;i chợ nổi tiếng nhất Yangon, đ&atilde; c&oacute; tr&ecirc;n 70 năm tuổi, với hơn 2000 quầy h&agrave;ng b&aacute;n từ con rối nổi tiếng của Mandalay, đến những vi&ecirc;n đ&aacute; qu&yacute; lấp l&aacute;nh.&nbsp;Ch&ugrave;a Phật Ngọc&nbsp;nơi c&oacute; tượng phật tạc bằng đ&aacute; ngũ hoa cương nặng 500 tấn, thăm quan&nbsp;ch&ugrave;a Voi trắng. Ăn tối</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;">NG&Agrave;Y&nbsp; 05 : YANGON &nbsp;- BẢO T&Agrave;NG X&Aacute; LỢI - &nbsp;H&Agrave; NỘI&nbsp;( Ăn S&aacute;ng, Trưa)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Sau bữa s&aacute;ng tại kh&aacute;ch sạn, qu&yacute; kh&aacute;ch sẽ được đưa tới thăm quan&nbsp;Bảo t&agrave;ng X&aacute; lợi Phật Thone Wain v&agrave; l&agrave;m lễ thỉnh X&aacute; lợi&nbsp;cầu may do đ&iacute;ch th&acirc;n Đức Tăng Thống l&agrave;m lễ trao X&aacute; lợi. (Chi ph&iacute; giọt dầu thỉnh x&aacute; lợi do qu&yacute; kh&aacute;ch tự t&uacute;c).&nbsp;Đo&agrave;n ra s&acirc;n bay, đ&oacute;n chuyến bay&nbsp;EK 388&nbsp;về H&agrave; Nội l&uacute;c 12h35</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>15h05- Về đến s&acirc;n bay nội b&agrave;i. Xe đ&oacute;n qu&yacute; kh&aacute;ch về lại điểm hẹn ban đầu.&nbsp;Kết th&uacute;c h&agrave;nh tr&igrave;nh tour&nbsp;<strong><a href="http://tourdulichmyanmar.vn/tour/with/Tour-du-lich-Myanmar-5N-4d/116">du lịch myanmar 5 ngày 4 đ&ecirc;m</a></strong>.</p>\r\n<p style="text-align: justify;">\r\n	<em>Hẹn gặp lại qu&yacute; kh&aacute;ch trong c&aacute;c h&agrave;nh tr&igrave;nh sau!</em></p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">GI&Aacute; TOUR : 13,900,000 VNĐ&nbsp;/KH&Aacute;CH - GI&Aacute; KHUYẾN MẠI: 13,500,000 VNĐ/ KH&Aacute;CH</span></span></h2>\r\n<p style="text-align: center;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">( &Aacute;p dụng cho đo&agrave;n từ 20 &nbsp;kh&aacute;ch trở l&ecirc;n )</span></span></strong></p>\r\n<h3 style="text-align: justify;">\r\n	<span style="font-size:14px;"><span style="color:#ff0000;">LƯU &Yacute;:</span></span></h3>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Gi&aacute; tour t&iacute;nh theo USD, USD được t&iacute;nh theo tỷ gi&aacute; 1 USD = 21.000vnđ. Do đ&oacute; Qu&yacute; kh&aacute;ch thanh to&aacute;n bằng VNĐ th&igrave; tỷ gi&aacute; sẽ t&iacute;nh theo tỷ gi&aacute; ni&ecirc;m yết của ng&acirc;n h&agrave;ng Việt Nam v&agrave;o thời điểm qu&yacute; kh&aacute;ch đăng k&yacute; tour</li>\r\n	<li style="text-align: justify;">\r\n		Hộ chiếu (Passport) của qu&yacute; kh&aacute;ch phải c&ograve;n thời hạn tr&ecirc;n 6 th&aacute;ng kể từ ng&agrave;y về.</li>\r\n	<li style="text-align: justify;">\r\n		Qu&yacute; kh&aacute;ch từ 70 tuổi trở l&ecirc;n vui l&ograve;ng đ&oacute;ng th&ecirc;m ph&iacute; bảo hiểm cao cấp (ph&iacute; thay đổi t&ugrave;y theo tour)</li>\r\n	<li style="text-align: justify;">\r\n		Qu&yacute; kh&aacute;ch từ 75 tuổi trở l&ecirc;n y&ecirc;u cầu phải c&oacute; giấy x&aacute;c nhận đầy đủ sức khỏe để đi du lịch nước ngo&agrave;i của B&aacute;c sĩ v&agrave; phải c&oacute; người th&acirc;n dưới 60 tuổi (đầy đủ sức khỏe) đi theo.</li>\r\n	<li style="text-align: justify;">\r\n		Sau khi đ&oacute;ng tiền, nếu qu&yacute; kh&aacute;ch muốn hủy tour, vui l&ograve;ng đem CMND v&agrave; h&oacute;a đơn đ&atilde; đ&oacute;ng tiền đến văn ph&ograve;ng của&nbsp;Mix Tourist&nbsp;để hủy tour. Cty kh&ocirc;ng nhận kh&aacute;ch b&aacute;o hủy qua điện thoại hoặc c&aacute;c li&ecirc;n lạc kh&aacute;c.</li>\r\n	<li style="text-align: justify;">\r\n		Du lịch Mix Tourist&nbsp; kh&ocirc;ng nhận kh&aacute;ch c&oacute; thai từ 6 th&aacute;ng trở l&ecirc;n tham gia c&aacute;c tour du lịch nước ngo&agrave;i.</li>\r\n	<li style="text-align: justify;">\r\n		Trong trường hợp bất khả kh&aacute;ng do thời tiết, thi&ecirc;n tai, đ&igrave;nh c&ocirc;ng, bạo động, ph&aacute; hoại, chiến tranh, dịch bệnh, chuyến bay bị tr&igrave; ho&atilde;n hay bị hủy do thời tiết hoặc do kỹ thuật&hellip;..dẫn đến tour kh&ocirc;ng thể thực hiện tiếp tục được, C&ocirc;ng ty sẽ ho&agrave;n trả lại tiền tour cho qu&yacute; kh&aacute;ch sau khi đ&atilde; trừ lại c&aacute;c chi ph&iacute; dịch vụ đ&atilde; thực hiện v&agrave; kh&ocirc;ng chịu tr&aacute;ch nhiệm bồi thường th&ecirc;m bất kỳ chi ph&iacute; n&agrave;o kh&aacute;c.</li>\r\n	<li style="text-align: justify;">\r\n		Chương tr&igrave;nh v&agrave; gi&aacute; c&oacute; thể thay đổi tuỳ t&igrave;nh h&igrave;nh chuyến bay, kh&aacute;ch sạn tại Myanmar v&agrave; được x&aacute;c nhận ch&iacute;nh thức v&agrave;o ng&agrave;y họp đo&agrave;n, trước ng&agrave;y đi 2 - 3 ng&agrave;y. C&aacute;c điểm tham quan c&oacute; thể kh&ocirc;ng theo thứ tự trong chương tr&igrave;nh nhưng vẩn đảm bảo đầy đủ nội dung theo trong chương tr&igrave;nh.</li>\r\n</ul>\r\n', '', '', 'Tour du lịch Myanmar du xuân đầu năm tết 2017', 'du lịch Myanmar, du lịch Myanmar tết, du lịch tết Myanmar', 'Cùng MixTourist tham gia lễ hội đầu xuân 2017, hành hương về với xứ sở chùa vàng chắc chăn bạn sẽ có những trải nghiệm vô cùng tuyệt vời', '<ul>\r\n	<li>\r\n		V&eacute; m&aacute;y bay khứ hồi Việt nam - Myanmar, thuế s&acirc;n bay 2 đầu, ph&iacute; an ninh s&acirc;n bay, Phụ thu xăng dầu h&agrave;ng kh&ocirc;ng.</li>\r\n	<li>\r\n		Ăn theo chương tr&igrave;nh &amp; 03 đ&ecirc;m kh&aacute;ch sạn 3 sao (ti&ecirc;u chuẩn 02 kh&aacute;ch/ph&ograve;ng 02 giường đơn, trường hợp lẽ nam lẽ nữ ngủ ph&ograve;ng 3).</li>\r\n	<li>\r\n		Kh&aacute;ch sạn ti&ecirc;u chuẩn 3 sao ( 2 người lớn/ph&ograve;ng, chia sẻ</li>\r\n	<li>\r\n		V&eacute; v&agrave;o cửa tham quan theo chương tr&igrave;nh - Xe đ&oacute;n tiễn s&acirc;n bay v&agrave; tham quan.</li>\r\n	<li>\r\n		Hướng dẫn Tiếng Việt suốt tuyến</li>\r\n	<li>\r\n		Qu&agrave; tặng của Cty du lịch.</li>\r\n</ul>\r\n<br />\r\n', '<ul>\r\n	<li>\r\n		Lệ ph&iacute; l&agrave;m visa nhập lại Việt Nam cho kh&aacute;ch mang quốc tịch nước ngo&agrave;i.</li>\r\n	<li>\r\n		Nước uống, điện thoại, giặt ủi, h&agrave;nh l&yacute; qu&aacute; cước theo quy định của h&agrave;ng kh&ocirc;ng.</li>\r\n	<li>\r\n		Tiền tip cho HDV v&agrave; t&agrave;i xế ( 3 USD/ Kh&aacute;ch/ ng&agrave;y )</li>\r\n	<li>\r\n		Thuốc men, bệnh viện&hellip;v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c của kh&aacute;ch ngo&agrave;i chương tr&igrave;nh.</li>\r\n</ul>\r\n'),
(12, 1, 8, 46, '', 1, 1, 'Tour du lịch SYDNEY – MELBOURNE', 'tour-du-lich-sydney-melbourne', '', '', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/uc/du-lich-sydney-mixtourist.jpg', '53900000', '52500000', '', '', '', '', '', '7 Ngày 6 đêm', '2', 'Sydney, melbourne', '10/10, 28/10, 15/11, 28/11, 16/12', 'Máy bay, Ô tô', 3, '<p style="text-align: justify;">\r\n	Nằm ở ph&iacute;a Đ&ocirc;ng Nam nước &Uacute;c, <strong>Sydney </strong>từ nhiều năm nay được xem l&agrave; một trong những th&agrave;nh phố đ&aacute;ng sống nhất thế giới. Được th&agrave;nh lập v&agrave;o năm 1788, hiện nay <strong>Sydney </strong>nhộn nhịp c&oacute; đến 4.5 triệu d&acirc;n cư sinh sống, đồng thời cũng được xem l&agrave; trung t&acirc;m kinh tế văn h&oacute;a của&nbsp;&Uacute;c.</p>\r\n<p style="text-align: justify;">\r\n	C&ugrave;ng <strong>Mixtourist </strong>kh&aacute;m ph&aacute; th&agrave;nh phố Sydney xinh đẹp nh&eacute;!</p>\r\n', '', '<h2 style="text-align: justify;">\r\n	<span style="font-size: 16px; color: rgb(255, 0, 0);">NG&Agrave;Y 01: H&Agrave;NỘI/ HỒ CH&Iacute; MINH &ndash; SYDNEY &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(Ăn &nbsp;tối tr&ecirc;n m&aacute;y bay)</span></h2>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch tập trung tại điểm hẹn trong th&agrave;nh phố Hồ Ch&iacute; Minh, xe v&agrave; hướng dẫn vi&ecirc;n đưa đo&agrave;n ra s&acirc;n bay quốc tế T&acirc;n Sơn Nhất, l&agrave;m thủ tục đ&aacute;p chuyến bay mang số hiệu VN773 SGNSYD 2040-0915,&nbsp;khởi h&agrave;nh l&uacute;c 20h40 đi th&agrave;nh phố Sydney - Australia. Qu&yacute; kh&aacute;ch d&ugrave;ng bữa ăn nhẹ v&agrave; nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<p style="text-align: justify;">\r\n	Lưu &yacute;: Qu&yacute; kh&aacute;ch khởi h&agrave;nh từ H&agrave; Nội hoặc Đ&agrave; Nẵng tự t&uacute;c phương tiện di chuyển đến s&acirc;n bay T&acirc;n Sơn Nhất chậm nhất 3 tiếng trước giờ khởi h&agrave;nh của m&aacute;y bay để đảm bảo đ&uacute;ng h&agrave;nh tr&igrave;nh cho đo&agrave;n.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 02: SYDNEY &ndash; CITY TOUR &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(Ăn s&aacute;ng tr&ecirc;n m&aacute;y bay, trưa, tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong> đo&agrave;n đến s&acirc;n bay Sydney, xe v&agrave; HDV đ&oacute;n đo&agrave;n. Qu&yacute; kh&aacute;ch bắt đầu một h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; th&agrave;nh phố Sydney với những cảnh đẹp như:</p>\r\n<p style="text-align: justify;">\r\n	Nh&agrave; h&aacute;t Con S&ograve;&nbsp;(Sydney Opera House)&nbsp;c&oacute; kiến tr&uacute;c cực kỳ ấn tượng v&agrave; nổi tiếng của Sydney với ba mặt hướng ra biển. Darling Habour: một cảng biển nhộn nhịp nơi c&oacute; nhiều qu&aacute;n c&agrave; ph&ecirc; l&atilde;ng mạn ngo&agrave;i trời nổi tiếng ở Sydney.</p>\r\n<p style="text-align: justify;">\r\n	Vườn B&aacute;ch Thảo (Botanic Garden): vườn thực vật được mở cửa từ năm 1816 với diện t&iacute;ch 30 hectares. Mrs. Macquaries Chair để ghi những bức h&igrave;nh đẹp nhất của Cầu cảng Sydney (Sydney Bridge &amp; Habour).</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong> Đo&agrave;n thăm khu phố cổ The Rocks - thường được v&iacute; như &ldquo;bảo t&agrave;ng Sydney ngo&agrave;i trời&rdquo; bởi nơi n&agrave;y hội tụ cả qu&aacute; khứ v&agrave; hiện tại của th&agrave;nh phố Sydney. Khu The Rocks nằm kề cầu cảng Sydney l&agrave; nơi những người da trắng đầu ti&ecirc;n đến định cư ở &Uacute;c, bởi thế n&oacute; được xem l&agrave; cội nguồn h&igrave;nh th&agrave;nh n&ecirc;n th&agrave;nh phố Sydney v&agrave; đất nước &Uacute;c.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng, nhận ph&ograve;ng v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn ở Sydney.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 03: FEATHERDALE PARK &nbsp;- MADAME TUSSAUD &nbsp;MUSEUM&nbsp; &nbsp; &nbsp;(Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong> Qu&yacute; kh&aacute;ch ăn s&aacute;ng tại kh&aacute;ch sạn. Sau đ&oacute;, xe đưa đo&agrave;n tham quan C&ocirc;ng vi&ecirc;n&nbsp;Featherdale Park&nbsp;- tại đ&acirc;y Qu&yacute; kh&aacute;ch c&oacute; dịp ngắm nh&igrave;n v&ocirc; số động vật y&ecirc;u qu&yacute; nhất của &Uacute;c: gấu koalas, emus, chuột t&uacute;i, chim c&ocirc;ng, chim c&aacute;nh cụt&hellip; chụp h&igrave;nh lưu niệm.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong> Xe đưa Qu&yacute; kh&aacute;ch quay trở lại th&agrave;nh phố&nbsp;Sydney&nbsp;để tham quan&nbsp;Bảo t&agrave;ng&nbsp;Madame Tussauds. Tại bảo t&agrave;ng c&oacute; trưng b&agrave;y c&aacute;c bức tượng s&aacute;p của một số nh&acirc;n vật nổi tiếng của xứ sở chuột t&uacute;i như: Nữ diễn vi&ecirc;n Cate Blanchett v&agrave; Nicole Kidman b&ecirc;n cạn tổng thống Mỹ Barack Obama v&agrave; thần tượng pop Lady Gaga...Qu&yacute; kh&aacute;ch c&oacute; thể chi&ecirc;m ngưỡng nền nghệ thuật t&aacute;i tạo lịch sử v&agrave; c&aacute;c di sản v&agrave; chụp ảnh tại nơi đ&acirc;y.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn tối v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 04: SYDNEY &ndash; MELBOURNE &nbsp;&nbsp;&nbsp; - CITY TOUR &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Sau bữa điểm t&acirc;m, Xe &nbsp;đưa đo&agrave;n &nbsp;ra s&acirc;n bay l&agrave;m thủ tục l&ecirc;n m&aacute;y bay, bay sang Melbourne, th&agrave;nh phố lớn thứ hai tại &Uacute;c. Xuống đến s&acirc;n bay, xe đ&oacute;n đo&agrave;n đi tham quan v&agrave; kh&aacute;m ph&aacute; c&aacute;c thắng cảnh đẹp tại Melbourne như:&nbsp;Phố Swanston; Nh&agrave; thờ lớn tr&ecirc;n đường Patrick; Treasure House, Fitzroy Garden&hellip;</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng, sau đ&oacute; tiếp tục thăm quan&nbsp;Trung t&acirc;m t&agrave;i ch&iacute;nh tr&ecirc;n đường Collins Street, Parliament House, Trung t&acirc;m mỹ thuật Arts Precincts, Quảng trường li&ecirc;n bang Federation Square, Đ&agrave;i tưởng niệm chiến tranh.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn tối v&agrave; nghỉ đ&ecirc;m ở kh&aacute;ch sạn tại Melbourne</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối:</strong> Qu&yacute; kh&aacute;ch&nbsp;tự do kh&aacute;m ph&aacute; v&agrave; thăm quan&nbsp;Southbank &ndash; đ&ocirc; thị mới của Melbourne.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;">NG&Agrave;Y 05: MELBOURNE &ndash;TRANG TRẠI RAU QUẢ -&nbsp; BALLARAT (Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Sau bữa s&aacute;ng, xe đưa đo&agrave;n&nbsp; khởi h&agrave;nh&nbsp;đi Ballarat, tr&ecirc;n đường đi, đo&agrave;n gh&eacute; thăm v&agrave; h&aacute;i tr&aacute;i c&acirc;y&nbsp;tại trang trại T&aacute;o; D&acirc;u t&acirc;y; Đ&agrave;o; Cherry...&nbsp;(T&ugrave;y theo từng m&ugrave;a h&aacute;i tr&aacute;i g&igrave;). Qu&yacute; kh&aacute;ch tận tay h&aacute;i v&agrave; ăn thỏa th&iacute;ch tại vườn tr&aacute;i v&agrave; mua những tr&aacute;i ngon do ch&iacute;nh m&igrave;nh h&aacute;i mang về l&agrave;m qu&agrave;, đ&acirc;y l&agrave; một trong những chương tr&igrave;nh hấp dẫn v&agrave; mang lại cho Qu&yacute; kh&aacute;ch những trải nghiệm th&uacute; vị.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	Khởi h&agrave;nh đi&nbsp;Thị trấn Ballarat&nbsp;t&igrave;m hiểu về nơi đ&atilde; kh&aacute;m ph&aacute; ra mỏ v&agrave;ng năm 1851 qua sự t&aacute;i hiện cảnh đ&agrave;o v&agrave;ng trong thế kỷ 19. Qu&yacute; kh&aacute;ch thử vận may, đ&atilde;i v&agrave;ng tại d&ograve;ng suối, nếu may mắn c&oacute; thể t&igrave;m được v&agrave;ng. Sau đ&oacute; Qu&yacute; kh&aacute;ch được hướng dẫn đưa v&agrave;o thăm quan mỏ v&agrave;ng v&agrave; được nghe giới thiệu c&aacute;ch thức khai th&aacute;c v&agrave;ng từ thế kỷ 19.&nbsp;Tiếp tục thăm quan đồi v&agrave;ng v&agrave; bảo t&agrave;ng v&agrave;ng &ndash; nơi trưng b&agrave;y những khối v&agrave;ng được t&igrave;m thấy tại Ballarat</p>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n ăn tối tại nh&agrave; h&agrave;ng, nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 06: GRAND PARK - DANDENONG &ndash; SHOPPING &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Sau bữa s&aacute;ng, xe đưa Qu&yacute; kh&aacute;ch đi ra v&ugrave;ng ngoại &ocirc; Belgrave, thăm quan&nbsp;C&ocirc;ng vi&ecirc;n Quốc Gia Dandenong. Qu&yacute; kh&aacute;ch dừng ch&acirc;n ngắm cảnh rừng nguy&ecirc;n sinh với c&aacute;c c&acirc;y gỗ lớn, ngắm những ch&uacute; chim với bộ l&ocirc;ng đỏ ch&oacute;t hay nhưng ch&uacute; vẹt trắng từ thi&ecirc;n nhi&ecirc;n hội tụ về, Qu&yacute; kh&aacute;ch cho chim v&agrave; vẹt ăn, chụp h&igrave;nh lưu niệm.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch tiếp tục trải nghiệm tr&ecirc;n&nbsp;chuyến t&agrave;u&nbsp; lửa hơi nước&nbsp;Puffing Billy&nbsp;cổ được ra đời từ năm 1900. T&agrave;u đưa Qu&yacute; kh&aacute;ch xuy&ecirc;n qua c&aacute;ch rừng nguy&ecirc;n sinh với những cảnh đẹp thi&ecirc;n nhi&ecirc;n h&ugrave;ng vỹ, mang lại sự trải nghiệm th&uacute; vị.</p>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong> Đo&agrave;n thỏa sức mua sắm tại c&aacute;c trung t&acirc;m shopping lớn của Melbourne như:&nbsp;Melbourne Central; DFO...&nbsp;t&ugrave;y v&agrave;o thời gian v&agrave; nhu cầu mua sắm của đo&agrave;n.</p>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n ăn tối tại nh&agrave; h&agrave;ng v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn ở Melbourne.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 07: MELBOURNE - H&Agrave; NỘI/ HỒ CH&Iacute; MINH &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (Ăn s&aacute;ng)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n ăn s&aacute;ng tại kh&aacute;ch sạn v&agrave; l&agrave;m thủ tục trả ph&ograve;ng. Xe đưa đo&agrave;n ra s&acirc;n bay Melbourne l&agrave;m thủ tục đ&aacute;p chuyến bay về Việt Nam, tr&ecirc;n chuyến bay&nbsp;VN 780T MELSGN 1125-1545. Ăn trưa tr&ecirc;n m&aacute;y bay. Về đến điểm đ&oacute;n ban đầu. Kết th&uacute;c chương tr&igrave;nh tour. Xin ch&agrave;o tạm biệt v&agrave; hẹn gặp lại Qu&yacute; kh&aacute;ch.</p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>GI&Aacute; TOUR TRỌN G&Oacute;I</strong></span></span></h2>\r\n<table border="1" cellpadding="0" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>B&aacute;o gi&aacute; trọn g&oacute;i</strong></p>\r\n				<p style="text-align: center;">\r\n					<strong>Dịch vụ ti&ecirc;u chuẩn 3 sao</strong></p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>Gi&aacute; người lớn</strong></p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>Gi&aacute; cho trẻ em &nbsp;từ 2 đến 11 tuổi</strong></p>\r\n				<p style="text-align: center;">\r\n					<strong>(C&oacute; v&agrave; kh&ocirc;ng giường ri&ecirc;ng )</strong></p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>Phụ thu ph&ograve;ng đơn</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					Đo&agrave;n từ 15 kh&aacute;ch người lớn. (C&oacute; HDV từ VN)</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					52.500.000</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					85% - 90%</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					8.500.000</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">Lưu &yacute;:</span></span></h3>\r\n<p style="text-align: justify;">\r\n	-&nbsp;Gi&aacute; tour trong chương tr&igrave;nh &aacute;p dụng cho kh&aacute;ch khởi h&agrave;nh từ TP.HCM; kh&aacute;ch khởi h&agrave;nh từ Đ&agrave; Nẵng v&agrave; c&aacute;c tỉnh th&agrave;nh kh&aacute;c sẽ tự t&uacute;c v&eacute; m&aacute;y bay khứ hồi nội địa v&agrave;o TP. HCM để gh&eacute;p c&ugrave;ng đo&agrave;n hoặc c&oacute; phụ thu.</p>\r\n<p style="text-align: justify;">\r\n	-&nbsp;Em b&eacute; dưới 2 tuổi gi&aacute; bao gồm: Visa, bảo hiểm, V&eacute; m&aacute;y bay, ngủ chung giường với cha mẹ. V&igrave; vậy cha mẹ tự lo c&aacute;c chi ph&iacute; ph&aacute;t sinh v&agrave; ăn uống theo c&aacute;ch ri&ecirc;ng của b&eacute;.</p>\r\n<p style="text-align: justify;">\r\n	-&nbsp;Điều kiện tham gia chương tr&igrave;nh: Qu&yacute; kh&aacute;ch vui l&ograve;ng xem kỹ hợp đồng hoặc phiếu đăng k&yacute; tham gia chương tr&igrave;nh.</p>\r\n', '', '', 'Tour du lịch SYDNEY – MELBOURNE', 'du lịch sydney, du lịch melbourne, du lịch úc, du lịch australia', 'Nằm ở phía Đông Nam nước Úc, Sydney từ nhiều năm nay được xem là một trong những thành phố đáng sống nhất thế giới. Cùng Mixtourist khám phá thành phố Sydney xinh đẹp nhé', '', ''),
(13, 1, 8, 46, '', 1, 1, 'Tour du lịch MELBOURNE - SYDNEY', 'tour-du-lich-melbourne-sydney', '', '', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/uc/du-lich-melbourne-mixtourist.jpg', '', '53990000', '', '', '', '', '', '6 Ngày 5 Đêm', '1', 'Sydney, Melbourne', '24/9, 8/10, 20/10, 25/11, 27/12/', 'Máy bay, Ô tô', 3, '', '', '<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 01 : HANOI -&nbsp;BANGKOK</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>18h00:</strong> Xe v&agrave; hướng dẫn vi&ecirc;n (HDV) đ&oacute;n qu&yacute; kh&aacute;ch tại điểm hẹn trong trung t&acirc;m th&agrave;nh phố đưa ra&nbsp;s&acirc;n bay Nội B&agrave;i&nbsp;đ&aacute;p chuyến bay đi qua&nbsp;Bangkok &ndash; Th&aacute;i Lan, HANBKK&nbsp;// TG 565 // 20.45 &ndash; 22.35&nbsp;của h&atilde;ng h&agrave;ng kh&ocirc;ng&nbsp;Th&aacute;i Lan.</p>\r\n<p style="text-align: justify;">\r\n	<strong>23h35:</strong>&nbsp;giờ địa phương: m&aacute;y bay hạ c&aacute;nh xuống s&acirc;n bay&nbsp;Suvanabumi&nbsp;&ndash; Bangkok,&nbsp;HDV hướng dẫn Qu&yacute; kh&aacute;ch l&agrave;m thủ tục nối chuy&ecirc;n bay đi&nbsp;<a>Melbourne</a>&nbsp;&ndash; &Uacute;c, BKKMEL // TG465 // 00.15 &ndash; 12.05.Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;">NG&Agrave;Y 02 : MELBOURNE&nbsp;(Ăn trưa, tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>12h05:</strong> M&aacute;y bay hạ c&aacute;nh xuống&nbsp;&nbsp;s&acirc;n bay quốc tế Melbourne. Sau khi l&agrave;m thủ tục nhập cảnh, xe v&agrave; HDV đưa Qu&yacute; kh&aacute;ch v&agrave;o trung t&acirc;m th&agrave;nh phố ăn trưa tại nh&agrave; h&agrave;ng. Sau đ&oacute;, Qu&yacute; kh&aacute;ch tham quan:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Nh&agrave; Quốc hội cũ&nbsp;&ndash;&nbsp;t&ograve;a nh&agrave; n&agrave;y được x&acirc;y dựng từ năm 1856, b&ecirc;n ngo&agrave;i t&ograve;a nh&agrave; được thiết kế theo lối kiến tr&uacute;c thời Victoria v&agrave; ph&iacute;a tr&ecirc;n những h&agrave;ng cột b&ecirc;n ngo&agrave;i được mạ v&agrave;ng nguy&ecirc;n chất.</li>\r\n	<li style="text-align: justify;">\r\n		Quảng trường Li&ecirc;n bang &ndash;&nbsp;nơi diễn ra c&aacute;c hoạt động văn h&oacute;a, kinh tế, du lịch....c&ugrave;ng những nh&agrave; h&agrave;ng, qu&aacute;n bar độc đ&aacute;o đ&atilde; khiến cho quảng trường n&agrave;y trở th&agrave;nh tr&aacute;i tim v&agrave; linh hồn của Melbourne;</li>\r\n	<li style="text-align: justify;">\r\n		T&ograve;a thị ch&iacute;nh Th&agrave;nh phố&nbsp;cũng l&agrave; một điểm đến kh&ocirc;ng thể bỏ qua khi du kh&aacute;ch đến Melbourne để t&igrave;m hiểu th&ecirc;m về bộ m&aacute;y của ch&iacute;nh phủ địa phương;</li>\r\n	<li style="text-align: justify;">\r\n		Đ&agrave;i tưởng niệm chiến tranh thế giới thứ nhất &ndash;&nbsp;c&ocirc;ng tr&igrave;nh mang kiến tr&uacute;c với những bậc thang cao, những cột trụ v&agrave; m&aacute;i v&ograve;m m&ocirc; ph&ograve;ng một ng&ocirc;i đền uy nghi mang kh&ocirc;ng kh&iacute; thanh tịnh trầm mặc&nbsp;để tưởng niệm những người con &Uacute;c đ&atilde; phục vụ cho Tổ Quốc;</li>\r\n	<li style="text-align: justify;">\r\n		Trạm xe lửa trung t&acirc;m Finders Station&nbsp;được ho&agrave;n th&agrave;nh v&agrave;o năm 1909 v&agrave; l&agrave; biểu tượng văn h&oacute;a của Melbourne với h&igrave;nh ảnh m&aacute;i v&ograve;m, th&aacute;p đồng hồ c&ugrave;ng cửa cong nổi bật. C&oacute; thể n&oacute;i đ&acirc;y l&agrave; ga đường sắt đầu tiền của &Uacute;c v&agrave; phục vụ cho to&agrave;n bộ mạng lưới đường sắt đ&ocirc; thị.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	<strong>18h00:</strong>&nbsp;Ăn tối tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại kh&aacute;ch sạn 3* ở <strong>Melbourne</strong>.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 03 : MELBOURNE -&nbsp;VƯỜN TH&Uacute; HEALSVILE &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>07h00:</strong> Ăn s&aacute;ng tại kh&aacute;ch sạn</p>\r\n<p style="text-align: justify;">\r\n	<strong>08h00:</strong> Xe đưa Qu&yacute; kh&aacute;ch đi tham quan&nbsp;thung lũng Yarra (c&aacute;ch Melbourne 51 km ~ 1h chạy xe) &ndash;&nbsp;Nằm ở hướng Đ&ocirc;ng Bắc của th&agrave;nh phố Melbourne, thung lũng Yarra nổi tiếng c&oacute; rượu vang nổ v&agrave; vang đen hảo hạng.Tới đ&acirc;y Qu&yacute; kh&aacute;ch được nếm thử rượu vang của &Uacute;c trong c&aacute;c n&ocirc;ng trại nho v&agrave; xưởng rượu gia đ&igrave;nh.</p>\r\n<p style="text-align: justify;">\r\n	Sau đ&oacute;, Qu&yacute; kh&aacute;ch l&ecirc;n xe tới Healsvile tham quan&nbsp;Vườn th&uacute; Healsvile -&nbsp;Nơi đ&acirc;y Qu&yacute; kh&aacute;chn sẽ c&oacute; cơ hội được ngắm nh&igrave;n v&agrave; chụp h&igrave;nh lưu niệm với c&aacute;c con th&uacute; v&ocirc; c&ugrave;ng đ&aacute;ng y&ecirc;u v&agrave; l&agrave; c&aacute;c lo&agrave;i th&uacute; đặc trưng của nước &Uacute;c như: những ch&uacute; chuột t&uacute;i Kangaroo - biểu tượng của nước &Uacute;c, ch&uacute; gấu Koala dễ thương v&agrave; tham ngủ, th&uacute; mỏ vịt - Platypus...</p>\r\n<p style="text-align: justify;">\r\n	<strong>12h30:&nbsp;</strong>Ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>13h30: </strong>Qu&yacute; kh&aacute;ch quay trở về Melbourne, tr&ecirc;n đường Qu&yacute; kh&aacute;ch gh&eacute; tham v&agrave; v&agrave;o vườn h&aacute;i tr&aacute;i c&acirc;y theo m&ugrave;a như cherry, d&acirc;u t&acirc;y, ....</p>\r\n<p style="text-align: justify;">\r\n	Về th&agrave;nh phố Melbourne, Qu&yacute; kh&aacute;ch tự do mua sắm tại c&aacute;c trung t&acirc;m thương mại trong th&agrave;nh phố.</p>\r\n<p style="text-align: justify;">\r\n	<strong>18h30:&nbsp;</strong>Ăn tối tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại kh&aacute;ch sạn 3* ở <strong>Melbourne</strong>.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 04 : &nbsp;MELBOURNE -&nbsp;SYDNEY &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ( Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>06h00:</strong> Xe v&agrave; HDV đưa Qu&yacute; kh&aacute;ch ra s&acirc;n bay l&agrave;m thủ tục đ&aacute;p chuyến bay đi Sydney</p>\r\n<p style="text-align: justify;">\r\n	<strong>09h14: </strong>Qu&yacute; kh&aacute;ch tới s&acirc;n bay Sydney, xe v&agrave; HDV đ&oacute;n v&agrave; dẫn Qu&yacute; kh&aacute;ch đi &nbsp;tham quan:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Chụp ảnh với&nbsp;Nh&agrave; h&aacute;t Opera Sydney&nbsp;&ndash; Nh&agrave; h&aacute;t thiết kế năm 1955 v&agrave; ho&agrave;n th&agrave;nh năm 1973 mang kiến tr&uacute;c độc đ&aacute;o h&igrave;nh con thuyền với những c&aacute;nh buồm no gi&oacute; đang vươn m&igrave;nh ra biển khơi. Nh&agrave; h&aacute;t tọa lạc tr&ecirc;n diện t&iacute;ch 1,8 ha, d&agrave;i 183 m v&agrave; rộng 120 m với 588 cột trụ b&ecirc; t&ocirc;ng đ&uacute;c sẵn v&agrave; ch&ocirc;n ở độ s&acirc;u 25 m dưới mặt nước biển. Nh&agrave; h&aacute;t kh&ocirc;ng chỉ l&agrave; một trong những h&igrave;nh ảnh mang t&iacute;nh biểu tượng đầy mạnh mẽ của thế giới hiện đại, m&agrave; c&ograve;n l&agrave; niềm tự h&agrave;o của người d&acirc;n Sydney n&oacute;i ri&ecirc;ng v&agrave; đất nước Australia n&oacute;i chung.</li>\r\n	<li style="text-align: justify;">\r\n		Vườn Royal Botanic Garden&nbsp;&ndash;&nbsp;nơi c&oacute; h&agrave;ng ng&agrave;n loại c&acirc;y cỏ kh&aacute;c nhau được chăm s&oacute;c hết sức c&ocirc;ng phu. Đ&acirc;y l&agrave; điểm l&yacute; tưởng để Qu&yacute; kh&aacute;ch chụp to&agrave;n cảnh Nh&agrave; h&aacute;t con s&ograve; v&agrave; cầu cảng Sydney.</li>\r\n	<li style="text-align: justify;">\r\n		Nh&agrave; thờ Saint Merry:&nbsp;Đ&acirc;y l&agrave; nơi ở của Đức Tổng Gi&aacute;m Mục Sydney, v&agrave; l&agrave; nơi đứng đầu trong danh s&aacute;ch c&aacute;c nh&agrave; nguyện c&ocirc;ng gi&aacute;o đầu ti&ecirc;n tại &Uacute;c. Nh&agrave; thờ được x&acirc;y dựng bằng sa thạch địa phương v&agrave; mang cấu tr&uacute;c gợi nhớ thời ch&acirc;u &Acirc;u trung cổ.</li>\r\n	<li style="text-align: justify;">\r\n		Darling Harbour &ndash; Cảng t&igrave;nh y&ecirc;u,&nbsp;xưa kia nổi tiếng l&agrave;&nbsp;&ldquo; The Hungry Mile&rdquo;(Con đường đ&oacute;i kh&aacute;t) nơi tập trung nhiều người thất nghiệp, v&ocirc; gia cư đến để t&igrave;m kiếm cơ hội việc l&agrave;m, nhưng sau cuộc t&aacute;i thiết vĩ đại của ch&iacute;nh quyền th&agrave;nh phố th&igrave; cảng Darling nhanh ch&oacute;ng trở th&agrave;nh cảng biển sầm uất kết hợp với khu thương mại giải tr&iacute; v&agrave; ẩm thực nổi tiếng.</li>\r\n	<li style="text-align: justify;">\r\n		Ngắm cầu cảng Sydney.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	<strong>12h30:</strong> Ăn trưa tại nh&agrave; h&agrave;ng</p>\r\n<p style="text-align: justify;">\r\n	<strong>14h00: </strong>Qu&yacute; kh&aacute;ch tiếp tục tham quan:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		B&atilde;i biển Bondi&nbsp;&ndash; một trong nhữn b&atilde;i biển c&aacute;t trắng v&agrave; mịn nhất xứ sở chuột t&uacute;i</li>\r\n	<li style="text-align: justify;">\r\n		Dạo chơi, đi bộ&nbsp;phố Pitt street&nbsp;v&agrave; trung t&acirc;m mua sắm sầm uất nhất Sydney Westfield</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	<strong>18h30:&nbsp;</strong>Ăn tối tại nh&agrave; h&agrave;ng</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại kh&aacute;ch sạn 3* ở Sydney.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 05 :&nbsp;SYDNEY&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;( Ăn s&aacute;ng, trưa)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>07h00:</strong> Ăn s&aacute;ng&nbsp; tại kh&aacute;ch sạn.</p>\r\n<p style="text-align: justify;">\r\n	<strong>08h00:</strong> Qu&yacute; kh&aacute;c khởi h&agrave;nh đi về hướng T&acirc;y Bắc, đến Echo Point, từ đ&oacute; Qu&yacute; kh&aacute;ch sẽ được&nbsp; ngắm&nbsp;N&uacute;i 3 chị em (Three Sisters).</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Điểm Vọng (Echo Point)&nbsp;&ndash; tại nơi đ&acirc;y du kh&aacute;ch c&oacute; dịp lắng nghe những truyền thuyết huyền b&iacute; của những người thổ d&acirc;n xa xưa về nguồn gốc của&nbsp;N&uacute;i&nbsp;Ba Chị Em&nbsp;&ndash;&nbsp;l&uacute;c ẩn m&igrave;nh trong sương, l&uacute;c li&ecirc;n tục thay đổi m&agrave;u sắc theo &aacute;nh nắng mặt trời.</li>\r\n	<li style="text-align: justify;">\r\n		Qu&yacute; kh&aacute;ch sẽ được thử cảm gi&aacute;c mạnh bằng việc&nbsp;đi t&agrave;u trượt tr&ecirc;n v&aacute;ch n&uacute;i dựng đứng xuống thung lũng từ độ cao 1000m,&nbsp;đi bộ kh&aacute;m ph&aacute; khung cảnh tĩnh mịch của n&uacute;i rừng v&agrave; thăm 1 số mỏ than đ&atilde; được khai th&aacute;c từ đầu thế kỷ 19.</li>\r\n	<li style="text-align: justify;">\r\n		Đi c&aacute;p treo trở về Scenic World.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	<strong>12h00:</strong> Ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch quay trở về Sydney mua sắm tại DFO Sydney Olympic..</p>\r\n<p style="text-align: justify;">\r\n	<strong>18h30:</strong> Ăn tối tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại kh&aacute;ch sạn 3* ở Sydney.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 06 : SYDNEY&nbsp;H&Agrave; NỘI&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>06h30:</strong> Ăn s&aacute;ng tại kh&aacute;ch sạn. Sau đ&oacute;, xe đưa Qu&yacute; kahcsh ra s&acirc;n bay đ&aacute;p chuyến bay về Việt Nam, nối chuyến qua&nbsp;Bangkok &ndash; Th&aacute;i Lan, SYDBKK // TG476 // 10.00 &ndash; 16.20</p>\r\n<p style="text-align: justify;">\r\n	<strong>16h20: </strong>giờ địa phương: M&aacute;y bay hạ c&aacute;nh xuống s&acirc;n bay&nbsp;Suvanabumi&nbsp;&ndash; Bangkok, đo&agrave;n l&agrave;m thủ tục nối chuyến bay về&nbsp;H&agrave; Nội, BKKHAN // TG564 // 17.50 &ndash; 19.40</p>\r\n<p style="text-align: justify;">\r\n	<strong>12h10:</strong> giờ địa phương:&nbsp;M&aacute;y bay hạ c&aacute;nh xuống s&acirc;n bay Nội B&agrave;i, xe đ&oacute;n đo&agrave;n v&agrave; đưa qu&yacute; kh&aacute;ch về điểm hẹn ban đầu.</p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;"><strong>GI&Aacute; TRỌN G&Oacute;I</strong></span></span></h2>\r\n<p style="text-align: center;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;"><strong>(&Aacute;p dụng cho 01 kh&aacute;ch gh&eacute;p đo&agrave;n từ 20 người lớn trở l&ecirc;n)</strong></span></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n<table border="1" cellpadding="0" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>NGƯỜI LỚN</strong></p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>TRẺ EM</strong></p>\r\n				<p style="text-align: center;">\r\n					<strong>(Từ 2 đến 12 tuổi)</strong></p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>PHỤ PH&Iacute; PH&Ograve;NG ĐƠN</strong></p>\r\n				<p style="text-align: center;">\r\n					<strong>(nếu c&oacute;)</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					53,990,000 đ</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					45,500,000 đ</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					6.900.000 đ</p>\r\n				<div>\r\n					&nbsp;</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">LƯU &Yacute;</span></span></h3>\r\n<p style="text-align: justify;">\r\n	- &nbsp;Hộ chiếu của qu&yacute; kh&aacute;ch phải c&ograve;n gi&aacute; trị từ 06 th&aacute;ng trở l&ecirc;n t&iacute;nh từ ng&agrave;y khởi h&agrave;nh.</p>\r\n<p style="text-align: justify;">\r\n	- &nbsp;Trẻ em 02 &ndash; 12 tuổi ngủ chung giường với bố mẹ.</p>\r\n<p style="text-align: justify;">\r\n	- &nbsp;Chương tr&igrave;nh ch&iacute;nh thức được x&aacute;c nhận trước ng&agrave;y khởi h&agrave;nh 1-2 ng&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	- &nbsp;Lịch tr&igrave;nh c&oacute; thể thay đổi tuỳ thuộc v&agrave;o t&igrave;nh h&igrave;nh thời tiết, giao th&ocirc;ng nhưng vẫn đảm bảo đầy đủ c&aacute;c điểm tham quan.</p>\r\n<p style="text-align: justify;">\r\n	- &nbsp;Gi&aacute; c&oacute; thể thay đổi nếu h&agrave;ng kh&ocirc;ng th&ocirc;ng b&aacute;o thay đổi mức phụ thu nhi&ecirc;n liệu v&agrave; lệ ph&iacute; s&acirc;n bay ở c&aacute;c nơi&hellip;</p>\r\n<p style="text-align: justify;">\r\n	- &nbsp;Gi&aacute; v&agrave; ng&agrave;y khởi h&agrave;nh c&oacute; thể thay đổi nếu đo&agrave;n kh&ocirc;ng đủ 15 kh&aacute;ch người lớn.</p>\r\n<p style="text-align: justify;">\r\n	- &nbsp;Trường hợp qu&yacute; kh&aacute;ch kh&ocirc;ng được ĐSQ cấp Visa v&agrave;o &Uacute;c th&igrave; phải trả c&aacute;c chi ph&iacute; như dịch thuật, lệ ph&iacute; Visa, ph&iacute; tư vấn Visa v&agrave; nộp hồ sơ, ph&iacute; đặt cọc v&eacute; m&aacute;y bay&hellip;(khoảng&nbsp;8.000.000 VND/người, t&ugrave;y theo từng trường hợp cụ thể).</p>\r\n', '', '', 'Tour du lịch MELBOURNE - SYDNEY', 'du lịch sydney, du lịch melbourne, du lịch úc, du lịch australia', 'Cùng MixTourist khám phá châu Úc và trải nghiệm những điều thú vị và bất ngờ tại đất nước của những chú Kangaru nhé', '<ul>\r\n	<li style="text-align: justify;">\r\n		V&eacute; m&aacute;y bay khứ hồi&nbsp;H&agrave; Nội &ndash;&nbsp;Melbourne //Sydney&nbsp;&ndash; H&agrave; Nội, phụ thu nhi&ecirc;n liệu v&agrave; lệ ph&iacute; s&acirc;n bay c&aacute;c nước của H&agrave;ng kh&ocirc;ng&nbsp;Th&aacute;i Lan</li>\r\n	<li style="text-align: justify;">\r\n		Chuyến bay nội địa&nbsp;Melbourne &ndash; Sydney của h&agrave;ng kh&ocirc;ng Qantas</li>\r\n	<li style="text-align: justify;">\r\n		Thuế s&acirc;n bay 2 nước, ph&iacute; an ninh s&acirc;n bay, phụ thu nhi&ecirc;n liệu h&agrave;ng kh&ocirc;ng.</li>\r\n	<li style="text-align: justify;">\r\n		Kh&aacute;ch sạn ti&ecirc;u chuẩn&nbsp;3*,&nbsp;2 người /ph&ograve;ng (nếu đo&agrave;n lẻ nam hoặc nữ sẽ xếp ph&ograve;ng 3 người).</li>\r\n	<li style="text-align: justify;">\r\n		Lệ ph&iacute; visa nhập cảnh v&agrave;o &Uacute;c, hỗ trợ l&agrave;m thủ tục xin visa v&agrave; thư mời.</li>\r\n	<li style="text-align: justify;">\r\n		C&aacute;c bữa ăn theo chương tr&igrave;nh.</li>\r\n	<li style="text-align: justify;">\r\n		Nước uống theo chương tr&igrave;nh.</li>\r\n	<li style="text-align: justify;">\r\n		V&eacute; tham quan thắng cảnh theo chương tr&igrave;nh.</li>\r\n	<li style="text-align: justify;">\r\n		Xe &ocirc; t&ocirc; m&aacute;y lạnh hiện đại đưa đ&oacute;n theo chương tr&igrave;nh.</li>\r\n	<li style="text-align: justify;">\r\n		HDV kinh nghiệm, nhiệt t&igrave;nh từ VN cho đo&agrave;n từ 16 kh&aacute;ch trở l&ecirc;n. HDV tiếng Việt ki&ecirc;m l&aacute;i xe tại &Uacute;c.</li>\r\n	<li style="text-align: justify;">\r\n		Bảo hiểm du lịch quốc tế, mức bồi thường tối đa l&ecirc;n tới&nbsp;50,000USD/người/chuyến</li>\r\n</ul>\r\n', '<ul>\r\n	<li>\r\n		Ph&iacute; l&agrave;m hộ chiếu.</li>\r\n	<li>\r\n		Visa nhập cảnh lại Việt Nam cho kh&aacute;ch mang quốc tịch nước ngo&agrave;i.</li>\r\n	<li>\r\n		Phụ thu nghỉ ph&ograve;ng đơn.</li>\r\n	<li>\r\n		Tiền bồi dưỡng cho HDV v&agrave; l&aacute;i xe tại c&aacute;c nơi:&nbsp;48 USD/kh&aacute;ch/tour</li>\r\n	<li>\r\n		C&aacute;c chi ph&iacute; c&aacute; nh&acirc;n: điện thoại, giặt l&agrave;, h&agrave;nh l&yacute; qu&aacute; cước theo quy định của h&agrave;ng kh&ocirc;ng v&agrave; c&aacute;c chi ph&iacute; kh&aacute;c kh&ocirc;ng c&oacute; trong chương tr&igrave;nh.</li>\r\n</ul>\r\n');
INSERT INTO `tour` (`id`, `tour_quoc_te`, `DanhMuc1Id`, `DanhMuc2Id`, `danhmuc_multi`, `promotion`, `packages`, `name`, `name_url`, `count_down`, `code`, `img`, `price_sales`, `price`, `price_2`, `price_3`, `price_4`, `price_5`, `price_6`, `durations`, `departure`, `destination`, `departure_time`, `vehicle`, `hotel`, `summary`, `highlights`, `schedule`, `price_list`, `content`, `list_img`, `title`, `keyword`, `description`, `inclusion`, `exclusion`) VALUES
(14, 1, 8, 47, '46,47', 1, 1, 'Tour du lịch Úc - New Zealand', 'tour-du-lich-uc-new-zealand', '', '', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/uc/du-lich-uc-new-zealand.jpg', '', '77500000', '', '', '', '', '', '9 Ngày 8 Đêm', '1', 'Sydney, Melbourne, Aucland, Taupo, Rotorua', '20/03, 28/04, 28/05, 28/06, 28/07', 'Máy bay, Ô tô', 3, '', '', '<p>\r\n	<u><strong>Lịch bay dự kiến:</strong></u></p>\r\n<p>\r\n	1 VN 257B 20MAR&nbsp; HANSGN 1610&nbsp; 1820</p>\r\n<p>\r\n	2 VN 773L 20MAR&nbsp; SGNSYD &nbsp;2110&nbsp; 0950&nbsp;&nbsp; 21MAR</p>\r\n<p>\r\n	3 NZ 718L 22MAR&nbsp; SYDAKL &nbsp;1550&nbsp; 2100</p>\r\n<p>\r\n	4 NZ 123L 26MAR&nbsp; AKLMEL 0830&nbsp; 1035</p>\r\n<p>\r\n	5 VN 780T 28MAR&nbsp; MELSGN 1125&nbsp; 1545</p>\r\n<p>\r\n	6 VN 264B 28MAR&nbsp; SGNHAN 1745&nbsp; 1950</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color: rgb(255, 0, 0);"><span style="font-size: 16px;">NG&Agrave;Y 01: H&Agrave; NỘI &ndash; SYDNEY</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Hướng dẫn vi&ecirc;n đ&oacute;n Qu&yacute; kh&aacute;ch tại s&acirc;n bay Nội B&agrave;i, l&agrave;m thủ tục chuyến bay đi Sydney. Qu&yacute; kh&aacute;ch nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color: rgb(255, 0, 0);"><span style="font-size: 16px;">NG&Agrave;Y 02: SYDNEY &ndash; CITY TOUR</span></span><span style="color:#ff0000;"><span style="font-size: 16px;">&nbsp;</span><span style="font-size:16px;">(ĂN TRƯA, TỐI)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong> Xe v&agrave; HDV địa phương đ&oacute;n Qu&yacute; kh&aacute;ch tại s&acirc;n bay đưa về th&agrave;nh phố. Đo&agrave;n bắt đầu chương tr&igrave;nh kh&aacute;m ph&aacute; th&agrave;nh phố Sydney với những cảnh đẹp như: T&ograve;a nh&agrave; Queen Victoria, Vườn b&aacute;ch thảo- Botanic Gardens, Nh&agrave; h&aacute;t Sydney Opera - biểu tượng của Sydney, Cầu cảng Darling Harbour,&nbsp;Mrs. Macquaries Chair, Khu The Rocks -&nbsp;Mũi đ&aacute; lịch sử v&agrave; ngắm nh&igrave;n to&agrave;n cảnh th&agrave;nh phố; Sydney Tower&nbsp;(thăm b&ecirc;n ngo&agrave;i).</p>\r\n<p style="text-align: justify;">\r\n	<strong>12h00:</strong> Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong> Qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u&nbsp;Captain Cook Highlights&nbsp;ngắm cảnh&nbsp;Vịnh Jackson&nbsp;v&agrave; ngắm nh&igrave;n th&agrave;nh phố Sydney tuyệt đẹp từ g&oacute;c nh&igrave;n của biển v&agrave; d&ugrave;ng đồ uống nhẹ.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối:</strong>&nbsp;Ăn tối tại nh&agrave; h&agrave;ng. Qu&yacute; kh&aacute;ch nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color: rgb(255, 0, 0);"><span style="font-size: 16px;">NG&Agrave;Y 03:&nbsp;SYDNEY &ndash; WILDLIFE PARK - AUCKLAND</span></span><span style="font-size:16px;"><span style="color:#ff0000;">&nbsp;(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng: </strong>Qu&yacute; kh&aacute;ch ăn s&aacute;ng tại kh&aacute;ch sạn, sau đ&oacute; Qu&yacute; kh&aacute;ch khởi h&agrave;nh đi tham quan:&nbsp;Vườn th&uacute; hoang d&atilde;WildLife&nbsp;Park&nbsp;nơi bảo tồn những loại động vật Qu&yacute; hiếm của Australia. Qu&yacute; kh&aacute;ch c&oacute; thể ngắm nh&igrave;n cận cảnh&nbsp;những ch&uacute; Kangaroos, Đ&agrave; điểu sa mạc &uacute;c, gấu T&uacute;i Koala...</p>\r\n<p style="text-align: justify;">\r\n	<strong>12h00&nbsp;:</strong> Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n khởi h&agrave;nh về lại Sydney. Xe đưa Qu&yacute; kh&aacute;ch đi s&acirc;n bay đ&aacute;p chuyến bay đi Auckland &ndash; New Zealand</p>\r\n<p style="text-align: justify;">\r\n	3 NZ 718L 22MAR&nbsp; SYDAKL 1550&nbsp; 2100</p>\r\n<p style="text-align: justify;">\r\n	Xe đ&oacute;n Qu&yacute; kh&aacute;ch về th&agrave;nh phố Auckland. Qu&yacute; kh&aacute;ch ăn tối v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color: rgb(255, 0, 0);"><span style="font-size: 16px;">NG&Agrave;Y 04: ROTORUA &ndash; ĐỘNG ĐOM Đ&Oacute;M- RỪNG C&Acirc;Y GỖ ĐỎ&nbsp;</span></span><span style="color:#ff0000;"><span style="font-size:16px;">(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng: </strong>Qu&yacute; kh&aacute;ch ăn s&aacute;ng tại kh&aacute;ch sạn, Đo&agrave;n khởi h&agrave;nh đi&nbsp;Th&agrave;nh phố Rotorua&nbsp;&ndash;l&agrave; th&agrave;nh phố cao nhất của New Zealand với độ cao 305m (1000 feet) so với mặt nước biển, c&ograve;n được gọi l&agrave; &ldquo;Th&agrave;nh phố Lưu huỳnh&rdquo;.</p>\r\n<p style="text-align: justify;">\r\n	Tr&ecirc;n đường đi, Qu&yacute; kh&aacute;ch&nbsp;gh&eacute; thăm&nbsp;B&atilde;i biển C&aacute;t đen&nbsp;độc đ&aacute;o tại New Zealand v&agrave; thăm&nbsp;Đảo Chim.</p>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n di chuyển đến&nbsp;động&nbsp;Waitomo&nbsp;với hệ thống những hang động ngầm b&ecirc;n dưới mặt đất d&agrave;i h&agrave;ng trăm km. Chương tr&igrave;nh kh&aacute;m ph&aacute; vẻ đẹp kỳ vĩ của thi&ecirc;n nhi&ecirc;n tại:&nbsp;Waitomo Caves-khu hang động nằm s&acirc;u trong l&ograve;ng đất với những khối thạch nhũ được h&igrave;nh th&agrave;nh qua h&agrave;ng trăm triệu năm. Đặc biệt l&agrave; kh&aacute;m ph&aacute; khu&nbsp;Động đom đ&oacute;m-hang động với h&agrave;ng triệu đom đ&oacute;m ph&aacute;t s&aacute;ng nằm treo lơ lửng tr&ecirc;n th&agrave;nh hang tạo n&ecirc;n một vẻ huyền b&iacute; hiếm thấy.</p>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n đến Rotorua, Xe đưa Qu&yacute; kh&aacute;ch đi thăm quan v&agrave; tản bộ trong&nbsp;Rừng c&acirc;y gỗ đỏ-Red Wood Jungle.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối: </strong>Qu&yacute; kh&aacute;ch ăn tối, nhận ph&ograve;ng v&agrave; nghỉ ngơi tại kh&aacute;ch sạn ở Rotorua.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color: rgb(255, 0, 0);"><span style="font-size: 16px;">NG&Agrave;Y 05: HOBBITON &ndash; AUCKLAND-CITY TOUR</span></span><span style="color:#ff0000;">&nbsp;<span style="font-size:16px;">(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng: </strong>Ăn s&aacute;ng tại kh&aacute;ch sạn, l&agrave;m thủ tục trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh về Auckland. Gh&eacute; thăm&nbsp;ng&ocirc;i l&agrave;ng xinh xắn Hobbiton tại thị trấn Matamata&nbsp;&ndash; nơi đ&atilde; tạo ra t&aacute;c phẩm điện ảnh nổi tiếng&nbsp;Lord Of The Rings (Ch&uacute;a tể của những chiếc nhẫn) của đạo diễn Peter Jackson cuối những năm 1990.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	Về đến Auckland, xe v&agrave; HDV đưa Qu&yacute; kh&aacute;ch đi tham quan th&agrave;nh phố:&nbsp;Eden Mount&nbsp;&ndash; ngọn n&uacute;i lửa h&igrave;nh ch&eacute;n đ&atilde; ngưng hoạt động v&agrave; l&agrave; nơi Qu&yacute; kh&aacute;ch c&oacute; thể ngắm nh&igrave;n to&agrave;n cảnh th&agrave;nh phố Auckland,&nbsp;Cầu cảng Harbour Bridge&nbsp;l&agrave; điểm nhấn tuyệt đẹp của Auckland.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối:&nbsp;</strong>Qu&yacute; kh&aacute;ch ăn tối v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn ở Auckland.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color: rgb(255, 0, 0);"><span style="font-size: 16px;">NG&Agrave;Y 06: AUCKLAND &ndash; CITY TOUR</span></span><span style="color:#ff0000;"><span style="font-size:16px;">&nbsp;(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong> Sau bữa s&aacute;ng, xe đưa Qu&yacute; kh&aacute;ch đi kh&aacute;m ph&aacute; th&agrave;nh phố Auckland, bắt đầu l&agrave;:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Đại&nbsp;lộ nữ ho&agrave;ng (Queen Street)&nbsp;- con phố ch&iacute;nh của Auckland, k&eacute;o d&agrave;i 3 km, l&agrave; nơi tập trung nhiều t&ograve;a nh&agrave; lớn nhất v&agrave; c&oacute; lịch sử l&acirc;u đời ở New Zealand, l&agrave; nơi mua sắm l&yacute; tưởng cho du kh&aacute;ch.</li>\r\n	<li style="text-align: justify;">\r\n		Tamaki Waterfront Drive&nbsp;&ndash; con đường ven biển v&agrave; dừng thăm&nbsp;vịnh Mission Bay</li>\r\n	<li style="text-align: justify;">\r\n		Thăm quan&nbsp;Đ&agrave;i tưởng niệm L&atilde;nh đạo Đảng Lao động&nbsp;đầu ti&ecirc;n của nước New Zealand</li>\r\n	<li style="text-align: justify;">\r\n		Thăm&nbsp;phố cổ Parnell&nbsp;&ndash; trước đ&acirc;y l&agrave; ng&ocirc;i l&agrave;ng nổi tiếng ở ph&iacute;a đ&ocirc;ng trung t&acirc;m th&agrave;nh phố với những ng&ocirc;i nh&agrave; villa bằng gỗ xinh đẹp v&agrave; độc đ&aacute;o.</li>\r\n	<li style="text-align: justify;">\r\n		C&ocirc;ng vi&ecirc;n The Domain&nbsp;- c&ocirc;ng vi&ecirc;n rộng 75 hecta tọa lạc ngay trung t&acirc;m th&agrave;nh phố.</li>\r\n	<li style="text-align: justify;">\r\n		Qu&yacute; kh&aacute;ch ăn trưa tại&nbsp;nh&agrave; h&agrave;ng Seafood Eden Road.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>Trở lại trung t&acirc;m th&agrave;nh phố,&nbsp;thăm quan v&agrave; mua sắm tại Đại lộ nữ ho&agrave;ng&nbsp;(Queen Street)&nbsp;- con phố ch&iacute;nh của Auckland, k&eacute;o d&agrave;i 3 km, l&agrave; nơi tập trung nhiều t&ograve;a nh&agrave; lớn nhất v&agrave; c&oacute; lịch sử l&acirc;u đời ở New Zealand, l&agrave; nơi mua sắm l&yacute; tưởng cho du kh&aacute;ch c&aacute;c loại h&agrave;ng h&oacute;a như: quần &aacute;o; đồ lưu niệm; mỹ phẩm....</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối: </strong>Qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng v&agrave; nghỉ ngơi tại kh&aacute;ch sạn ở Auckland.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color: rgb(255, 0, 0);"><span style="font-size: 16px;">NG&Agrave;Y 07: AUCKLAND - MELBOURNE &ndash; CITY TOUR&nbsp;</span></span><span style="color:#ff0000;"><span style="font-size:16px;">(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Xe đưa Qu&yacute; kh&aacute;ch đi s&acirc;n bay đ&aacute;p chuyến bay đi th&agrave;nh phố Melbourne</p>\r\n<p style="text-align: justify;">\r\n	4 NZ 123L 26MAR&nbsp; AKLMEL 0830&nbsp; 1035</p>\r\n<p style="text-align: justify;">\r\n	Xuống s&acirc;n bay Melbourne, xe đ&oacute;n Qu&yacute; kh&aacute;ch bắt đầu kh&aacute;m ph&aacute; th&agrave;nh phố Melbourne qua c&aacute;c thắng cảnh như:&nbsp;Phố Swanston;&nbsp;Nh&agrave; thờ lớn tr&ecirc;n đường Patrick;&nbsp;Treasure House.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng, sau đ&oacute; tiếp tục thăm quan&nbsp;Vườn hoa Fitzroy Garden;&nbsp;Trung t&acirc;m t&agrave;i ch&iacute;nh tr&ecirc;n đường Collins Street,&nbsp;T&ograve;a nh&agrave; Quốc hội-Parliament House,&nbsp;Trung t&acirc;m mỹ thuật-&nbsp;Arts Precincts.&nbsp;Quảng trường li&ecirc;n bang&nbsp;Federation Square, Đ&agrave;i tưởng niệm chiến tranh.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối: </strong>Qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng v&agrave; tự do thăm quan&nbsp;Southbank &ndash;&nbsp;đ&ocirc; thị mới của Melbourne. Qu&yacute; kh&aacute;ch kh&aacute;m ph&aacute; s&ograve;ng b&agrave;i lớn nhất Nam B&aacute;n Cầu tại&nbsp;Crow Casino Melbourne&nbsp;v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 08: MELBOURNE - TRANG TRẠI HOA QUẢ&nbsp;- BALLARAT&nbsp;(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong> Sau bữa s&aacute;ng, xe đưa đo&agrave;n&nbsp; khởi h&agrave;nh đi Ballarat, tr&ecirc;n đường đi, đo&agrave;n gh&eacute; thăm v&agrave; h&aacute;i tr&aacute;i c&acirc;y tại trang trại&nbsp;T&aacute;o; D&acirc;u t&acirc;y; Đ&agrave;o; Cherry...&nbsp;(T&ugrave;y theo từng m&ugrave;a h&aacute;i tr&aacute;i g&igrave;). Qu&yacute; kh&aacute;ch tận tay h&aacute;i v&agrave; ăn thỏa th&iacute;ch tại vườn tr&aacute;i v&agrave; mua những tr&aacute;i ngon do ch&iacute;nh m&igrave;nh h&aacute;i mang về l&agrave;m qu&agrave;, đ&acirc;y l&agrave; một trong những chương tr&igrave;nh hấp dẫn v&agrave; mang lại cho qu&yacute; kh&aacute;ch những trải nghiệm th&uacute; vị.</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Đo&agrave;n khởi h&agrave;nh đi Thị trấn&nbsp;Ballarat&nbsp;t&igrave;m hiểu về nơi đ&atilde; kh&aacute;m ph&aacute; ra mỏ v&agrave;ng năm 1851 qua sự t&aacute;i hiện cảnh đ&agrave;o v&agrave;ng trong thế kỷ 19. Qu&yacute; kh&aacute;ch thử vận may, đ&atilde;i v&agrave;ng tại d&ograve;ng suối, nếu may mắn c&oacute; thể t&igrave;m được v&agrave;ng. Sau đ&oacute; Qu&yacute; kh&aacute;ch được hướng dẫn đưa v&agrave;o thăm quan mỏ v&agrave;ng v&agrave; được nghe giới thiệu c&aacute;ch thức khai th&aacute;c v&agrave;ng từ thế kỷ 19.</li>\r\n	<li style="text-align: justify;">\r\n		Qu&yacute; kh&aacute;ch d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng. Tiếp tục thăm quan&nbsp;Đồi v&agrave;ng v&agrave; bảo t&agrave;ng v&agrave;ng&nbsp;&ndash; nơi trưng b&agrave;y những khối v&agrave;ng được t&igrave;m thấy tại&nbsp;Ballarat.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n quay trở về Melbourne. Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color: rgb(255, 0, 0);"><span style="font-size: 16px;">NG&Agrave;Y 09: MELBOURNE &ndash; H&Agrave; NỘI</span></span><span style="color:#ff0000;"><span style="font-size:16px;">&nbsp;(ĂN S&Aacute;NG)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Sau bữa s&aacute;ng, Qu&yacute; kh&aacute;ch l&agrave;m thủ tục trả ph&ograve;ng v&agrave; khởi h&agrave;nh ra s&acirc;n bay để đ&aacute;p chuyến bay về lại Việt Nam.</p>\r\n<p style="text-align: justify;">\r\n	5 VN 780T 28MAR&nbsp; MELSGN 1125&nbsp; 1545</p>\r\n<p style="text-align: justify;">\r\n	6 VN 264B 28MAR&nbsp; SGNHAN 1745&nbsp; 1950</p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>GI&Aacute; TRỌN G&Oacute;I / KH&Aacute;CH</strong></span></span></h2>\r\n<div>\r\n	<table border="1" cellpadding="0" cellspacing="0">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						<strong>Số lượng kh&aacute;ch</strong></p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						<strong>Người lớn</strong></p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						<strong>Trẻ em</strong></p>\r\n					<p style="text-align: center;">\r\n						<strong>(2-11 tuổi ngủ ri&ecirc;ng giường)</strong></p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						<strong>Em b&eacute;</strong></p>\r\n					<p style="text-align: center;">\r\n						<strong>dưới 2 tuổi</strong></p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						Từ 10-14 kh&aacute;ch người lớn</p>\r\n					<p style="text-align: center;">\r\n						(đi gh&eacute;p, kh&ocirc;ng c&oacute; trưởng đo&agrave;n từ VN hoặc c&oacute; phụ thu)</p>\r\n				</td>\r\n				<td rowspan="2">\r\n					<p style="text-align: center;">\r\n						77.500.000đ</p>\r\n				</td>\r\n				<td rowspan="2">\r\n					<p style="text-align: center;">\r\n						69.750.000đ</p>\r\n				</td>\r\n				<td rowspan="2">\r\n					<p style="text-align: center;">\r\n						23.250.000đ</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						Từ 15 kh&aacute;ch</p>\r\n					<p style="text-align: center;">\r\n						(đi gh&eacute;p c&oacute; trưởng đo&agrave;n từ VN)</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<h3>\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">LƯU &Yacute;</span></span></h3>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		C&aacute;c kh&aacute;ch gh&eacute;p lẻ c&oacute; thể xuất ph&aacute;t từ H&agrave; Nội// Đ&agrave; Nẵng // S&agrave;i G&ograve;n. Trưởng đo&agrave;n từ VN c&oacute; thể xuất ph&aacute;t từ&nbsp; H&agrave; Nội// Đ&agrave; Nẵng // S&agrave;i G&ograve;n</li>\r\n	<li style="text-align: justify;">\r\n		Phụ thu ph&ograve;ng đơn trong trường hợp lẻ nam hoặc nữ: 8.500.000đ/kh&aacute;ch/tour</li>\r\n	<li style="text-align: justify;">\r\n		Em b&eacute; dưới 2 tuổi gi&aacute; bao gồm: Visa, bảo hiểm, V&eacute; m&aacute;y bay, ngủ chung giường với cha mẹ. V&igrave; vậy cha mẹ tự lo c&aacute;c chi ph&iacute; ph&aacute;t sinh v&agrave; ăn uống theo c&aacute;ch ri&ecirc;ng của b&eacute;.</li>\r\n</ul>\r\n', '', '', 'Tour du lịch Úc - New Zealand', 'du lịch úc và new zealand, du lịch úc, du lịch new zealand', 'Tour du lịch Úc và New Zealand của MixTourist sẽ mang lại cho bạn rất nhiều trải nghiệm thú vị và vô cùng tuyệt vời.', '<ul>\r\n	<li style="text-align: justify;">\r\n		Kh&aacute;ch sạn ti&ecirc;u chuẩn quốc tế 3 sao, 2 kh&aacute;ch/ph&ograve;ng, trường hợp lẻ nam//nữ ngủ ph&ograve;ng ba hoặc phụ thu ph&ograve;ng đơn với c&aacute;c kh&aacute;ch đi đơn lẻ v&agrave; kh&ocirc;ng thể gh&eacute;p ph&ograve;ng.</li>\r\n	<li style="text-align: justify;">\r\n		Ăn theo chư&shy;ơng tr&igrave;nh (kh&ocirc;ng gồm đồ uống) 15AUD/người/bữa trưa v&agrave; 20AUD/người/bữa tối.</li>\r\n	<li style="text-align: justify;">\r\n		Vận chuyển bằng xe du lịch ti&ecirc;u chuẩn Quốc tế.</li>\r\n	<li style="text-align: justify;">\r\n		Ph&iacute; tham quan thắng cảnh, v&agrave;o cửa một lần.</li>\r\n	<li style="text-align: justify;">\r\n		Hư&shy;ớng dẫn vi&ecirc;n tiếng Việt ki&ecirc;m l&aacute;i xe tại &Uacute;c hoặc hướng dẫn vi&ecirc;n tiếng Việt từ VN.</li>\r\n	<li style="text-align: justify;">\r\n		V&eacute; m&aacute;y bay theo lịch tr&igrave;nh.</li>\r\n	<li style="text-align: justify;">\r\n		Visa nhập cảnh v&agrave;o Australia; New Zealand.</li>\r\n	<li style="text-align: justify;">\r\n		Bảo hiểm du lịch quốc tế mức 30.000USD/1 kh&aacute;ch</li>\r\n	<li style="text-align: justify;">\r\n		Mỗi kh&aacute;ch một chai nước/ ng&agrave;y</li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n', '<ul>\r\n	<li>\r\n		Chi ph&iacute; ph&ograve;ng đơn, điện thoại, giặt l&agrave;, đi lại ngo&agrave;i giờ, h&agrave;nh l&yacute; qu&aacute; cước...</li>\r\n	<li>\r\n		C&aacute;c chi ph&iacute; ph&aacute;t sinh kh&ocirc;ng đề cập trong chương tr&igrave;nh.</li>\r\n	<li>\r\n		Tiền Tip cho HDV v&agrave; l&aacute;i xe ở nư&shy;ớc ngo&agrave;i 7 USD/ng&agrave;y/ngư&shy;ời.</li>\r\n	<li>\r\n		Chi ph&iacute; đi lại để lấy dấu v&acirc;n tay (khi nộp hồ sơ visa) tại HN hoặc HCM</li>\r\n	<li>\r\n		Chi ph&iacute; ph&aacute;t sinh dịch vụ tr&ecirc;n thực tế</li>\r\n</ul>\r\n'),
(15, 1, 8, 46, '', 1, 1, 'Tour du lịch Úc du xuân tết âm lịch', 'tour-du-lich-uc-du-xuan-tet-am-lich', '', '', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/uc/du-lich-uc-tet-mixtourist.jpg', '', '60900000', '', '', '', '', '', '7 Ngày 6 Đêm', '1', 'Sydney, Melbourne, Canbera', 'Mùng 1 tết, mùng 2 tết', 'Máy bay, Ô tô', 3, '', '', '<h2 style="text-align: justify;">\r\n	<span style="font-size: 16px; color: rgb(255, 0, 0);">NG&Agrave;Y 01: H&Agrave;NỘI/ Đ&Agrave; NẴNG/ HỒ CH&Iacute; MINH - SYDNEY &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (Ăn tối tr&ecirc;n m&aacute;y bay)</span></h2>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch tập trung tại điểm hẹn trong th&agrave;nh phố H&agrave; Nội, xe v&agrave; trưởng đo&agrave;n của c&ocirc;ng ty đ&oacute;n Qu&yacute; kh&aacute;ch đưa ra s&acirc;n bay l&agrave;m thủ tục đ&aacute;p chuyến bay khởi h&agrave;nh đi Sydney, đo&agrave;n transit tại TP.Hồ Ch&iacute; Minh.</p>\r\n<p style="text-align: justify;">\r\n	Kh&aacute;ch h&agrave;ng ở Đ&agrave; Nẵng v&agrave; HCM tự t&uacute;c ra s&acirc;n bay.</p>\r\n<p style="text-align: justify;">\r\n	Sau đ&oacute;,&nbsp;đo&agrave;n tiếp tục khởi h&agrave;nh đến Sydney v&agrave; nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="http://dulichaustralia.com.vn/uploads/images/dulichuc-nha-hat-con-so.jpg" style="height: 399px; width: 600px;" /></p>\r\n<h2 style="text-align: center;">\r\n	&nbsp;</h2>\r\n<div>\r\n	<h2 style="text-align: justify;">\r\n		<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 02: &nbsp; SYDNEY - CITY TOUR &nbsp;(Ăn s&aacute;ng tr&ecirc;n m&aacute;y bay, trưa, tối)</span></span></h2>\r\n</div>\r\n<p style="text-align: justify;">\r\n	Đến s&acirc;n bay Sydney, xe v&agrave; HDV đ&oacute;n đo&agrave;n. Qu&yacute; kh&aacute;ch bắt đầu một h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; th&agrave;nh phố Sydney với những cảnh đẹp như:</p>\r\n<p style="text-align: justify;">\r\n	Nh&agrave; h&aacute;t Con S&ograve;&nbsp;(Sydney Opera House)&nbsp;c&oacute; kiến tr&uacute;c cực kỳ ấn tượng v&agrave; nổi tiếng của Sydney với ba mặt hướng ra biển.</p>\r\n<p style="text-align: justify;">\r\n	Darling Habour:&nbsp;một cảng biển nhộn nhịp nơi c&oacute; nhiều qu&aacute;n c&agrave; ph&ecirc; l&atilde;ng mạn ngo&agrave;i trời nổi tiếng ở Sydney.</p>\r\n<p style="text-align: justify;">\r\n	Vườn B&aacute;ch Thảo (Botanic Garden):&nbsp;vườn thực vật được mở cửa từ năm 1816 với diện t&iacute;ch 30 hectares.</p>\r\n<p style="text-align: justify;">\r\n	Mrs. Macquaries Chair&nbsp;để ghi những bức h&igrave;nh đẹp nhất của Cầu cảng&nbsp;Sydney (Sydney Bridge &amp; Habour).</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>Đo&agrave;n thăm&nbsp;khu phố cổ The Rocks&nbsp;-&nbsp;thường được v&iacute; như &ldquo;bảo t&agrave;ng Sydney ngo&agrave;i trời&rdquo; bởi nơi n&agrave;y hội tụ cả qu&aacute; khứ v&agrave; hiện tại của th&agrave;nh phố Sydney.&nbsp;Khu The Rocks nằm kề cầu cảng Sydney l&agrave; nơi những người da trắng đầu ti&ecirc;n đến định cư ở &Uacute;c, bởi thế n&oacute; được xem l&agrave; cội nguồn h&igrave;nh th&agrave;nh n&ecirc;n th&agrave;nh phố Sydney v&agrave; đất nước &Uacute;c.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng, nhận ph&ograve;ng v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn ở Sydney.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="http://dulichaustralia.com.vn/uploads/images/dulichuc-chuot-tui.jpg" style="height: 399px; width: 600px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<div>\r\n	<h2 style="text-align: justify;">\r\n		<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 03: FEATHERDALE PARK - BLUE MOUNTAIN (Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n</div>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng: </strong>Qu&yacute; kh&aacute;ch ăn s&aacute;ng tại kh&aacute;ch sạn. Sau đ&oacute;, xe đưa đo&agrave;n tham quan - tại đ&acirc;y qu&yacute; kh&aacute;ch c&oacute; dịp ngắm nh&igrave;n v&ocirc; số động vật y&ecirc;u qu&yacute; nhất của &Uacute;c: gấu koalas, emus, chuột t&uacute;i, chim c&ocirc;ng, chim c&aacute;nh cụt&hellip; chụp h&igrave;nh lưu niệm.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>Đo&agrave;n tham quan Khu bảo tồn thi&ecirc;n nhi&ecirc;n&nbsp;Blue Mountain&nbsp;- Chụp ảnh tại mỏm n&uacute;i đẹp nhất (Three sisters), nếu may mắn du kh&aacute;ch c&oacute; thể chụp được những bức h&igrave;nh c&ugrave;ng với người thổ d&acirc;n với ống kh&egrave;n v&agrave; những giai điệu truyền thống. Tiếp tục trải nghiệm bằng T&agrave;u k&eacute;o&nbsp;Scenicworld&nbsp;an to&agrave;n xuống ch&acirc;n n&uacute;i để dạo bộ trong c&ocirc;ng vi&ecirc;n quốc gia, ngắm nh&igrave;n những khung cảnh tuyệt đẹp, sau đ&oacute; đi ra bến xe c&aacute;p treo trở lại điểm ban đầu.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn tối v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="http://dulichaustralia.com.vn/uploads/images/dulichuc-new-parliament.jpg" style="height: 399px; width: 600px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<div>\r\n	<h2 style="text-align: justify;">\r\n		<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 04: SYDNEY - CANBERRA - MELBOURNE (Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n</div>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch d&ugrave;ng điểm t&acirc;m tại kh&aacute;ch sạn. Sau đ&oacute;, xe đưa qu&yacute; kh&aacute;ch đi tới thủ đ&ocirc; của nước &Uacute;c &ndash;&nbsp;Thủ đ&ocirc;&nbsp;Canberra. Sau hơn 3 giờ, qu&yacute; kh&aacute;ch đến thủ đ&ocirc; Canberra, bắt đầu h&agrave;nh tr&igrave;nh thăm quan với những thắng cảnh đẹp của thủ đ&ocirc; như:&nbsp;New Parliament house&nbsp;&ndash; Nh&agrave; Quốc hội với lối kiến tr&uacute;c v&agrave; ph&ograve;ng họp của&nbsp;Thượng viện, Hạ viện, tọa lạc b&ecirc;n hồ&nbsp;Burley Griffin&nbsp;tuyệt đẹp.</p>\r\n<p style="text-align: justify;">\r\n	Xe chạy dọc theo hồ&nbsp;Burley Griffin&nbsp;tuyệt đẹp, th&aacute;p phun nước&nbsp;Captain Cook&nbsp;v&agrave; thăm quan con đường&nbsp; nơi đặt trụ sở Đại sứ qu&aacute;n c&aacute;c nước.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong> Xe v&agrave; HDV đ&oacute;n đo&agrave;n khởi h&agrave;nh ra s&acirc;n bay Canberra đ&aacute;p chuyến bay đi Melbourne - th&agrave;nh phố lớn thứ hai tại Australia.</p>\r\n<p style="text-align: justify;">\r\n	3 QF 483G 12FEB CBRMEL 2000&nbsp; 2110</p>\r\n<p style="text-align: justify;">\r\n	Tới Melbourne, xe đưa qu&yacute; kh&aacute;ch đi ăn tối v&agrave; về nhận ph&ograve;ng kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch tự do nghỉ ngơi v&agrave; kh&aacute;m ph&aacute; Melbourne về đ&ecirc;m.</p>\r\n<div>\r\n	<h2 style="text-align: justify;">\r\n		<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 05: &nbsp; MELBOURNE &ndash; CITY TOUR &nbsp;(Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n</div>\r\n<p style="text-align: justify;">\r\n	Sau bữa điểm t&acirc;m, Xe &nbsp;đưa đo&agrave;n đi tham quan th&agrave;nh phố Melbourne, th&agrave;nh phố lớn thứ hai tại &Uacute;c. Kh&aacute;m ph&aacute; c&aacute;c thắng cảnh đẹp tại Melbourne như:&nbsp;Phố Swanston; Nh&agrave; thờ lớn tr&ecirc;n đường Patrick; Treasure House, Fitzroy Garden&hellip;</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng, sau đ&oacute; tiếp tục thăm quan&nbsp;Trung t&acirc;m t&agrave;i ch&iacute;nh tr&ecirc;n đường Collins Street, Parliament House, trung t&acirc;m mỹ thuật Arts Precincts, quảng trường li&ecirc;n bang Federation Square, đ&agrave;i tưởng niệm chiến tranh.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối: </strong>Qu&yacute; kh&aacute;ch ăn tối v&agrave; nghỉ đ&ecirc;m ở kh&aacute;ch sạn tại Melbourne hoặc tự do kh&aacute;m ph&aacute; v&agrave; thăm quan thử vận may tại&nbsp;s&ograve;ng b&agrave;i Crow&nbsp;lớn nhất nam b&aacute;n cầu&nbsp;.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="http://dulichaustralia.com.vn/uploads/images/dulichuc-rose-garden.jpg" style="height: 399px; width: 600px;" /></p>\r\n<h2 style="text-align: justify;">\r\n	&nbsp;</h2>\r\n<div>\r\n	<h2 style="text-align: justify;">\r\n		<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 06: MELBOURNE - ROSE GARDEN - &nbsp;BALLARAT &nbsp;(Ăn s&aacute;ng, trưa, tối)</span></span></h2>\r\n</div>\r\n<p style="text-align: justify;">\r\n	Sau bữa s&aacute;ng, Xe đưa qu&yacute; kh&aacute;ch đến với&nbsp;Rose Garden&nbsp;nơi c&oacute; hơn 5000 ng&agrave;n lo&agrave;i hoa hồng được sưu tập giống từ khắp thế giới đang c&ugrave;ng nhau khoe sắc, qu&yacute; kh&aacute;ch thư gi&atilde;n thưởng thức v&agrave; chụp h&igrave;nh lưu niệm tuyệt đẹp.</p>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n khởi h&agrave;nh đi Thị trấn&nbsp;Ballarat&nbsp;t&igrave;m hiểu về nơi đ&atilde; kh&aacute;m ph&aacute; ra mỏ v&agrave;ng năm 1851 qua sự t&aacute;i hiện cảnh đ&agrave;o v&agrave;ng trong thế kỷ 19. Qu&yacute; kh&aacute;ch thử vận may đầu năm mới, đ&atilde;i v&agrave;ng tại d&ograve;ng suối, nếu may mắn c&oacute; thể t&igrave;m được v&agrave;ng. Sau đ&oacute; qu&yacute; kh&aacute;ch được hướng dẫn đưa v&agrave;o thăm quan mỏ v&agrave;ng v&agrave; được nghe giới thiệu c&aacute;ch thức khai th&aacute;c v&agrave;ng từ thế kỷ 19. Tiếp tục thăm quan đồi v&agrave;ng v&agrave; bảo t&agrave;ng v&agrave;ng &ndash; nơi trưng b&agrave;y những khối v&agrave;ng được t&igrave;m thấy tại&nbsp;Ballarat.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<div>\r\n	<h2 style="text-align: justify;">\r\n		<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 07: MELBOURNE - H&Agrave; NỘI/ Đ&Agrave; NẴNG/ HCM &nbsp;(Ăn s&aacute;ng)</span></span></h2>\r\n</div>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n ăn s&aacute;ng tại kh&aacute;ch sạn v&agrave; l&agrave;m thủ tục trả ph&ograve;ng. Xe đưa đo&agrave;n ra s&acirc;n bay Melbourne l&agrave;m thủ tục đ&aacute;p chuyến bay về Việt Nam. Ăn trưa tr&ecirc;n m&aacute;y bay. Về đến điểm đ&oacute;n ban đầu. Kết th&uacute;c chương tr&igrave;nh tour. Xin ch&agrave;o tạm biệt v&agrave; hẹn gặp lại Qu&yacute; kh&aacute;ch.</p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">GI&Aacute; TOUR TRỌN G&Oacute;I: VNĐ/ KH&Aacute;CH</span></span></h2>\r\n<p style="text-align: center;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">(&Aacute;p dụng cho đo&agrave;n từ 15-20 kh&aacute;ch)</span></span></strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n<table border="1" cellpadding="0" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>Ng&agrave;y khởi h&agrave;nh</strong></p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>Người lớn</strong></p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>Trẻ em (2-11 tuổi)</strong></p>\r\n				<p style="text-align: center;">\r\n					<strong>(Ngủ ri&ecirc;ng giường)</strong></p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					<strong>Em b&eacute; &gt;2 tuổi</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					M&ugrave;ng 1 (08/02)</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					62.900.000đ</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					55.700.000đ</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					18.570.000đ</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					M&ugrave;ng 2 (09/02)</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					60.900.000đ</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					53.900.000đ</p>\r\n			</td>\r\n			<td>\r\n				<p style="text-align: center;">\r\n					17.970.000đ</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">GHI CH&Uacute;:</span></span></h3>\r\n<p style="text-align: justify;">\r\n	C&ocirc;ng ty sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm bảo đảm c&aacute;c điểm tham quan hoặc ho&agrave;n lại chi ph&iacute; tour trong trường hợp:</p>\r\n<p style="text-align: justify;">\r\n	+ Xảy ra thi&ecirc;n tai: b&atilde;o lụt, hạn h&aacute;n, động đất&hellip;</p>\r\n<p style="text-align: justify;">\r\n	+ Sự cố về an ninh: khủng bố, biểu t&igrave;nh</p>\r\n<p style="text-align: justify;">\r\n	+ Sự cố về h&agrave;ng kh&ocirc;ng: trục trặc kỹ thuật, an ninh, dời, hủy, ho&atilde;n chuyến bay.</p>\r\n<p style="text-align: justify;">\r\n	+ V&igrave; l&yacute; do kh&aacute;ch quan, hải quan nước sở tại kh&ocirc;ng cho ph&eacute;p nhập cảnh th&igrave; c&ocirc;ng ty du lịch sẽ kh&ocirc;ng ho&agrave;n lại bất kỳ chi ph&iacute; ph&aacute;t sinh v&agrave; ph&iacute; kh&ocirc;ng sử dụng dịch vụ n&agrave;o của kh&aacute;ch.</p>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">ĐIỀU KIỆN &Aacute;P DỤNG B&Aacute;O GI&Aacute; TR&Ecirc;N</span></span></h3>\r\n<p style="text-align: justify;">\r\n	1.&nbsp;Trong điều kiện c&aacute;c dịch vụ theo tour sẵn s&agrave;ng. Sẽ c&oacute; phụ thu nếu dịch vụ hết hoặc x&aacute;c nhận đăng k&yacute; tour qu&aacute; cận so với ng&agrave;y khởi h&agrave;nh. Thứ tự lịch tr&igrave;nh c&oacute; thể thay đổi t&ugrave;y thuộc lịch tr&igrave;nh bay hoặc điều kiện kh&aacute;ch quan kh&aacute;c nhưng vẫn đảm bảo c&aacute;c tuyến điểm thăm quan.</p>\r\n<p style="text-align: justify;">\r\n	2.&nbsp;Điều kiện để khởi h&agrave;nh: đo&agrave;n c&oacute; &iacute;t nhất 10 kh&aacute;ch người lớn trở l&ecirc;n (trẻ em từ 12 tuổi được t&iacute;nh như người lớn), nếu dưới 10 kh&aacute;ch người lớn th&igrave; tour sẽ dời ng&agrave;y khởi h&agrave;nh hoặc nếu vẫn khởi h&agrave;nh th&igrave; c&oacute; phụ thu chi ph&iacute; tổ chức t&ugrave;y theo số lượng đo&agrave;n c&oacute; t&iacute;nh đến trước khởi h&agrave;nh 10 ng&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	3.&nbsp;Thời gian đăng k&yacute; trước ng&agrave;y khởi h&agrave;nh:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		4-5 tuần với c&aacute;c kh&aacute;ch Miền Trung// Miền Nam (Nộp visa tại HCM)</li>\r\n	<li style="text-align: justify;">\r\n		3-5 tuần với c&aacute;c kh&aacute;ch Miền Bắc (Nộp visa tại H&agrave; Nội)</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	4.&nbsp;Điều khoản thanh to&aacute;n:&nbsp;đầy đủ tiền tour theo quy định như sau:</p>\r\n<ol style="text-align: justify;">\r\n</ol>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Lần 1: Đặt cọc tour để x&aacute;c nhận dịch vụ: 50% sau khi đăng k&yacute;// hoặc sau khi k&yacute; hợp đồng (Đầy đủ hồ sơ visa v&agrave; kh&ocirc;ng muộn hơn 5 tuần trước chuyến đi để đảm bảo tiến độ l&agrave;m visa, kh&ocirc;ng t&iacute;nh ng&agrave;y lễ, ng&agrave;y nghỉ)</li>\r\n	<li style="text-align: justify;">\r\n		Lần 2: Thanh to&aacute;n to&agrave;n bộ số tiền c&ograve;n lại khi đạt kết quả visa hoặc 30 ng&agrave;y trước khởi h&agrave;nh, t&ugrave;y v&agrave;o điều kiện n&agrave;o đến trước.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	5.&nbsp;Trong trường hợp kh&aacute;ch bị trượt visa:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Ph&iacute; kh&ocirc;ng ho&agrave;n lại l&agrave; tiền visa 4.000.000vnđ/1 kh&aacute;ch nếu kết quả biết trước ng&agrave;y khởi h&agrave;nh 30 ng&agrave;y (Kh&ocirc;ng kể ng&agrave;y lễ v&agrave; ng&agrave;y nghỉ cuối tuần)</li>\r\n	<li style="text-align: justify;">\r\n		Ph&iacute; kh&ocirc;ng ho&agrave;n lại l&agrave; tiền visa 4.000.000vnđ/1 kh&aacute;ch + ph&iacute; ph&ograve;ng 1 đ&ecirc;m 100usd + ph&iacute; ho&agrave;n v&eacute; m&aacute;y bay: 110usd/kh&aacute;ch, nếu kết quả visa biết trong v&ograve;ng 15-29 ng&agrave;y trước ng&agrave;y khởi h&agrave;nh.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	(V&igrave; vậy qu&yacute; kh&aacute;ch vui l&ograve;ng đăng k&yacute; tour sớm để tr&aacute;nh được c&aacute;c rủi ro về t&agrave;i ch&iacute;nh c&oacute; thể xảy ra.)</p>\r\n<p style="text-align: justify;">\r\n	6.&nbsp;Kh&ocirc;ng được ho&agrave;n ph&iacute; cho những dịch vụ kh&ocirc;ng sử dụng.</p>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">CHI TIẾT VỀ DỊCH VỤ ĐƯỢC PHỤC VỤ TRONG CHƯƠNG TR&Igrave;NH</span></span></h3>\r\n<p style="text-align: justify;">\r\n	<strong>LOẠI XE SỬ DỤNG</strong></p>\r\n<div>\r\n	<table border="1" cellpadding="0" cellspacing="0">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						Group size</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						10+1(up)</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						15+1 (up)</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						20+1 (up)</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						25-&gt; 29 +1</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						30-&gt; 34 +1</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						35-&gt; 40 +1</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						Xe phục vụ</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						12-14seat</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						20 &ndash; 24s</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						24-30 s</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						30-35 s</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						35- 45 s</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						40-55 s</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p style="text-align: justify;">\r\n	<strong>HỆ THỐNG KH&Aacute;CH SẠN</strong></p>\r\n<p style="text-align: justify;">\r\n	Tập đo&agrave;n Accor bao gồm hệ thống Ibis// Mercure v&agrave; c&aacute;c kh&aacute;ch sạn chất lượng tương đương kh&aacute;c.</p>\r\n<p style="text-align: justify;">\r\n	<strong>HỆ THỐNG NH&Agrave; H&Agrave;NG</strong></p>\r\n<p style="text-align: justify;">\r\n	Hệ thống nh&agrave; h&agrave;ng: chủ yếu l&agrave; nh&agrave; h&agrave;ng Việt Nam, ngo&agrave;i ra c&oacute; c&aacute;c bữa tại nh&agrave; h&agrave;ng &Yacute;, H&agrave;n Quốc; Th&aacute;i, buffet Crow, Hoa&hellip;hoặc tương đương t&ugrave;y v&agrave;o t&iacute;nh chất từng đo&agrave;n.</p>\r\n', '', '', 'Tour du lịch Úc du xuân tết âm lịch', 'du lịch úc, du lịch tết úc, du lịch úc tết, du lịch australia tết, du xuân tại úc', 'Du lịch úc vào dịp tết là một trải nghiệm vô cùng tuyệt vời. Hãy đăng ký và thử tận hưởng cái tết tại đất nước đáng sống nhất thế giới cùng MixTourist xem sao nhé!', '<ul>\r\n	<li>\r\n		Kh&aacute;ch sạn ti&ecirc;u chuẩn quốc tế 3 sao, 2 kh&aacute;ch/ph&ograve;ng, trường hợp nam/nữ lẻ ngủ ph&ograve;ng ba.</li>\r\n	<li>\r\n		Ăn theo chư&shy;ơng tr&igrave;nh (kh&ocirc;ng gồm đồ uống) 15AUD/người/bữa trưa v&agrave; 20AUD/người/bữa tối.</li>\r\n	<li>\r\n		Vận chuyển bằng xe du lịch ti&ecirc;u chuẩn Quốc tế.</li>\r\n	<li>\r\n		Ph&iacute; tham quan thắng cảnh, v&agrave;o cửa một lần.</li>\r\n	<li>\r\n		Hư&shy;ớng dẫn vi&ecirc;n tiếng Việt ki&ecirc;m l&aacute;i xe tại &Uacute;c.</li>\r\n	<li>\r\n		V&eacute; m&aacute;y bay quốc tế Việt Nam &ndash; &Uacute;c của Vietnamairlines</li>\r\n	<li>\r\n		V&eacute; m&aacute;y bay nội địa: Canberra - Melbourne</li>\r\n	<li>\r\n		Visa nhập cảnh v&agrave;o Australia.</li>\r\n	<li>\r\n		Bảo hiểm du lịch quốc tế mức 30.000USD/1 kh&aacute;ch</li>\r\n	<li>\r\n		Mỗi kh&aacute;ch một chai nước/ ng&agrave;y.</li>\r\n	<li>\r\n		Trưởng đo&agrave;n đi từ Việt Nam suốt tuyến</li>\r\n</ul>\r\n', '<ul>\r\n	<li>\r\n		Chi ph&iacute; ph&ograve;ng đơn, điện thoại, giặt l&agrave;, đi lại ngo&agrave;i giờ&hellip;</li>\r\n	<li>\r\n		H&agrave;nh l&yacute; qu&aacute; cước</li>\r\n	<li>\r\n		C&aacute;c chi ph&iacute; ph&aacute;t sinh kh&ocirc;ng đề cập trong chương tr&igrave;nh.</li>\r\n	<li>\r\n		Tiền Tip cho HDV v&agrave; l&aacute;i xe ở nư&shy;ớc ngo&agrave;i 7 USD/ng&agrave;y/ngư&shy;ời.</li>\r\n	<li>\r\n		Chi ph&iacute; đi lại để lấy dấu v&acirc;n tay (khi nộp hồ sơ visa) tại HN hoặc HCM</li>\r\n</ul>\r\n');
INSERT INTO `tour` (`id`, `tour_quoc_te`, `DanhMuc1Id`, `DanhMuc2Id`, `danhmuc_multi`, `promotion`, `packages`, `name`, `name_url`, `count_down`, `code`, `img`, `price_sales`, `price`, `price_2`, `price_3`, `price_4`, `price_5`, `price_6`, `durations`, `departure`, `destination`, `departure_time`, `vehicle`, `hotel`, `summary`, `highlights`, `schedule`, `price_list`, `content`, `list_img`, `title`, `keyword`, `description`, `inclusion`, `exclusion`) VALUES
(16, 1, 8, 46, '', 1, 1, 'Tour du lịch MELBOURNE - CANBERRA - SYDNEY', 'tour-du-lich-melbourne-canberra-sydney', '', '', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/uc/du-lich-canberra-mixtourist.jpg', '', '64980000', '', '', '', '', '', '8 Ngày 7 Đêm', '1', 'Sydney, Melbourne, Canbera', 'Theo yêu cầu', 'Máy bay, Ô tô', 3, '', '', '<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y&nbsp; 01:&nbsp; H&Agrave; NỘI &ndash; &nbsp;MELBOURNE &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp; (Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Xe v&agrave; HDV<strong>&nbsp;<a href="http://dulichaustralia.com.vn/du-lich-australia.html">tour du lịch Australia</a></strong>&nbsp;của&nbsp;<strong>Mix Tourist</strong>&nbsp;đ&oacute;n qu&yacute; kh&aacute;c tại điểm hẹn đưa đo&agrave;n khởi h&agrave;nh ra&nbsp;s&acirc;n bay Nội B&agrave;i&nbsp;đi&nbsp;Melbourne.&nbsp;Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 02: MELBOURNE &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(Ăn: Trưa, Chiều)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Đo&agrave;n hạ c&aacute;nh tại s&acirc;n bay quốc tế Melbouurne, xe đ&oacute;n qu&yacute; kh&aacute;ch tham quan th&agrave;nh phố với&nbsp;Trung t&acirc;m nghệ thuật,&nbsp;Nh&agrave; thờ Th&aacute;nh Patrick,&nbsp; Đ&agrave;i tưởng niệm,&nbsp;Vườn Flagstaff,&nbsp;khu phố người Hoa,&nbsp;Vườn Fitzoy,&nbsp;Nh&agrave; quốc hội,&nbsp;T&ograve;a thị ch&iacute;nh Melbourne.&nbsp;Ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:&nbsp;</strong>Xe v&agrave; HDV&nbsp;<strong><a href="http://dulichaustralia.com.vn/du-lich-australia.html">tour du lịch ch&acirc;u &uacute;c</a></strong>&nbsp;đưa đo&agrave;n đi tham quan v&agrave; tự do mua sắm tại&nbsp;trung t&acirc;m thương mại Queen Victoria.&nbsp;Ăn tối tại nh&agrave; h&agrave;ng. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn&nbsp;Merucure Hotel Welcome ***</p>\r\n<p style="text-align: justify;">\r\n	( Hoặc tương đương )</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;">NG&Agrave;Y 03: MELBOURNE&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (Ăn: S&aacute;ng, trưa, chiều)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Đo&agrave;n khởi h&agrave;nh đi tham quan&nbsp;Vườn thực vật, B&atilde;i biển St Kilda, tham quan&nbsp;đảo Philip&nbsp;&ndash; Thế giới của c&aacute;c lo&agrave;i chim c&aacute;nh cụt , qu&yacute; kh&aacute;ch sẽ tận mắt chứng kiến h&agrave;ng trăm ch&uacute; chim c&aacute;nh cụt lũ lụt nối đu&ocirc;i nhau trở về khi ho&agrave;ng h&ocirc;n bu&ocirc;ng xuống. Ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong> Qu&yacute; kh&aacute;ch c&oacute; cơ hội đi thử vận may tại s&ograve;ng b&agrave;i lớn nhất của&nbsp;nước &Uacute;c&nbsp;&ndash;&nbsp;Crown Casino. Ăn tối tại nh&agrave; h&agrave;ng. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn&nbsp;Merucure Hotel Welcome***&nbsp;( Hoặc tương đương )</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 04: MELBOURNE - CANBERRA &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (Ăn: S&aacute;ng, trưa, chiều)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Xe v&agrave; HDV đưa đo&agrave;n khởi h&agrave;nh đi&nbsp;Canberra- thủ đ&ocirc; của Australia, Đ&aacute;p chuyến bay&nbsp;QF814&nbsp;l&uacute;c&nbsp;08h25 &ndash; 09h30&nbsp;xe đ&oacute;n đo&agrave;n đi thanm quan th&agrave;nh phố:&nbsp;Nh&agrave; Quốc Hội mới của li&ecirc;n Bang &Uacute;c&nbsp;với kiến tr&uacute;c rất hiện đại,&nbsp;Hồ Burley Griffin&nbsp;với th&aacute;p phun nước cao 147m,&nbsp;Đ&agrave;i tưởng niệm thuyền trưởng Jame Cook&nbsp;v&agrave;&nbsp;vườn hoa anh đ&agrave;o.&nbsp;Ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:&nbsp;</strong>Đo&agrave;n tiếp tục tham quan&nbsp;Phủ thủ tướng&nbsp;v&agrave;&nbsp;khu nh&agrave; ngoại giao,&nbsp;Đ&agrave;i tưởng niệm c&aacute;c cuộc chiến tranh, Đồi Ainslie Lookout, T&ograve;a &aacute;n tối cao&hellip;.&nbsp; Ăn tối tại nh&agrave; h&agrave;ng. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn&nbsp;Rydges Lakeside Canberra ***&nbsp;( Hoặc tương đương )</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 05: CANBERRA - SYDNEY&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; (Ăn: S&aacute;ng, trưa, chiều)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:&nbsp;</strong>Xe v&agrave; HDV đưa đo&agrave;n đi tham qua&nbsp;th&agrave;nh phố Sydney trong<strong>&nbsp;<a href="http://dulichaustralia.com.vn/du-lich-australia.html">tour &Uacute;c</a>&nbsp;</strong>(c&aacute;ch thủ đ&ocirc; 300km). Tr&ecirc;n đường đi đo&agrave;n gh&eacute; thăm&nbsp;B&atilde;i biển Bondi,&nbsp;Biển Manly, Vịnh Double, Vịnh Hoa Hồng.&nbsp;Ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong>&nbsp;Qu&yacute; kh&aacute;ch tham quan khu phố&nbsp;King Cross.&nbsp;Ăn tối tại nh&agrave; h&agrave;ng trong th&agrave;nh phố. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn&nbsp;Metro&nbsp;Hotel Sydney&nbsp;Central ***( Hoặc tương đương )</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 06: SYDNEY&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (Ăn: S&aacute;ng, trưa, chiều)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Đo&agrave;n tham quan&nbsp;th&agrave;nh phố Sydney&nbsp;lớn nhất nước &Uacute;c với những danh lam thắng cảnh nối tiếng như:&nbsp;Nh&agrave; h&aacute;t kịch Opera con S&ograve;&nbsp;&ndash; biểu tượng của th&agrave;nh phố Sydney với to&agrave;n nước &Uacute;c,&nbsp;Cầu cảng Sydney&nbsp;&ndash; Sydney Harbor Bridge. Ăn trưa tr&ecirc;n du thuyền&nbsp; qu&yacute; kh&aacute;ch c&oacute; cơ hội ngắm phong cảnh ngoạn mục&nbsp;Cảng Sydney, thủy cung, khu Mũi đ&aacute; lịch sử, nh&agrave; thờ th&aacute;nh Mary, T&ograve;a nh&agrave; Nữ ho&agrave;ng Victoria, Th&aacute;p Sydney&nbsp;(tham quan b&ecirc;n ngo&agrave;i).</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong>&nbsp;Qu&yacute; kh&aacute;ch tự do mua sắm tại trung t&acirc;m thương mại&nbsp;City&nbsp;Market, Paddington Market.&nbsp;Ăn tối tại nh&agrave; h&agrave;ng. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn&nbsp;Metro&nbsp;Hotel Sydney&nbsp;Central ***&nbsp;( Hoặc tương đương )</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 07: SYDNEY &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(Ăn: S&aacute;ng, trưa, chiều)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Xe đưa qu&yacute; kh&aacute;ch ra ngoại &ocirc; tham quan di sản thi&ecirc;n nhi&ecirc;n thế giới&nbsp;Blue Mountain&nbsp;&ndash; Chi&ecirc;m &nbsp;ngưỡng cảnh đẹp tự nhi&ecirc;n của N&uacute;i đ&aacute; Ba chị em huyền thoại. Ăn trưa tại nh&agrave; h&agrave;ng địa phương.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều:</strong>&nbsp;Xe v&agrave; HDV đưa qu&yacute; kh&aacute;ch tham&nbsp;Khu C&ocirc;ng vi&ecirc;n Quốc gia, nơi nu&ocirc;i dưỡng v&agrave; bảo tồn c&aacute;c lo&agrave;i động vật đặc trưng như Gấu tr&uacute;c Koala, Đ&agrave; điểu &Uacute;c v&agrave; chuột t&uacute;i Kangaroo. Ăn tối tại nh&agrave; h&agrave;ng. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn&nbsp;Metro&nbsp;Hotel Sydney&nbsp;Central ***&nbsp;( Hoặc tương đương )</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 08: SYDNEY &ndash; H&Agrave; NỘI&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (Ăn: S&aacute;ng)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Sau bữa s&aacute;ng đo&agrave;n l&agrave;m thủ tục trả ph&ograve;ng ra s&acirc;n bay về&nbsp;H&agrave; Nội&nbsp;( Qu&aacute; cảnh S&agrave;i G&ograve;n). Đo&agrave;n đ&aacute;p chuyến bay&nbsp;VN 782&nbsp;l&uacute;c&nbsp;10h15. Về tới s&acirc;n bay&nbsp;Nội B&agrave;i&nbsp;l&uacute;c&nbsp;20h00. Xe đ&oacute;n đo&agrave;n về nội th&agrave;nh, chia tay kết th&uacute;c chương tr&igrave;nh</p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">GI&Aacute; TRỌN G&Oacute;I: 64.980.000 VND/ KH&Aacute;CH</span></span></h2>\r\n<p style="text-align: center;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">( &Aacute;p dụng cho đo&agrave;n từ 16 kh&aacute;ch trở l&ecirc;n )</span></span></strong></p>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">LƯU &Yacute;</span></span></h3>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Gi&aacute; &aacute;p dụng cho đo&agrave;n khởi h&agrave;nh trong th&aacute;ng</li>\r\n	<li style="text-align: justify;">\r\n		Gi&aacute; t&iacute;nh cho trẻ em ngủ chung giường với bố mẹ.</li>\r\n	<li style="text-align: justify;">\r\n		Gi&aacute; c&oacute; thể thay đổi khi h&agrave;ng kh&ocirc;ng tăng phụ thu nhi&ecirc;n liệu</li>\r\n	<li style="text-align: justify;">\r\n		Chương tr&igrave;nh v&agrave; giờ bay c&oacute; thể thay đổi tuỳ theo ng&agrave;y khởi h&agrave;nh cụ thể</li>\r\n</ul>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">THỦ TỤC ĐĂNG K&Yacute; ( XIN VISA )</span></span></h3>\r\n<p style="text-align: justify;">\r\n	&ndash; Yều cầu to&agrave;n bộ giấy tờ bản gốc.</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Hộ chiếu c&ograve;n hạn sử dụng 6 th&aacute;ng với 05 ảnh 4 cm x6 cm chụp tr&ecirc;n nền ph&ocirc;ng trắng (c&oacute; chữ k&yacute; đương đơn trong hộ chiếu)</li>\r\n	<li style="text-align: justify;">\r\n		Đơn xin visa, điền đầy đủ th&ocirc;ng tin v&agrave; do ch&iacute;nh người xin k&yacute; t&ecirc;n</li>\r\n	<li style="text-align: justify;">\r\n		Sơ yếu l&yacute; lịch c&oacute; dấu của phường hoặc cơ quan x&aacute;c nhận</li>\r\n	<li style="text-align: justify;">\r\n		Sổ hộ khẩu</li>\r\n	<li style="text-align: justify;">\r\n		Đăng k&yacute; kết h&ocirc;n</li>\r\n	<li style="text-align: justify;">\r\n		Bản sao c&ocirc;ng chứng giấy khai sinh, nếu mất giấy khai sinh bắt buộc phải c&oacute; x&aacute;c nhận của ủy ban nh&acirc;n d&acirc;n nơi thường tr&uacute; về ng&agrave;y th&aacute;ng năm sinh</li>\r\n	<li style="text-align: justify;">\r\n		Sổ tiết kiệm trị gi&aacute; tối thiểu 5000 USD</li>\r\n	<li style="text-align: justify;">\r\n		Sổ đỏ nh&agrave; đất hoặc t&agrave;i sản kh&aacute;c (nếu c&oacute;)</li>\r\n	<li style="text-align: justify;">\r\n		Nếu đang đi l&agrave;m (Hợp đồng lao động hoặc quyết định bổ nhiệm k&egrave;m theo đơn xin nghỉ ph&eacute;p) Nếu qu&yacute; kh&aacute;ch đ&atilde; về hưu xin vui l&ograve;ng cung cấp thẻ hưu tr&iacute; hoặc quyết định nghỉ hưu</li>\r\n	<li style="text-align: justify;">\r\n		Đặt cọc tối thiểu 1000 USD/kh&aacute;ch</li>\r\n	<li style="text-align: justify;">\r\n		Thanh to&aacute;n hết to&agrave;n bộ số tiền trước 6 ng&agrave;y khởi h&agrave;nh</li>\r\n	<li style="text-align: justify;">\r\n		Lệ ph&iacute; kh&ocirc;ng ho&agrave;n lại:&nbsp; Ph&iacute; l&agrave;m visa trong trường hợp Đại sứ qu&aacute;n từ chối cấp visa cho Qu&yacute; kh&aacute;ch , chi ph&iacute; dịch thuật hồ sơ, ph&iacute; đặt v&eacute; m&aacute;y bay, ph&iacute; ph&ograve;ng kh&aacute;ch sạn&hellip;..</li>\r\n	<li style="text-align: justify;">\r\n		Mọi giấy tờ gốc của Qu&yacute; kh&aacute;ch sẽ được ho&agrave;n lại nguy&ecirc;n vẹn ngay sau khi Qu&yacute; kh&aacute;ch trở về Việt Nam v&agrave; l&ecirc;n tr&igrave;nh diện cơ quan hữu quan đ&agrave;y đủ</li>\r\n	<li style="text-align: justify;">\r\n		Thời gian nộp hồ sơ: 1 th&aacute;ng trước ng&agrave;y khởi h&agrave;nh</li>\r\n	<li style="text-align: justify;">\r\n		Thời gian khởi h&agrave;nh căn cứ v&agrave;o ng&agrave;y được cấp visa của đo&agrave;n.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	<em>Giấy tờ bổ sung cho một số trường hợp:</em></p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Du kh&aacute;ch dưới 18 tuổi cần nộp thư chấp thuận của cả bố v&agrave; mẹ của người đ&oacute; cho ph&eacute;p người đ&oacute; đi. Chữ k&yacute; của bố/mẹ trong thư chấp thuận phải được cơ quan c&oacute; thẩm quyền x&aacute;c nhận</li>\r\n</ul>\r\n', '', '', 'Tour du lịch MELBOURNE - CANBERRA - SYDNEY', 'du lịch sydney, du lịch melbourne, du lịch canberra, du lịch úc, du lịch australia', 'Tour du lịch MELBOURNE - CANBERRA - SYDNEY của Mix Tourist sẽ đưa du khách đi tham quan và khám phá hàng trăm cảnh đẹp và địa danh nổi tiếng tại Australia', '<ul>\r\n	<li>\r\n		Kh&aacute;ch sạn theo ti&ecirc;u chuẩn quốc tế 3* (Ph&ograve;ng đ&ocirc;i, nếu kh&aacute;ch lẻ nam hoặc nữ sẽ ở ph&ograve;ng 3 người)</li>\r\n	<li>\r\n		C&aacute;c bữa ăn theo chương tr&igrave;nh</li>\r\n	<li>\r\n		Xe m&aacute;y lạnh đời mới : trong v&agrave; ngo&agrave;i nước</li>\r\n	<li>\r\n		V&eacute; m&aacute;y bay quốc tế khứ hồi</li>\r\n	<li>\r\n		Thuế s&acirc;n bay quốc tế hai nước v&agrave; lệ ph&iacute; an ninh H&agrave;ng kh&ocirc;ng + phụ ph&iacute; xăng dầu</li>\r\n	<li>\r\n		Lệ ph&iacute; Visa nhập cảnh Australia</li>\r\n	<li>\r\n		Hướng dẫn vi&ecirc;n tiếng Việt suốt tuyến</li>\r\n	<li>\r\n		V&eacute; thắng cảnh v&agrave;o cửa lần 1 tại c&aacute;c điểm trong chương tr&igrave;nh</li>\r\n	<li>\r\n		Bảo hiểm du lịch.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>\r\n		Chi ph&iacute; ph&ograve;ng đơn</li>\r\n	<li>\r\n		Ph&iacute; dịch thuật hồ sơ tr&igrave;nh DSQ xin cấp visa</li>\r\n	<li>\r\n		Tiền bồi dưỡng cho HDV v&agrave; l&aacute;i xe địa phương</li>\r\n</ul>\r\n'),
(17, 1, 7, 49, '48,49', 1, 1, 'Tour du lịch TP.HCM - Mỹ - Canada', 'tour-du-lich-tphcm-my-canada', '', '', '/view/admin/Themes/kcfinder/upload/images/tour/travel-cost3-du-l%E1%BB%8Bch-canada-mixtourist.jpg', '', '88850000', '', '', '', '', '', '10 Ngày 9 Đêm', '2', 'NewYork, Philadelphia, Washington, Thác Niagara, Toronto, Montreal', 'Theo yêu cầu', 'Máy bay, ô tô', 3, '<p>\r\n	Tour du lịch TP.HCM - Mỹ - Canada sẽ đưa bạn tới những điểm đến đẹp nhất tại Mỹ v&agrave; Canada</p>\r\n', '<p>\r\n	Tour du lịch TP.HCM - Mỹ - Canada sẽ đưa bạn tới những điểm đến đẹp nhất tại Mỹ v&agrave; Canada</p>\r\n<ul>\r\n	<li>\r\n		New York</li>\r\n	<li>\r\n		Philadelphia</li>\r\n	<li>\r\n		Thủ đ&ocirc; Washington D.C</li>\r\n	<li>\r\n		Th&aacute;c Niagara</li>\r\n	<li>\r\n		Toronto</li>\r\n	<li>\r\n		Ottawa</li>\r\n	<li>\r\n		Montreal</li>\r\n</ul>\r\n', '<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 01: TP.HCM&nbsp; - NEW YORK &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;( Nghỉ đ&ecirc;m m&aacute;y bay )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Xe v&agrave; HDV của&nbsp;Mix Tourist&nbsp;đ&oacute;n Qu&yacute; kh&aacute;ch tại điểm hẹn, sau đ&oacute; khởi h&agrave;nh đi s&acirc;n bay T&acirc;n Sơn Nhất, đ&aacute;p chuyến bay&nbsp; đi New York. Qu&yacute; kh&aacute;ch nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 02: NEW YORK &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;( Ăn trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Đến s&acirc;n bay quốc tế JFK, Qu&yacute; kh&aacute;ch l&agrave;m thủ tục nhập cảnh Hoa Kỳ. Xe v&agrave; HDV địa phương đ&oacute;n Đo&agrave;n, đưa đi ăn trưa.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch sẽ đi tham quan th&agrave;nh phố&nbsp; New York- Th&agrave;nh phố lớn nhất nước Mỹ&nbsp; với c&aacute;c địa danh như: T&ograve;a nh&agrave; cao nhất New York , Đại lộ số 5 &ndash; con đường nổi tiếng nhất New York,&nbsp; Quảng trường Thời Đại , Trung t&acirc;m thương mại Rockerfeller .</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại New York</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 03: NEW YORK &ndash; PHILADELPHIA&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:&nbsp;</strong>Ăn s&aacute;ng, Qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u tham quan Tượng Nữ Thần Tự Do&ndash; một trong những biểu tượng nổi tiếng nhất của Hoa Kỳ, được Ph&aacute;p ch&iacute;nh thức giao tặng Mỹ v&agrave;o ng&agrave;y 4/7/1884, đồng thời được c&ocirc;ng nhận l&agrave; di sản thế giới UNESCO.</p>\r\n<p style="text-align: justify;">\r\n	Xe đưa Qu&yacute; kh&aacute;ch tiếp tục tham quan Th&agrave;nh phố New York với c&aacute;c địa danh kh&aacute;c như Phố Wall&ndash; Trung t&acirc;m Giao dịch Chứng kho&aacute;n nổi tiếng của Mỹ, China Town, b&ecirc;n ngo&agrave;i Trụ sở Li&ecirc;n hiệp quốc , Bảo t&agrave;ng S&aacute;p Madame Tussaud - l&agrave; t&ograve;a nh&agrave; lớn&nbsp; trưng b&agrave;y gần 200 tượng s&aacute;p của c&aacute;c danh nh&acirc;n nổi&nbsp; tiếng thế giới như Tổng thống Brush, C&ocirc;ng nương Diana&hellip;.</p>\r\n<p style="text-align: justify;">\r\n	Cuối ng&agrave;y, xe đưa Qu&yacute; kh&aacute;ch đến với Th&agrave;nh phố Philadelphia. Nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi. Nghỉ đ&ecirc;m tại Philadelphia.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 04: PHILADELPHIA &ndash; WASHINGTON D.C&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng</strong>:&nbsp;Ăn s&aacute;ng. Tham quan Th&agrave;nh phố Philadelphia. Tại đ&acirc;y, Qu&yacute; kh&aacute;ch sẽ dạo bước tr&ecirc;n Quảng Trường Độc Lập của Mỹ v&agrave; chụp h&igrave;nh b&ecirc;n Quả chu&ocirc;ng Tự do -nơi đ&aacute;nh tiếng chu&ocirc;ng đầu ti&ecirc;n b&aacute;o hiệu sự tự do của nước Mỹ. Tham quan Xưởng sản xuất tiền đồng của Hoa Kỳ .</p>\r\n<p style="text-align: justify;">\r\n	Xe đưa Qu&yacute; kh&aacute;ch tiếp tục đến với Thủ đ&ocirc; Washington D.C, tham quan T&ograve;a nh&agrave; Quốc Hội v&agrave; Nh&agrave; Trắng - nơi ở v&agrave; l&agrave;m việc của Tổng Thống Mỹ, chụp h&igrave;nh b&ecirc;n Tượng Washington, Nh&agrave; Tưởng Niệm Tổng Thống Lincoln , Nh&agrave; Tưởng Niệm Tổng Thống Jefferson, Bảo t&agrave;ng Tự Nhi&ecirc;n để chi&ecirc;m ngưỡng vi&ecirc;n kim cương xanh lớn nhất thế giới. Nghỉ đ&ecirc;m tại Washington D.C</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 05: HERSHEY WORLD- CORNING GLASS &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng</strong>:&nbsp;Ăn s&aacute;ng. Tiếp tục cuộc h&agrave;nh tr&igrave;nh ngược l&ecirc;n ph&iacute;a Bắc nước Mỹ đến Bang Pennsylvania, tham quan nh&agrave; m&aacute;y sản xuất s&ocirc;c&ocirc;la nổi tiếng: Hershey Chocolate World ở Harrisburg. Qu&yacute; kh&aacute;ch sẽ được thưởng thức s&ocirc;c&ocirc;la miễn ph&iacute; với đủ c&aacute;c hương vị kh&aacute;c nhau.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Trưa</strong>: Tiếp tục đi tham quan Trung t&acirc;m thủy tinh Corning Glass , ngắm nh&igrave;n những kiệt t&aacute;c thủy tinh đầy m&agrave;u sắc, t&igrave;m hiểu về những dụng cụ, m&aacute;y m&oacute;c chuy&ecirc;n biệt v&agrave; c&aacute;ch chế t&aacute;c thủy tinh.Tiếp tục h&agrave;nh tr&igrave;nh, xe đưa Qu&yacute; kh&aacute;ch đến với Th&aacute;c Niagara. Qu&yacute; kh&aacute;ch tự do chụp ảnh b&ecirc;n một trong những th&aacute;c nước h&ugrave;ng vĩ nhất thế giới về đ&ecirc;m.</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại th&aacute;c Niagara.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 06: TH&Aacute;C NIAGARA &ndash; TORONTO &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp; &nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng</strong>:&nbsp;Ăn s&aacute;ng. Đến với Th&aacute;c Niagara, Qu&yacute; kh&aacute;ch c&oacute; cơ hội nh&igrave;n ngắm những thắng cảnh tuyệt mỹ của Ngọn Th&aacute;c Niagara nổi tiếng với c&aacute;c nh&oacute;m th&aacute;c như : Th&aacute;c Dấu Ch&acirc;n Ngựa, Th&aacute;c Dải Khăn C&ocirc; D&acirc;u, Th&aacute;c Người Mỹ&hellip; Qu&yacute; kh&aacute;ch l&ecirc;n du thuyền &ldquo;Maid of the Mist&rdquo; tham quan th&aacute;c Niagara. Tận hưởng cảm gi&aacute;c hồi hộp cực độ khi t&agrave;u đương đầu trực tiếp với ngọn th&aacute;c vĩ đại n&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	Từ đ&acirc;y du kh&aacute;ch c&oacute; thể cảm nhận to&agrave;n bộ sự vĩ đại của khối nước khổng lồ đổ từ độ cao 176 feet l&agrave;m th&agrave;nh những l&agrave;n sương m&ugrave; nước v&agrave; bảy sắc cầu vồng tạo n&ecirc;n những cảm x&uacute;c kh&oacute; tả ( chương tr&igrave;nh chỉ thực hiện trong m&ugrave;a h&egrave;), xem phim tư liệu về th&aacute;c tại Khu Imax Movies, c&oacute; dịp chi&ecirc;m ngưỡng ngọn Th&aacute;p Skylon đặt cạnh gần th&aacute;c Niagara.</p>\r\n<p style="text-align: justify;">\r\n	Xe đưa Qu&yacute; kh&aacute;ch rời th&aacute;c Niagara để đến với Th&agrave;nh phố Toronto &ndash; một th&agrave;nh phố sầm uất v&agrave; tấp nập với những bờ biển uốn lượn ngoạn mục v&agrave; những cảnh quan tự nhi&ecirc;n c&oacute; một kh&ocirc;ng hai tr&ecirc;n thế giới. Qu&yacute; kh&aacute;ch sẽ được tham quan Th&aacute;p CN cao 553,33 m&eacute;t - l&agrave; ngọn th&aacute;p cao nhất thế giới hiện nay, đồng thời l&agrave; biểu tượng của th&agrave;nh phố Toronto.</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại Toronto.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 07: TORONTO &ndash; OTTAWA &ndash; MONTREAL&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng</strong>:&nbsp;Ăn s&aacute;ng. Qu&yacute; kh&aacute;ch tiếp tục đến với Th&agrave;nh phố Ottawa &ndash; để kh&aacute;m ph&aacute; sự hiện đại v&agrave; năng động của thủ đ&ocirc; Canada. Qu&yacute; kh&aacute;ch sẽ tham quan To&agrave; Nh&agrave; Quốc Hội nơi ở c&ugrave;a c&aacute;c Thủ tướng Ch&iacute;nh phủ, K&ecirc;nh đ&agrave;o Rideau v&agrave; đắm ch&igrave;m trong m&ugrave;a lễ hội hoa Tulip tại th&agrave;nh phố xinh đẹp Ottawa.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều</strong>: Tham quan th&agrave;nh phố n&oacute;i tiếng Ph&aacute;p lớn thứ 2 tr&ecirc;n thế giới &ndash; Montreal, nơi Qu&yacute; kh&aacute;ch c&oacute; thể chi&ecirc;m ngưỡng những T&ograve;a Nh&agrave; cao tầng mang đậm kiến tr&uacute;c Ch&acirc;u &Acirc;u, c&aacute;c cửa h&agrave;ng, bảng hiệu bằng tiếng Ph&aacute;p c&oacute; mặt ở khắp nơi.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 08: MONTREAL &ndash; QUEBEC &ndash; MONTREAL &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng</strong>:&nbsp;Ăn s&aacute;ng. Xe đưa đo&agrave;n đi tham quan th&agrave;nh phố Montreal l&agrave; th&agrave;nh phố đ&ocirc;ng d&acirc;n thứ nh&igrave; của Canada. Qu&yacute; kh&aacute;ch c&oacute; thể tham quan khu phố cổ Old Montreal để chi&ecirc;m ngưỡng những kiến tr&uacute;c cổ của Ch&acirc;u &Acirc;u được x&acirc;y dựng v&agrave;o thập kỷ 17; C&ocirc;ng Vi&ecirc;n Olympic ,ngọn n&uacute;i Mount Royal, Qu&yacute; kh&aacute;ch sẽ được viếng v&agrave; chụp h&igrave;nh tại Nh&agrave; thờ lớn Saint Joseph&rsquo;s Oratory ở tr&ecirc;n ngọn n&uacute;i Mount Royal thật đặc biệt, lớn v&agrave; cao nhất ở Montreal c&oacute; sức chứa hơn 10.000 người; Nh&agrave; thờ Đức B&agrave; với nhiều kiến tr&uacute;c cổ xưa độc đ&aacute;o.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Trưa</strong>: Qu&yacute; kh&aacute;ch tiếp tục đến với th&agrave;nh phố cổ&nbsp; Quebec&nbsp; - được UNESCO c&ocirc;ng nhận l&agrave; Di sản văn h&oacute;a thế giới năm 1985 với những địa điểm du lịch hấp dẫn như: Quảng Trường Th&agrave;nh Phố , Th&aacute;c Montmorency, L&acirc;u đ&agrave;i Frontenac&nbsp; nguy nga mang đậm phong c&aacute;ch Ph&aacute;p.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối:</strong>&nbsp;Qu&yacute; kh&aacute;ch quay lại Montreal, nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 09: MONTREAL &ndash; NEW YORK &ndash; TP.HCM &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Sau khi ăn s&aacute;ng, xe đưa Qu&yacute; kh&aacute;ch rời Montreal trở lại New York. Qu&yacute; kh&aacute;ch ra s&acirc;n bay JFK, đ&aacute;p chuyến bay về&nbsp; lại Việt Nam. Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 10: TP.HCM</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Về đến s&acirc;n bay quốc tế T&acirc;n Sơn Nhất. Xe v&agrave; HDV đưa đo&agrave;n về điểm đ&oacute;n ban đầu, HDV chia tay đo&agrave;n. Kết th&uacute;c chuyến du lịch TP.HCM - Mỹ ( Bờ Đ&ocirc;ng Hoa Kỳ -&nbsp; Canada 10 ng&agrave;y 9 đ&ecirc;m.&nbsp;</p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">GI&Aacute; TOUR TRỌN G&Oacute;I: 88.850.000 VND&nbsp;/ KH&Aacute;CH</span></span></h2>\r\n<p style="text-align: center;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">( Gi&aacute; &aacute;p dụng cho đo&agrave;n từ 15 kh&aacute;ch trở l&ecirc;n )</span></span></strong></p>\r\n', '', '', 'Tour du lịch TP.HCM - Mỹ - Canada', 'du lịch mỹ, du lịch canada, du lịch canada từ mỹ, tour du lịch canada', 'Tour du lịch TP.HCM - Mỹ - Canada sẽ đưa bạn tới những điểm đến đẹp nhất tại Mỹ và Canada', '<ul>\r\n	<li>\r\n		V&eacute; m&aacute;y bay khứ hồi (Quốc Tế ) theo đo&agrave;n.</li>\r\n	<li>\r\n		Thuế s&acirc;n bay 2 nước + thuế an ninh + ph&iacute; xăng dầu</li>\r\n	<li>\r\n		Kh&aacute;ch sạn ti&ecirc;u chuẩn 3* ( 2 kh&aacute;ch/ph&ograve;ng), bố tr&iacute; 3 kh&aacute;ch/ph&ograve;ng</li>\r\n	<li>\r\n		C&aacute;c bữa ăn theo chương tr&igrave;nh.</li>\r\n	<li>\r\n		Xe du lịch đạt chuẩn đưa đ&oacute;n tham quan suốt chương tr&igrave;nh.</li>\r\n	<li>\r\n		V&eacute; tham quan theo chương tr&igrave;nh.</li>\r\n	<li>\r\n		Hướng dẫn vi&ecirc;n địa phương nhiệt t&igrave;nh v&agrave; chuy&ecirc;n nghiệp.</li>\r\n	<li>\r\n		Bảo hiểm du lịch Quốc tế mức bồi thường tối đa 75,000 USD /trường hợp.</li>\r\n	<li>\r\n		Qu&agrave; tặng từ Mix Tourist</li>\r\n</ul>\r\n', '<ul>\r\n	<li>\r\n		Visa Mỹ v&agrave; Canada</li>\r\n	<li>\r\n		C&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c ngo&agrave;i chương tr&igrave;nh, h&agrave;nh l&yacute; qu&aacute; cước.</li>\r\n	<li>\r\n		Phụ thu ph&ograve;ng đơn.</li>\r\n	<li>\r\n		Tip cho hướng dẫn vi&ecirc;n địa phương v&agrave; l&aacute;i xe 07usd/kh&aacute;ch/ng&agrave;y.</li>\r\n</ul>\r\n');
INSERT INTO `tour` (`id`, `tour_quoc_te`, `DanhMuc1Id`, `DanhMuc2Id`, `danhmuc_multi`, `promotion`, `packages`, `name`, `name_url`, `count_down`, `code`, `img`, `price_sales`, `price`, `price_2`, `price_3`, `price_4`, `price_5`, `price_6`, `durations`, `departure`, `destination`, `departure_time`, `vehicle`, `hotel`, `summary`, `highlights`, `schedule`, `price_list`, `content`, `list_img`, `title`, `keyword`, `description`, `inclusion`, `exclusion`) VALUES
(18, 1, 8, 46, '', 1, 1, 'Tour du lịch SÀI GÒN - MELBOURNE - SYDNEY', 'tour-du-lich-sai-gon-melbourne-sydney', '', '', '/view/admin/Themes/kcfinder/upload/images/danhmuc_tour/tour-quoc-te/uc/du-lich-sai-gon-melbourne-sydney-mixtourist.jpg', '', '57900000', '', '', '', '', '', '7 Ngày 6 Đêm', '', 'Sydney, Melbourne', '20/03, 28/04, 28/05, 28/06, 28/07', 'Máy bay, Ô tô', 3, '', '', '<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 01: H&Agrave;NỘI/ Đ&Agrave; NẴNG/ HCM &ndash; MELBOURNE &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (ĂN TỐI TR&Ecirc;N MB)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch tập trung tại điểm hẹn trong th&agrave;nh phố H&agrave; Nội, xe v&agrave; trưởng đo&agrave;n của c&ocirc;ng ty đ&oacute;n Qu&yacute; kh&aacute;ch đưa ra s&acirc;n bay l&agrave;m thủ tục đ&aacute;p chuyến bay khởi h&agrave;nh đi Melbourne, đo&agrave;n transit tại TP.Hồ Ch&iacute; Minh.</p>\r\n<p style="text-align: justify;">\r\n	Kh&aacute;ch h&agrave;ng ở Đ&agrave; Nẵng v&agrave; HCM tự t&uacute;c ra s&acirc;n bay.</p>\r\n<p style="text-align: justify;">\r\n	Sau đ&oacute;,&nbsp;đo&agrave;n tiếp tục khởi h&agrave;nh đến Melbourne v&agrave; nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 02:&nbsp; MELBOURNE - CITY TOUR &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (ĂN S&Aacute;NG TR&Ecirc;N MB, TRƯA, TỐI)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng: </strong>Đến s&acirc;n bay Melbourne, xe đ&oacute;n đo&agrave;n bắt đầu một h&agrave;nh tr&igrave;nh &nbsp;kh&aacute;m ph&aacute; th&agrave;nh phố Melbourne với những cảnh đẹp như:&nbsp;Phố Swanston; Nh&agrave; thờ lớn tr&ecirc;n đường Patrick; Treasure House, Fitzroy Garden&hellip;</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng, sau đ&oacute; tiếp tục thăm quan&nbsp;Trung t&acirc;m t&agrave;i ch&iacute;nh tr&ecirc;n đường Collins Street, Parliament House, Trung t&acirc;m mỹ thuật Arts Precincts, Quảng trường li&ecirc;n bang Federation Square.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối: </strong>Qu&yacute; kh&aacute;ch&nbsp;tự do kh&aacute;m ph&aacute; v&agrave; thăm quan&nbsp;Southbank &ndash; đ&ocirc; thị mới của Melbourne.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn tối v&agrave; nghỉ đ&ecirc;m ở kh&aacute;ch sạn tại Melbourne</p>\r\n<p style="text-align: justify;">\r\n	(Xe phục vụ trong ng&agrave;y 10 giờ, nếu ph&aacute;t sinh th&ecirc;m giờ th&igrave; b&ecirc;n kh&aacute;ch h&agrave;ng sẽ chịu 100Aud/1 giờ)</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 03: MELBOURNE &ndash; HEALESVILLE SANCTUARY &ndash; FARM &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:&nbsp;</strong>Sau bữa s&aacute;ng, xe đ&oacute;n đo&agrave;n đi sở th&uacute; hoang d&atilde;&nbsp;Healesville Sanctuary. Nơi đ&acirc;y c&aacute;c bạn sẽ c&oacute; cơ hội được ngắm nh&igrave;n v&agrave; chụp h&igrave;nh lưu niệm với c&aacute;c con th&uacute; v&ocirc; c&ugrave;ng đ&aacute;ng y&ecirc;u v&agrave; l&agrave; c&aacute;c lo&agrave;i th&uacute; đặc trưng của nước &Uacute;c như: những ch&uacute; chuột t&uacute;i&nbsp;Kangaroo&nbsp;&ndash; biểu tượng của nước &Uacute;c, ch&uacute; gấu&nbsp;Koala&nbsp;dễ thương v&agrave; tham ngủ, th&uacute; mỏ vịt -&nbsp;Platypus&nbsp;(m&agrave; c&aacute;c bạn hay gặp trong phim hoạt h&igrave;nh Phineas and Ferb); những con&nbsp;Đ&agrave; Điểu; con Wombats; con Dingoes&nbsp;v&agrave; nhiều con động vật đ&aacute;ng y&ecirc;u kh&aacute;c.</p>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>đo&agrave;n tiếp tục thăm quan v&agrave; h&aacute;i tr&aacute;i c&acirc;y tại&nbsp;n&ocirc;ng trại của &Uacute;c, Qu&yacute; kh&aacute;ch thả sức h&aacute;i c&aacute;c tr&aacute;i ch&iacute;n thơm căng mọng tại vườn v&agrave; thưởng thức&nbsp;(tr&aacute;i c&acirc;y t&ugrave;y m&ugrave;a: tr&aacute;i mận; cherry; t&aacute;o; d&acirc;u; đ&agrave;o...).</p>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n tiếp tục thăm quan tại xưởng sản xuất&nbsp;Chocolate&nbsp;trước khi trở lại Melbourne.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối:&nbsp;</strong>Đo&agrave;n ăn tối v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<p style="text-align: justify;">\r\n	(Xe phục vụ trong ng&agrave;y 10 giờ, nếu ph&aacute;t sinh th&ecirc;m giờ th&igrave; b&ecirc;n kh&aacute;ch h&agrave;ng sẽ chịu 100Aud/1 giờ)</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 04: MELBOURNE &ndash; GRAND PARK &ndash; DANDENONG - DFO &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng: </strong>Sau bữa s&aacute;ng, xe đưa Qu&yacute; kh&aacute;ch đi ra v&ugrave;ng ngoại &ocirc; Belgrave, thăm quan:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		C&ocirc;ng vi&ecirc;n Quốc Gia Dandenong. Qu&yacute; kh&aacute;ch dừng ch&acirc;n ngắm cảnh rừng nguy&ecirc;n sinh với c&aacute;c c&acirc;y gỗ lớn, ngắm những ch&uacute; chim với bộ l&ocirc;ng đỏ ch&oacute;t hay nhưng ch&uacute; vẹt trắng từ thi&ecirc;n nhi&ecirc;n hội tụ về, Qu&yacute; kh&aacute;ch cho chim v&agrave; vẹt ăn v&agrave; chụp h&igrave;nh lưu niệm. Qu&yacute; kh&aacute;ch c&oacute; thể tản bộ ngắm cảnh đẹp của rừng nguy&ecirc;n sinh với những c&acirc;y gỗ lớn v&agrave;i trăm năm hay ngắm nh&igrave;n khung cảnh đẹp hoang sơ nơi đ&acirc;y.</li>\r\n	<li style="text-align: justify;">\r\n		Qu&yacute; kh&aacute;ch trải nghiệm tr&ecirc;n t&agrave;u lửa hơi nước&nbsp;Puffing Billy&nbsp;xuy&ecirc;n qua c&aacute;nh rừng nguy&ecirc;n sinh khu vực&nbsp;Dandenong, thưởng thức vẻ đẹp h&ugrave;ng vĩ của rừng v&agrave; cảm gi&aacute;c tuyệt vời tr&ecirc;n con t&agrave;u lửa hơi nước của thế kỷ thứ 19.</li>\r\n	<li style="text-align: justify;">\r\n		Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>Qu&yacute; kh&aacute;ch trở lại Melbourne đi mua sắm tại khu&nbsp;DFO&nbsp;&ndash; nơi b&aacute;n đồ hiệu gi&aacute; rẻ, được b&aacute;n bởi c&aacute;c c&ocirc;ng ty lớn của &Uacute;C. Nếu gặp may, Qu&yacute; kh&aacute;ch c&oacute; thể mua được những h&agrave;ng hiệu nổi tiếng của &Uacute;c với gi&aacute; giảm 50% hoặc 75% .</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối: </strong>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối v&agrave; tự do kh&aacute;m ph&aacute; th&agrave;nh phố Melbourne về đ&ecirc;m.</p>\r\n<p style="text-align: justify;">\r\n	(Xe phục vụ trong ng&agrave;y 10 giờ, nếu ph&aacute;t sinh th&ecirc;m giờ th&igrave; b&ecirc;n kh&aacute;ch h&agrave;ng sẽ chịu 100Aud/1 giờ)</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 05:&nbsp; MELBOURNE- SYDNEY &ndash; CITY TOUR &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng: </strong>Sau bữa s&aacute;ng, Xe đưa đo&agrave;n ra s&acirc;n bay đ&aacute;p chuyến bay đi Sydney.</p>\r\n<p style="text-align: justify;">\r\n	Đến Sydney, bắt đầu một h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; th&agrave;nh phố Sydney với những cảnh đẹp như:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Nh&agrave; h&aacute;t Sydney Opera House &ndash; biểu tượng của nước &Uacute;c&nbsp;c&oacute; kiến tr&uacute;c cực kỳ ấn tượng v&agrave; nổi tiếng của Sydney với ba mặt hướng ra biển.</li>\r\n	<li style="text-align: justify;">\r\n		Darling Habour:&nbsp;một cảng biển nhộn nhịp nơi c&oacute; nhiều qu&aacute;n c&agrave; ph&ecirc; l&atilde;ng mạn ngo&agrave;i trời&nbsp;nổi tiếng ở Sydney.</li>\r\n	<li style="text-align: justify;">\r\n		Đo&agrave;n tiếp tục thăm&nbsp;khu phố cổ The Rocks&nbsp;-&nbsp;thường được v&iacute; như &ldquo;bảo t&agrave;ng Sydney ngo&agrave;i trời&rdquo; bởi nơi n&agrave;y hội tụ cả qu&aacute; khứ v&agrave; hiện tại của th&agrave;nh phố Sydney, l&agrave; nơi những người da trắng đầu ti&ecirc;n đến định cư ở &Uacute;c, v&agrave; n&oacute; được xem l&agrave; cội nguồn h&igrave;nh th&agrave;nh n&ecirc;n th&agrave;nh phố Sydney v&agrave; đất nước &Uacute;c.</li>\r\n	<li style="text-align: justify;">\r\n		Vườn B&aacute;ch Thảo (Botanic Garden):&nbsp;vườn thực vật được mở cửa từ năm 1816 với diện t&iacute;ch 30 hectares.</li>\r\n	<li style="text-align: justify;">\r\n		Mrs. Macquaries Chair&nbsp;để ghi những bức h&igrave;nh đẹp nhất của Cầu cảng&nbsp;Sydney (Sydney Bridge &amp; Habour).</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch ăn trưa tại nh&agrave; h&agrave;ng.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>Qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u&nbsp;Captain Cook, thăm quan cảnh đẹp&nbsp;Vịnh Jackson: ngắm th&agrave;nh phố Sydney tuyệt đẹp từ g&oacute;c nh&igrave;n của biển. Qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng, nhận ph&ograve;ng v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn ở Sydney.</p>\r\n<p style="text-align: justify;">\r\n	(Xe phục vụ trong ng&agrave;y 10 giờ, nếu ph&aacute;t sinh th&ecirc;m giờ th&igrave; b&ecirc;n kh&aacute;ch h&agrave;ng sẽ chịu 100Aud/1 giờ)</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 06: SYDNEY &ndash; ĐI ĐỒI C&Aacute;T &ndash; CẢNG STEPHEN &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(ĂN BA BỮA)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:&nbsp;</strong>Sau khi d&ugrave;ng điểm t&acirc;m s&aacute;ng, Qu&yacute; kh&aacute;ch khởi h&agrave;nh đi cảng biển&nbsp;Port Stephen.</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Đến nơi,&nbsp;Qu&yacute; kh&aacute;ch sẽ được thử cảm gi&aacute;c ngồi tr&ecirc;n xe chuy&ecirc;n dụng 4WD&nbsp;chạy tr&ecirc;n những đồi c&aacute;t v&agrave; thử &ldquo;trượt v&aacute;n kiểu &Uacute;c&rdquo;.</li>\r\n	<li style="text-align: justify;">\r\n		Sau đ&oacute; Qu&yacute; kh&aacute;ch khởi h&agrave;nh l&ecirc;n du thuyền v&agrave; d&ugrave;ng bữa trưa&nbsp;Buffet kiểu T&acirc;y&nbsp;ngay tr&ecirc;n du thuyền.</li>\r\n	<li style="text-align: justify;">\r\n		Du thuyền khởi h&agrave;nh ra khơi vịnh Stephen, du kh&aacute;ch bắt đầu h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n ngắm những đ&agrave;n chim&nbsp;Hải &Acirc;u&nbsp;v&agrave; những ch&uacute;&nbsp;c&aacute; Heo&nbsp;bơi lội giữa biển..chắc chắn sẽ để lại cho du kh&aacute;ch những trải nghiệm kh&oacute; qu&ecirc;n.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>Qu&yacute; kh&aacute;ch l&ecirc;n xe khởi h&agrave;nh về lại&nbsp;Sydney.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối:&nbsp;</strong>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối v&agrave; nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>\r\n<p style="text-align: justify;">\r\n	(Xe phục vụ trong ng&agrave;y 10 giờ, nếu ph&aacute;t sinh th&ecirc;m giờ th&igrave; b&ecirc;n kh&aacute;ch h&agrave;ng sẽ chịu 100Aud/1 giờ)</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 07: SYDNEY &ndash; H&Agrave; NỘI/ Đ&Agrave; NẴNG/ HCM &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(ĂN S&Aacute;NG)</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong> Đo&agrave;n ăn s&aacute;ng tại kh&aacute;ch sạn v&agrave; l&agrave;m thủ tục trả ph&ograve;ng. Xe đưa đo&agrave;n ra s&acirc;n bay Sydney l&agrave;m thủ tục đ&aacute;p chuyến bay về Việt Nam.</p>\r\n<p style="text-align: justify;">\r\n	Ăn trưa tr&ecirc;n m&aacute;y bay. Về đến điểm đ&oacute;n ban đầu. Kết th&uacute;c chương tr&igrave;nh tour.</p>\r\n<p style="text-align: justify;">\r\n	<em>Xin ch&agrave;o tạm biệt v&agrave; hẹn gặp lại Qu&yacute; kh&aacute;ch!</em></p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">GI&Aacute; TRỌN G&Oacute;I / KH&Aacute;CH</span></span></h2>\r\n<div>\r\n	<table border="1" cellpadding="0" cellspacing="0">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						<strong>Số lượng kh&aacute;ch k&egrave;m điều kiện</strong></p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						<strong>Người lớn</strong></p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						<strong>Trẻ em</strong></p>\r\n					<p style="text-align: center;">\r\n						<strong>(2-11 tuổi ngủ ri&ecirc;ng giường)</strong></p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						<strong>Em b&eacute;</strong></p>\r\n					<p style="text-align: center;">\r\n						<strong>dưới 2 tuổi</strong></p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						Từ 10-14 kh&aacute;ch người lớn</p>\r\n					<p style="text-align: center;">\r\n						(đi gh&eacute;p, kh&ocirc;ng c&oacute; trưởng đo&agrave;n từ VN hoặc c&oacute; phụ thu)</p>\r\n				</td>\r\n				<td rowspan="2">\r\n					<p style="text-align: center;">\r\n						57.900.000đ</p>\r\n				</td>\r\n				<td rowspan="2">\r\n					<p style="text-align: center;">\r\n						52.110.000đ</p>\r\n				</td>\r\n				<td rowspan="2">\r\n					<p style="text-align: center;">\r\n						17.370.000đ</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: center;">\r\n						Từ 15 kh&aacute;ch</p>\r\n					<p style="text-align: center;">\r\n						(đi gh&eacute;p c&oacute; trưởng đo&agrave;n từ VN)</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">LƯU &Yacute;</span></span></h3>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		C&aacute;c kh&aacute;ch gh&eacute;p lẻ c&oacute; thể xuất ph&aacute;t từ H&agrave; Nội// Đ&agrave; Nẵng // S&agrave;i G&ograve;n. Trưởng đo&agrave;n từ VN c&oacute; thể xuất ph&aacute;t từ&nbsp; H&agrave; Nội// Đ&agrave; Nẵng // S&agrave;i G&ograve;n</li>\r\n	<li style="text-align: justify;">\r\n		Phụ thu ph&ograve;ng đơn trong trường hợp lẻ nam hoặc nữ: 8.500.000đ/kh&aacute;ch/tour</li>\r\n	<li style="text-align: justify;">\r\n		Em b&eacute; dưới 2 tuổi gi&aacute; bao gồm: Visa, bảo hiểm, V&eacute; m&aacute;y bay, ngủ chung giường với cha mẹ. V&igrave; vậy cha mẹ tự lo c&aacute;c chi ph&iacute; ph&aacute;t sinh v&agrave; ăn uống theo c&aacute;ch ri&ecirc;ng của b&eacute;.</li>\r\n</ul>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">GHI CH&Uacute;:&nbsp;</span></span></h3>\r\n<p style="text-align: justify;">\r\n	C&ocirc;ng ty sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm bảo đảm c&aacute;c điểm tham quan hoặc ho&agrave;n lại chi ph&iacute; tour trong trường hợp:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Xảy ra thi&ecirc;n tai: b&atilde;o lụt, hạn h&aacute;n, động đất&hellip;</li>\r\n	<li style="text-align: justify;">\r\n		Sự cố về an ninh: khủng bố, biểu t&igrave;nh</li>\r\n	<li style="text-align: justify;">\r\n		Sự cố về h&agrave;ng kh&ocirc;ng: trục trặc kỹ thuật, an ninh, dời, hủy, ho&atilde;n chuyến bay.</li>\r\n	<li style="text-align: justify;">\r\n		V&igrave; l&yacute; do kh&aacute;ch quan, hải quan nước sở tại kh&ocirc;ng cho ph&eacute;p nhập cảnh th&igrave; c&ocirc;ng ty du lịch sẽ kh&ocirc;ng ho&agrave;n lại bất kỳ chi ph&iacute; ph&aacute;t sinh v&agrave; ph&iacute; kh&ocirc;ng sử dụng dịch vụ n&agrave;o của Qu&yacute; kh&aacute;ch.</li>\r\n</ul>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">KIỆN &Aacute;P DỤNG B&Aacute;O GI&Aacute; TR&Ecirc;N</span></span></h3>\r\n<p style="text-align: justify;">\r\n	1.&nbsp;Trong điều kiện c&aacute;c dịch vụ theo tour sẵn s&agrave;ng. Sẽ c&oacute; phụ thu nếu dịch vụ hết hoặc x&aacute;c nhận đăng k&yacute; tour qu&aacute; cận so với ng&agrave;y khởi h&agrave;nh. Thứ tự lịch tr&igrave;nh c&oacute; thể thay đổi t&ugrave;y thuộc lịch tr&igrave;nh bay hoặc điều kiện kh&aacute;ch quan kh&aacute;c nhưng vẫn đảm bảo c&aacute;c tuyến điểm thăm quan.</p>\r\n<p style="text-align: justify;">\r\n	2.&nbsp;C&ocirc;ng ty được quyền thay thế c&aacute;c dịch vụ tương đương với c&aacute;c dịch vụ dự kiến trong trường hợp dịch vụ dự kiến kh&ocirc;ng sẵn s&agrave;ng cho đo&agrave;n.</p>\r\n<p style="text-align: justify;">\r\n	3.&nbsp;Điều kiện để khởi h&agrave;nh: đo&agrave;n c&oacute;&nbsp;&iacute;t nhất 15 kh&aacute;ch người lớn&nbsp;trở l&ecirc;n (trẻ em từ 12 tuổi được t&iacute;nh như người lớn), nếu dưới 10 kh&aacute;ch người lớn th&igrave; tour sẽ dời ng&agrave;y khởi h&agrave;nh hoặc nếu vẫn khởi h&agrave;nh th&igrave; c&oacute; phụ thu chi ph&iacute; tổ chức t&ugrave;y theo số lượng đo&agrave;n c&oacute; t&iacute;nh đến trước khởi h&agrave;nh 10 ng&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	4.&nbsp;Điều khoản thanh to&aacute;n:&nbsp;đầy đủ tiền tour theo quy định như sau:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Lần 1: Đặt cọc tour để x&aacute;c nhận dịch vụ: 50% sau khi đăng k&yacute;// hoặc sau khi k&yacute; hợp đồng (Đầy đủ hồ sơ visa v&agrave; kh&ocirc;ng muộn hơn 5 tuần trước chuyến đi để đảm bảo tiến độ l&agrave;m visa, kh&ocirc;ng t&iacute;nh ng&agrave;y lễ, ng&agrave;y nghỉ)</li>\r\n	<li style="text-align: justify;">\r\n		Lần 2: Thanh to&aacute;n to&agrave;n bộ số tiền c&ograve;n lại khi đạt kết quả visa hoặc 20 ng&agrave;y trước khởi h&agrave;nh, t&ugrave;y v&agrave;o điều kiện n&agrave;o đến trước.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	5.&nbsp;Đo&agrave;n hủy tour: (Coi chi tiết theo hợp đồng)</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Sau khi x&aacute;c nhận dịch vụ v&agrave; k&yacute; hợp đồng, Ph&iacute; hủy dịch vụ l&agrave; 30%</li>\r\n	<li style="text-align: justify;">\r\n		Nh&oacute;m hủy bỏ tour trong v&ograve;ng 30 ng&agrave;y, Ph&iacute; hủy tour tương đương 50% tổng gi&aacute; trị tour.</li>\r\n	<li style="text-align: justify;">\r\n		Nh&oacute;m hủy tour trong v&ograve;ng 20 ng&agrave;y, Ph&iacute; hủy tour tương đương 85% tổng gi&aacute; trị tour.</li>\r\n	<li style="text-align: justify;">\r\n		Hủy tour trong v&ograve;ng 15 ng&agrave;y hoặc hủy m&agrave; kh&ocirc;ng th&ocirc;ng b&aacute;o th&igrave; sẽ chịu ph&iacute; 100% gi&aacute; trị tour.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	6.&nbsp;Trong trường hợp kh&aacute;ch bị trượt visa: C&aacute;c khoản ph&iacute; bị phạt t&ugrave;y theo thời điểm ra kết quả visa</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		Nếu kết quả visa biết trước ng&agrave;y khởi h&agrave;nh 20 ng&agrave;y (Kh&ocirc;ng kể ng&agrave;y lễ v&agrave; ng&agrave;y nghỉ cuối tuần) th&igrave; Qu&yacute; kh&aacute;ch chỉ mất ph&iacute; visa 3.500.000vnđ/1 kh&aacute;ch</li>\r\n	<li style="text-align: justify;">\r\n		Nếu kết quả visa biết trong v&ograve;ng 20 ng&agrave;y trước khởi h&agrave;nh (Kh&ocirc;ng kể ng&agrave;y lễ v&agrave; ng&agrave;y nghỉ cuối tuần) th&igrave; Qu&yacute; kh&aacute;ch sẽ mất c&aacute;c khoản ph&iacute; sau: tiền visa 3.500.000vnđ/1 kh&aacute;ch + ph&iacute; ph&ograve;ng 2 đ&ecirc;m 150usd + ph&iacute; ho&agrave;n v&eacute; m&aacute;y bay: 110usd/kh&aacute;ch.</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	(Ch&uacute;ng t&ocirc;i kh&ocirc;ng mong muốn Qu&yacute; kh&aacute;ch bị phạt trong trường hợp bị trượt visa, tuy nhi&ecirc;n ch&uacute;ng t&ocirc;i buộc phải tu&acirc;n thủ điều kiện về dịch vụ do h&atilde;ng h&agrave;ng kh&ocirc;ng v&agrave; đối t&aacute;c cung cấp dịch vụ tại &Uacute;c quy định. V&igrave; vậy Qu&yacute; kh&aacute;ch vui l&ograve;ng đăng k&yacute; tour sớm để tr&aacute;nh được c&aacute;c rủi ro về t&agrave;i ch&iacute;nh c&oacute; thể xảy ra nếu bị trượt visa.)</p>\r\n<p style="text-align: justify;">\r\n	7.&nbsp;Thời gian đăng k&yacute; trước ng&agrave;y khởi h&agrave;nh:</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		4-5 tuần với c&aacute;c kh&aacute;ch Miền Trung// Miền Nam (Nộp visa tại HCM)</li>\r\n	<li style="text-align: justify;">\r\n		3-5 tuần với c&aacute;c kh&aacute;ch Miền Bắc (Nộp visa tại H&agrave; Nội)</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	8.&nbsp;Kh&ocirc;ng được ho&agrave;n ph&iacute; cho những dịch vụ kh&ocirc;ng sử dụng.</p>\r\n<h3 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:14px;">CHI TIẾT VỀ DỊCH VỤ ĐƯỢC PHỤC VỤ TRONG CHƯƠNG TR&Igrave;NH</span></span></h3>\r\n<p style="text-align: justify;">\r\n	<strong>LOẠI XE SỬ DỤNG</strong></p>\r\n<div>\r\n	<table border="1" cellpadding="0" cellspacing="0">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						Group size</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						10+1(up)</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						15+1 (up)</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						20+1 (up)</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						25-&gt; 29 +1</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						30-&gt; 34 +1</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						35-&gt; 40 +1</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						Xe phục vụ</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						12-14seat</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						20 &ndash; 24s</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						24-30 s</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						30-35 s</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						35- 45 s</p>\r\n				</td>\r\n				<td>\r\n					<p style="text-align: justify;">\r\n						40-55 s</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p style="text-align: justify;">\r\n	<strong>HỆ THỐNG KH&Aacute;CH SẠN</strong></p>\r\n<p style="text-align: justify;">\r\n	Tập đo&agrave;n kh&aacute;ch sạn Accor bao gồm hệ thống Ibis// Mercure v&agrave; c&aacute;c kh&aacute;ch sạn chất lượng tương đương kh&aacute;c. Tập đo&agrave;n kh&aacute;ch sạn TFE bao gồm: Rydges; Travelodge; Vibe... v&agrave; c&aacute;c kh&aacute;ch sạn chất lượng tương kh&aacute;c.</p>\r\n<p style="text-align: justify;">\r\n	<strong>HỆ THỐNG NH&Agrave; H&Agrave;NG</strong></p>\r\n<p style="text-align: justify;">\r\n	Hệ thống nh&agrave; h&agrave;ng: chủ yếu l&agrave; nh&agrave; h&agrave;ng Việt Nam, ngo&agrave;i ra c&oacute; c&aacute;c bữa tại nh&agrave; h&agrave;ng &Yacute;, H&agrave;n Quốc; Th&aacute;i, buffet Crow, Hoa&hellip;hoặc tương đương t&ugrave;y v&agrave;o t&iacute;nh chất từng đo&agrave;n.</p>\r\n', '', '', 'Tour du lịch SÀI GÒN - MELBOURNE - SYDNEY', 'du lịch sydney, du lịch melbourne, du lịch úc, du lịch australia', 'Cùng MixTourist khám phá thành phố Melbourne, Sydney và trải nghiệm những điều thú vị và bất ngờ tại đất nước của những chú Kangaru nhé', '<ul>\r\n	<li>\r\n		Kh&aacute;ch sạn ti&ecirc;u chuẩn quốc tế 3 sao, 2 kh&aacute;ch/ph&ograve;ng, trường hợp lẻ nam//nữ ngủ ph&ograve;ng ba hoặc phụ thu ph&ograve;ng đơn với c&aacute;c kh&aacute;ch đi đơn lẻ v&agrave; kh&ocirc;ng thể gh&eacute;p ph&ograve;ng.</li>\r\n	<li>\r\n		Ăn theo chư&shy;ơng tr&igrave;nh (kh&ocirc;ng gồm đồ uống) 15AUD/người/bữa trưa v&agrave; 20AUD/người/bữa tối.</li>\r\n	<li>\r\n		Vận chuyển bằng xe du lịch ti&ecirc;u chuẩn Quốc tế.</li>\r\n	<li>\r\n		Ph&iacute; tham quan thắng cảnh, v&agrave;o cửa một lần.</li>\r\n	<li>\r\n		Hư&shy;ớng dẫn vi&ecirc;n tiếng Việt ki&ecirc;m l&aacute;i xe tại &Uacute;c hoặc hướng dẫn vi&ecirc;n tiếng Việt từ VN.</li>\r\n	<li>\r\n		V&eacute; m&aacute;y bay theo lịch tr&igrave;nh.</li>\r\n	<li>\r\n		Visa nhập cảnh v&agrave;o Australia.</li>\r\n	<li>\r\n		Bảo hiểm du lịch quốc tế mức 30.000USD/1 kh&aacute;ch</li>\r\n	<li>\r\n		Mỗi kh&aacute;ch một chai nước/ ng&agrave;y.</li>\r\n</ul>\r\n', '<ul>\r\n	<li style="text-align: justify;">\r\n		Chi ph&iacute; ph&ograve;ng đơn, điện thoại, giặt l&agrave;, đi lại ngo&agrave;i giờ, h&agrave;nh l&yacute; qu&aacute; cước...</li>\r\n	<li style="text-align: justify;">\r\n		C&aacute;c chi ph&iacute; ph&aacute;t sinh kh&ocirc;ng đề cập trong chương tr&igrave;nh.</li>\r\n	<li style="text-align: justify;">\r\n		Tiền Tip cho HDV v&agrave; l&aacute;i xe ở nư&shy;ớc ngo&agrave;i 7 USD/ng&agrave;y/ngư&shy;ời.</li>\r\n	<li style="text-align: justify;">\r\n		Chi ph&iacute; đi lại để lấy dấu v&acirc;n tay (khi nộp hồ sơ visa) tại HN hoặc HCM</li>\r\n	<li style="text-align: justify;">\r\n		Chi ph&iacute; ph&aacute;t sinh dịch vụ tr&ecirc;n thực tế</li>\r\n</ul>\r\n'),
(19, 1, 7, 49, '', 1, 1, 'Tour du lịch Hà Nội - Mỹ - Canada', 'tour-du-lich-ha-noi-my-canada', '', '', '/view/admin/Themes/kcfinder/upload/images/tour/places12-du-lich-canada-mixtourist.jpg', '', '88850000', '', '', '', '', '', '10 Ngày 9 Đêm', '1', 'NEW YORK, PHILADELPHIA, WASHINGTON, THÁC NIAGARA, TORONTO, MONTREAL', '', 'Máy bay, ô tô', 3, '<p>\r\n	Tour du lịch H&agrave; Nội - Mỹ - Canada sẽ đưa qu&yacute; kh&aacute;ch tới với những điểm đến hấp dẫn nhất tại Canada v&agrave; Mỹ</p>\r\n', '<p>\r\n	Tour du lịch H&agrave; Nội - Mỹ - Canada sẽ đưa qu&yacute; kh&aacute;ch tới với những điểm đến hấp dẫn nhất tại Canada v&agrave; Mỹ</p>\r\n<ul>\r\n	<li>\r\n		New York</li>\r\n	<li>\r\n		Washington D.C</li>\r\n	<li>\r\n		Philadelphia</li>\r\n	<li>\r\n		Toronto</li>\r\n	<li>\r\n		Montreal</li>\r\n</ul>\r\n', '<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 01: H&Agrave; NỘI&nbsp; - NEW YORK &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;( Nghỉ đ&ecirc;m m&aacute;y bay )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Xe v&agrave; HDV của&nbsp;Mix Tourist&nbsp;đ&oacute;n Qu&yacute; kh&aacute;ch tại điểm hẹn, sau đ&oacute; khởi h&agrave;nh đi s&acirc;n bay Nội B&agrave;i, đ&aacute;p chuyến bay&nbsp; đi New York. Qu&yacute; kh&aacute;ch nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 02: NEW YORK &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Đến s&acirc;n bay quốc tế JFK, Qu&yacute; kh&aacute;ch l&agrave;m thủ tục nhập cảnh Hoa Kỳ. Xe v&agrave; HDV địa phương đ&oacute;n Đo&agrave;n, đưa đi ăn trưa.</p>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch sẽ đi tham quan th&agrave;nh phố&nbsp; New York- Th&agrave;nh phố lớn nhất nước Mỹ&nbsp; với c&aacute;c địa danh như: T&ograve;a nh&agrave; cao nhất New York , Đại lộ số 5 &ndash; con đường nổi tiếng nhất New York,&nbsp; Quảng trường Thời Đại , Trung t&acirc;m thương mại Rockerfeller .</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại New York</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 03: NEW YORK &ndash; PHILADELPHIA&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:&nbsp;</strong>Ăn s&aacute;ng, Qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u tham quan Tượng Nữ Thần Tự Do&ndash; một trong những biểu tượng nổi tiếng nhất của Hoa Kỳ, được Ph&aacute;p ch&iacute;nh thức giao tặng Mỹ v&agrave;o ng&agrave;y 4/7/1884, đồng thời được c&ocirc;ng nhận l&agrave; di sản thế giới UNESCO.</p>\r\n<p style="text-align: justify;">\r\n	Xe đưa Qu&yacute; kh&aacute;ch tiếp tục tham quan Th&agrave;nh phố New York với c&aacute;c địa danh kh&aacute;c như Phố Wall&ndash; Trung t&acirc;m Giao dịch Chứng kho&aacute;n nổi tiếng của Mỹ, China Town, b&ecirc;n ngo&agrave;i Trụ sở Li&ecirc;n hiệp quốc , Bảo t&agrave;ng S&aacute;p Madame Tussaud - l&agrave; t&ograve;a nh&agrave; lớn&nbsp; trưng b&agrave;y gần 200 tượng s&aacute;p của c&aacute;c danh nh&acirc;n nổi&nbsp; tiếng thế giới như Tổng thống Brush, C&ocirc;ng nương Diana&hellip;.</p>\r\n<p style="text-align: justify;">\r\n	Cuối ng&agrave;y, xe đưa Qu&yacute; kh&aacute;ch đến với Th&agrave;nh phố Philadelphia. Nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi. Nghỉ đ&ecirc;m tại Philadelphia.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 04: PHILADELPHIA &ndash; WASHINGTON D.C&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Ăn s&aacute;ng. Tham quan Th&agrave;nh phố Philadelphia. Tại đ&acirc;y, Qu&yacute; kh&aacute;ch sẽ dạo bước tr&ecirc;n Quảng Trường Độc Lập của Mỹ v&agrave; chụp h&igrave;nh b&ecirc;n Quả chu&ocirc;ng Tự do -nơi đ&aacute;nh tiếng chu&ocirc;ng đầu ti&ecirc;n b&aacute;o hiệu sự tự do của nước Mỹ. Tham quan Xưởng sản xuất tiền đồng của Hoa Kỳ .</p>\r\n<p style="text-align: justify;">\r\n	Xe đưa Qu&yacute; kh&aacute;ch tiếp tục đến với Thủ đ&ocirc; Washington D.C, tham quan T&ograve;a nh&agrave; Quốc Hội v&agrave; Nh&agrave; Trắng - nơi ở v&agrave; l&agrave;m việc của Tổng Thống Mỹ, chụp h&igrave;nh b&ecirc;n Tượng Washington, Nh&agrave; Tưởng Niệm Tổng Thống Lincoln , Nh&agrave; Tưởng Niệm Tổng Thống Jefferson, Bảo t&agrave;ng Tự Nhi&ecirc;n để chi&ecirc;m ngưỡng vi&ecirc;n kim cương xanh lớn nhất thế giới. Nghỉ đ&ecirc;m tại Washington D.C</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 05: HERSHEY WORLD- CORNING GLASS &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Ăn s&aacute;ng. Tiếp tục cuộc h&agrave;nh tr&igrave;nh ngược l&ecirc;n ph&iacute;a Bắc nước Mỹ đến Bang Pennsylvania, tham quan nh&agrave; m&aacute;y sản xuất s&ocirc;c&ocirc;la nổi tiếng: Hershey Chocolate World ở Harrisburg. Qu&yacute; kh&aacute;ch sẽ được thưởng thức s&ocirc;c&ocirc;la miễn ph&iacute; với đủ c&aacute;c hương vị kh&aacute;c nhau.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Trưa:</strong> Tiếp tục đi tham quan Trung t&acirc;m thủy tinh Corning Glass , ngắm nh&igrave;n những kiệt t&aacute;c thủy tinh đầy m&agrave;u sắc, t&igrave;m hiểu về những dụng cụ, m&aacute;y m&oacute;c chuy&ecirc;n biệt v&agrave; c&aacute;ch chế t&aacute;c thủy tinh.Tiếp tục h&agrave;nh tr&igrave;nh, xe đưa Qu&yacute; kh&aacute;ch đến với Th&aacute;c Niagara. Qu&yacute; kh&aacute;ch tự do chụp ảnh b&ecirc;n một trong những th&aacute;c nước h&ugrave;ng vĩ nhất thế giới về đ&ecirc;m.</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại th&aacute;c Niagara.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 06: TH&Aacute;C NIAGARA &ndash; TORONTO &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Ăn s&aacute;ng. Đến với Th&aacute;c Niagara, Qu&yacute; kh&aacute;ch c&oacute; cơ hội nh&igrave;n ngắm những thắng cảnh tuyệt mỹ của Ngọn Th&aacute;c Niagara nổi tiếng với c&aacute;c nh&oacute;m th&aacute;c như : Th&aacute;c Dấu Ch&acirc;n Ngựa, Th&aacute;c Dải Khăn C&ocirc; D&acirc;u, Th&aacute;c Người Mỹ&hellip; Qu&yacute; kh&aacute;ch l&ecirc;n du thuyền &ldquo;Maid of the Mist&rdquo; tham quan th&aacute;c Niagara. Tận hưởng cảm gi&aacute;c hồi hộp cực độ khi t&agrave;u đương đầu trực tiếp với ngọn th&aacute;c vĩ đại n&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	Từ đ&acirc;y du kh&aacute;ch c&oacute; thể cảm nhận to&agrave;n bộ sự vĩ đại của khối nước khổng lồ đổ từ độ cao 176 feet l&agrave;m th&agrave;nh những l&agrave;n sương m&ugrave; nước v&agrave; bảy sắc cầu vồng tạo n&ecirc;n những cảm x&uacute;c kh&oacute; tả ( chương tr&igrave;nh chỉ thực hiện trong m&ugrave;a h&egrave;), xem phim tư liệu về th&aacute;c tại Khu Imax Movies, c&oacute; dịp chi&ecirc;m ngưỡng ngọn Th&aacute;p Skylon đặt cạnh gần th&aacute;c Niagara.</p>\r\n<p style="text-align: justify;">\r\n	Xe đưa Qu&yacute; kh&aacute;ch rời th&aacute;c Niagara để đến với Th&agrave;nh phố Toronto &ndash; một th&agrave;nh phố sầm uất v&agrave; tấp nập với những bờ biển uốn lượn ngoạn mục v&agrave; những cảnh quan tự nhi&ecirc;n c&oacute; một kh&ocirc;ng hai tr&ecirc;n thế giới. Qu&yacute; kh&aacute;ch sẽ được tham quan Th&aacute;p CN cao 553,33 m&eacute;t - l&agrave; ngọn th&aacute;p cao nhất thế giới hiện nay, đồng thời l&agrave; biểu tượng của th&agrave;nh phố Toronto.</p>\r\n<p style="text-align: justify;">\r\n	Nghỉ đ&ecirc;m tại Toronto.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 07: TORONTO &ndash; OTTAWA &ndash; MONTREAL&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:</strong>&nbsp;Ăn s&aacute;ng. Qu&yacute; kh&aacute;ch tiếp tục đến với Th&agrave;nh phố Ottawa &ndash; để kh&aacute;m ph&aacute; sự hiện đại v&agrave; năng động của thủ đ&ocirc; Canada. Qu&yacute; kh&aacute;ch sẽ tham quan To&agrave; Nh&agrave; Quốc Hội nơi ở c&ugrave;a c&aacute;c Thủ tướng Ch&iacute;nh phủ, K&ecirc;nh đ&agrave;o Rideau v&agrave; đắm ch&igrave;m trong m&ugrave;a lễ hội hoa Tulip tại th&agrave;nh phố xinh đẹp Ottawa.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Chiều: </strong>Tham quan th&agrave;nh phố n&oacute;i tiếng Ph&aacute;p lớn thứ 2 tr&ecirc;n thế giới &ndash; Montreal, nơi Qu&yacute; kh&aacute;ch c&oacute; thể chi&ecirc;m ngưỡng những T&ograve;a Nh&agrave; cao tầng mang đậm kiến tr&uacute;c Ch&acirc;u &Acirc;u, c&aacute;c cửa h&agrave;ng, bảng hiệu bằng tiếng Ph&aacute;p c&oacute; mặt ở khắp nơi.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 08: MONTREAL &ndash; QUEBEC &ndash; MONTREAL &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>S&aacute;ng:&nbsp;</strong>Ăn s&aacute;ng. Xe đưa đo&agrave;n đi tham quan th&agrave;nh phố Montreal l&agrave; th&agrave;nh phố đ&ocirc;ng d&acirc;n thứ nh&igrave; của Canada. Qu&yacute; kh&aacute;ch c&oacute; thể tham quan khu phố cổ Old Montreal để chi&ecirc;m ngưỡng những kiến tr&uacute;c cổ của Ch&acirc;u &Acirc;u được x&acirc;y dựng v&agrave;o thập kỷ 17; C&ocirc;ng Vi&ecirc;n Olympic ,ngọn n&uacute;i Mount Royal, Qu&yacute; kh&aacute;ch sẽ được viếng v&agrave; chụp h&igrave;nh tại Nh&agrave; thờ lớn Saint Joseph&rsquo;s Oratory ở tr&ecirc;n ngọn n&uacute;i Mount Royal thật đặc biệt, lớn v&agrave; cao nhất ở Montreal c&oacute; sức chứa hơn 10.000 người; Nh&agrave; thờ Đức B&agrave; với nhiều kiến tr&uacute;c cổ xưa độc đ&aacute;o.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Trưa:</strong> Qu&yacute; kh&aacute;ch tiếp tục đến với th&agrave;nh phố cổ&nbsp; Quebec&nbsp; - được UNESCO c&ocirc;ng nhận l&agrave; Di sản văn h&oacute;a thế giới năm 1985 với những địa điểm du lịch hấp dẫn như: Quảng Trường Th&agrave;nh Phố , Th&aacute;c Montmorency, L&acirc;u đ&agrave;i Frontenac&nbsp; nguy nga mang đậm phong c&aacute;ch Ph&aacute;p.</p>\r\n<p style="text-align: justify;">\r\n	<strong>Tối:</strong>&nbsp;Qu&yacute; kh&aacute;ch quay lại Montreal, nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 09: MONTREAL &ndash; NEW YORK &ndash; VIỆT NAM &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Sau khi ăn s&aacute;ng, xe đưa Qu&yacute; kh&aacute;ch rời Montreal trở lại New York. Qu&yacute; kh&aacute;ch ra s&acirc;n bay JFK, đ&aacute;p chuyến bay về&nbsp; lại Việt Nam. Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 10: VIỆT NAM</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Về đến s&acirc;n bay quốc tế Nội B&agrave;i. HDV v&agrave; xe đưa đo&agrave;n về điểm đ&oacute;n ban đầu, HDV chia tay đo&agrave;n, Kết th&uacute;c chuyến tham quan Bờ Đ&ocirc;ng Hoa Kỳ v&agrave; Canada.&nbsp;</p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">GI&Aacute; TOUR TRỌN G&Oacute;I: 88.850.000 VND&nbsp;/ KH&Aacute;CH</span></span></h2>\r\n<p style="text-align: center;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">(&nbsp; Gi&aacute; &aacute;p dụng cho đo&agrave;n từ 15 kh&aacute;ch trở l&ecirc;n )</span></span></strong></p>\r\n<h2 style="text-align: center;">\r\n	&nbsp;</h2>\r\n', '', '', 'Tour du lịch Hà Nội - Mỹ - Canada', 'du lịch mỹ, du lịch canada, du lịch canada từ mỹ, tour du lịch canada, tour du lịch mỹ', 'Tour du lịch Hà Nội - Mỹ - Canada sẽ đưa quý khách tới với những điểm đến hấp dẫn nhất tại Canada và Mỹ', '<ul>\r\n	<li style="text-align: justify;">\r\n		V&eacute; m&aacute;y bay khứ hồi (Quốc Tế ) theo đo&agrave;n.</li>\r\n	<li style="text-align: justify;">\r\n		Thuế s&acirc;n bay 2 nước + thuế an ninh + ph&iacute; xăng dầu</li>\r\n	<li style="text-align: justify;">\r\n		Kh&aacute;ch sạn ti&ecirc;u chuẩn 3* ( 2 kh&aacute;ch/ph&ograve;ng), bố tr&iacute; 3 kh&aacute;ch/ph&ograve;ng</li>\r\n	<li style="text-align: justify;">\r\n		C&aacute;c bữa ăn theo chương tr&igrave;nh.</li>\r\n	<li style="text-align: justify;">\r\n		Xe du lịch đạt chuẩn đưa đ&oacute;n tham quan suốt chương tr&igrave;nh.</li>\r\n	<li style="text-align: justify;">\r\n		V&eacute; tham quan theo chương tr&igrave;nh.</li>\r\n	<li style="text-align: justify;">\r\n		Hướng dẫn vi&ecirc;n địa phương nhiệt t&igrave;nh v&agrave; chuy&ecirc;n nghiệp.</li>\r\n	<li style="text-align: justify;">\r\n		Bảo hiểm du lịch Quốc tế mức bồi thường tối đa 75,000 USD /trường hợp.</li>\r\n	<li style="text-align: justify;">\r\n		Qu&agrave; tặng từ&nbsp;<a href="http://mixtourist.com.vn/" target="_blank">Mix Tourist</a></li>\r\n</ul>\r\n', '<div>\r\n	<ul>\r\n		<li>\r\n			Visa Mỹ v&agrave; Canada</li>\r\n		<li>\r\n			C&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c ngo&agrave;i chương tr&igrave;nh, h&agrave;nh l&yacute; qu&aacute; cước.</li>\r\n		<li>\r\n			Phụ thu ph&ograve;ng đơn.</li>\r\n		<li>\r\n			Tip cho hướng dẫn vi&ecirc;n địa phương v&agrave; l&aacute;i xe 07usd/kh&aacute;ch/ng&agrave;y.</li>\r\n	</ul>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n');
INSERT INTO `tour` (`id`, `tour_quoc_te`, `DanhMuc1Id`, `DanhMuc2Id`, `danhmuc_multi`, `promotion`, `packages`, `name`, `name_url`, `count_down`, `code`, `img`, `price_sales`, `price`, `price_2`, `price_3`, `price_4`, `price_5`, `price_6`, `durations`, `departure`, `destination`, `departure_time`, `vehicle`, `hotel`, `summary`, `highlights`, `schedule`, `price_list`, `content`, `list_img`, `title`, `keyword`, `description`, `inclusion`, `exclusion`) VALUES
(20, 1, 7, 49, '', 1, 1, 'Tour du lịch Hà Nội - Canada', 'tour-du-lich-ha-noi-canada', '', '', '/view/admin/Themes/kcfinder/upload/images/tour/city4-vancouver-du-lich-canada-mixtourist.jpg', '', '67500000', '', '', '', '', '', '5 Ngày 4 Đêm', '1', 'VANCOUVER, VICTORIA, TAIPEI, HÀ NỘI', '', 'Máy bay, ô tô', 3, '<p>\r\n	<font color="#333333" face="arial, Verdana, Helvetica, sans-serif"><span style="font-size: 13px;">Tour du lịch H&agrave; Nội - Canada sẽ mang đến cho qu&yacute; kh&aacute;ch những trải nghiệm kh&oacute; qu&ecirc;n tại đất nước Canada xinh đẹp</span></font></p>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	Tour du lịch H&agrave; Nội - Canada sẽ mang đến cho qu&yacute; kh&aacute;ch những trải nghiệm kh&oacute; qu&ecirc;n tại đất nước Canada xinh đẹp</p>\r\n<ul>\r\n	<li>\r\n		&quot;H&ograve;n Ngọc Th&aacute;i B&igrave;nh Dương&quot; - Vancouver</li>\r\n	<li>\r\n		Th&agrave;nh phố Victoria</li>\r\n</ul>\r\n', '<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 01: H&Agrave; NỘI &ndash; VANCOUVER&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( &nbsp;ăn tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Xe v&agrave; HDV của&nbsp;Mix Tourist&nbsp;l&agrave;m thủ tục cho Qu&yacute; kh&aacute;ch tại s&acirc;n bay Nội B&agrave;i đi Vancouver qu&aacute; cảnh tại Đ&agrave;i Bắc. Nghỉ đ&ecirc;m tr&ecirc;n m&aacute;y bay.</p>\r\n<p style="text-align: justify;">\r\n	Do vượt tuyến đổi ng&agrave;y Đo&agrave;n đến s&acirc;n bay Vancouver International Airport - YVR c&ugrave;ng ng&agrave;y l&uacute;c chiều tối. L&agrave;m thủ tục nhập cảnh. Sau khi ăn tối, Qu&yacute; kh&aacute;ch về kh&aacute;ch sạn, nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi. Nghỉ đ&ecirc;m tại Vancouver.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 02: VANCOUVER &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Tham quan những địa danh nổi tiếng &quot;H&ograve;n Ngọc Th&aacute;i B&igrave;nh Dương&quot; - Vancouver: Tham quan v&agrave; chụp ảnh b&ecirc;n ngo&agrave;i Canada Place, tham quan Stanley Park, Gastown, China Town, vườn hoa Queen Elizabeth Park, trải nghiệm cảm gi&aacute;c tr&ecirc;n cầu treo Capilano Suspension Bridge nổi tiếng cổ nhất, cao nhất v&agrave; d&agrave;i nhất thế giới. Nghỉ đ&ecirc;m tại Vancouver.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 3: VICTORIA &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Ăn s&aacute;ng, trưa, tối )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Đo&agrave;n ăn s&aacute;ng tại kh&aacute;ch sạn, sau đ&oacute; ngồi ph&agrave; 1 giờ&nbsp; đồng hồ ( Tr&ecirc;n ph&agrave; c&oacute; nhiều tiện nghi cửa h&agrave;ng qu&agrave; lưu niệm, qu&aacute;n caf&eacute;, boong ngắm cảnh rất hiện đại v&agrave; ho&agrave;nh tr&aacute;ng )&nbsp; Qu&yacute; kh&aacute;ch tới Victoria : Tham quan v&agrave; &nbsp;chụp ảnh lưu niệm tại Cảng Inner, tham quan v&agrave; chụp ảnh b&ecirc;n ngo&agrave;i kh&aacute;ch sạn Empress, T&ograve;a nh&agrave; quốc hội, tiếp tục tham quan Phố người Hoa cổ nhất khu vực Bắc Mỹ, vịnh Oak, vườn hoa Butchart Garden. L&ecirc;n ph&agrave; trở lại Vancouver l&uacute;c chiều. Nghỉ đ&ecirc;m tại Vancouver.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 04: VANCOUVER &ndash; TAIPEI &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ( Tr&ecirc;n m&aacute;y bay )</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Sau bữa s&aacute;ng, đo&agrave;n khởi h&agrave;nh ra s&acirc;n bay đ&aacute;p chuyến bay về Việt Nam ( nối chuyến tại Taipei &ndash; Đ&agrave;i Loan ). Quý khách nghỉ ngơi, ăn u&ocirc;́ng tr&ecirc;n máy bay.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">NG&Agrave;Y 05: TAIPEI &ndash; H&Agrave; NỘI</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Qu&yacute; kh&aacute;ch nối chuyển về H&agrave; Nội. Đo&agrave;n về tới s&acirc;n bay Nội B&agrave;i, HDV chia tay đo&agrave;n, kết th&uacute;c chương tr&igrave;nh du lịch H&agrave; Nội -&nbsp; Canada 5 ng&agrave;y 4 đ&ecirc;m.</p>\r\n', '<h2 style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">GI&Aacute; TOUR TRỌN G&Oacute;I: &nbsp;67.500.000 VND/ KH&Aacute;CH</span></span></h2>\r\n<p style="text-align: center;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">( Gi&aacute; &aacute;p dụng cho đo&agrave;n từ 15 kh&aacute;ch trở l&ecirc;n )</span></span></strong></p>\r\n', '', '', 'Tour du lịch Hà Nội - Canada', 'du lịch canada, tour du lịch canada, tour du lịch vancouver, tour du lịch canada vancouver, du lịch victoria canada', 'Tour du lịch Hà Nội - Canada sẽ mang đến cho quý khách những trải nghiệm khó quên tại đất nước Canada xinh đẹp', '<ul>\r\n	<li style="text-align: justify;">\r\n		V&eacute; m&aacute;y bay quốc tế: H&agrave; Nội - Vancouver - H&agrave; Nội&nbsp; ( Hạng phổ th&ocirc;ng )</li>\r\n	<li style="text-align: justify;">\r\n		Thuế s&acirc;n bay v&agrave; Ph&iacute; an ninh.</li>\r\n	<li style="text-align: justify;">\r\n		Kh&aacute;ch sạn ti&ecirc;u chuẩn quốc tế 3 &nbsp;(2 kh&aacute;ch / ph&ograve;ng, nếu lẻ nam hoặc nữ ngủ 3 người/ ph&ograve;ng )</li>\r\n	<li style="text-align: justify;">\r\n		C&aacute;c bữa ăn theo chương tr&igrave;nh (Việt, Hoa, Nhật, Th&aacute;i, Canada).</li>\r\n	<li style="text-align: justify;">\r\n		Xe đưa đ&oacute;n theo chương tr&igrave;nh (tối đa 11h / ng&agrave;y).</li>\r\n	<li style="text-align: justify;">\r\n		V&eacute; tham quan: cầu treo Capilano, nh&agrave; thờ Montreal, Vừơn hoa Butchart Garden, Ph&agrave; BC</li>\r\n	<li style="text-align: justify;">\r\n		HDV tiếng Việt chuy&ecirc;n nghiệp hướng dẫn suốt tuyến ( tối đa 11h / ng&agrave;y)</li>\r\n	<li style="text-align: justify;">\r\n		Lệ ph&iacute; visa (nộp LSQ, dịch hồ sơ, thư mời): 4.000.000đ (ph&iacute; n&agrave;y kh&ocirc;ng được ho&agrave;n lại nếu qu&yacute; kh&aacute;ch bị từ chối visa).</li>\r\n	<li style="text-align: justify;">\r\n		Bảo hiểm du lịch du lịch với mức đền b&ugrave; tối đa l&agrave; 1.000.000.000 vnd / kh&aacute;ch cho c&aacute;c chi ph&iacute; y tế về tai nạn v&agrave; ốm đau...</li>\r\n	<li style="text-align: justify;">\r\n		Qu&agrave; tặng từ Mix Tourist</li>\r\n</ul>\r\n', '<ul>\r\n	<li style="text-align: justify;">\r\n		Tip cho hướng dẫn vi&ecirc;n v&agrave; t&agrave;i xế tại địa phương (6 USD / ng&agrave;y x 4 ng&agrave;y = 24 USD / kh&aacute;ch)</li>\r\n	<li style="text-align: justify;">\r\n		V&eacute; m&aacute;y bay nội địa v&agrave; phương tiện di chuyển ra s&acirc;n bay về thăm th&acirc;n (nếu c&oacute;).</li>\r\n	<li style="text-align: justify;">\r\n		Chi ph&iacute; dời ng&agrave;y v&agrave; đổi chặng bay (nếu c&oacute;).</li>\r\n	<li style="text-align: justify;">\r\n		Chi ph&iacute; trả cho HDV v&agrave; t&agrave;i xế phục vụ ng&ograve;ai giờ (nếu c&oacute; y&ecirc;u cầu).</li>\r\n</ul>\r\n'),
