(2, 501, 253, 'Tour Sapa 2 ngày 1 đêm', 'SP02', 'azbooking.vn', 'Vui, HDV khá nhiệt tình, tổng quan tour rất OK', '2018-04-07', 1, 9, 1, 10, 1, 0, 1, 9, 1, 9, 1, 9, 1, 9.2, 'Cảm ơn AZbooking, lần sau nhất định sẽ hợp tác', 0, 'Cô Tô hoặc Đà Nẵng', '2018-04-17 22:21:07', '2018-04-17 22:33:32', 0),
(3, 501, 335, 'Tour du lịch Hà Nội - Cô Tô - Cô Tô Con - Hà Nội', 'CT3N', 'azbooking.vn', 'Đoàn hơn 30 người mà đi ai cũng vui vẻ, đặc biệt cảm ơn cty, teambuiding thực sự rất vui. Nhất định sẽ hợp tác tần sau ^^', '2018-04-07', 1, 9, 1, 10, 1, 0, 1, 9, 1, 8, 1, 9, 1, 9, '', 0, 'Đà Nẵng', '2018-04-18 09:14:41', '0000-00-00 00:00:00', 0),
(4, 532, 280, 'Tour du lịch Thái Lan 4 ngày 3 đêm bay Lion Air', '', 'azbooking.vn', 'Tôi tham gia chuyến du lịch Thái Lan ngày 29/03 của AZbooking, giá ngon bổ rẻ, hướng dẫn nhiệt tình cực kì luôn, tối còn dẫn chúng tôi đi chơi bét nhè. Xe ở Thái còn đẹp và đi êm nữa. Chắc chắn mình sẽ chọn AZbooking cho những lần vi vu tới', '2018-03-29', 1, 9, 1, 10, 1, 0, 1, 9, 1, 8, 1, 10, 1, 9.2, '', 0, '', '2018-04-18 09:49:28', '0000-00-00 00:00:00', 0),
(5, 501, 290, 'TOUR DU LỊCH ÚC: HN/HCM - SYDNEY - CANBERRA - MELBOURNE 7N6Đ', 'UC01', 'azbooking.vn', 'Chuyến đi thật tuyệt vời. HDV vô cùng nhiệt tình, tour 5*', '2018-04-10', 1, 10, 1, 10, 1, 0, 0, 10, 1, 10, 1, 10, 1, 10, '', 0, 'Tư vấn tôi tour Mỹ tháng 10', '2018-04-18 11:25:16', '0000-00-00 00:00:00', 0),
(6, 533, 245, 'Tour du lịch Myanmar: Hà Nội-Yangon-Bago-Golden Rock 4 ngày 3 đêm', 'MM02', 'azbooking.vn', 'Tôi vừa có chuyến đi Myanmar 4N3Đ với Azbooking, thật sự ấn tượng với chuyến đi này, mọi thứ từ ăn uống, khách sạn đến các điểm tham quan, công ty đều sắp xếp rất hợp lý. Tôi ấn tượng nhất là ngày đi lên chùa Golden Rock bằng xe chuyên dụng của chính phủ.', '2018-04-10', 1, 10, 1, 10, 1, 0, 1, 10, 1, 10, 1, 10, 1, 10, '', 0, 'Sắp tới tôi định đi du lịch Maldives. Nếu công ty có chương trình này liên hệ tôi nhé.', '2018-04-18 11:54:10', '2018-04-19 20:43:02', 0),
(7, 534, 285, 'Tour Thái Lan CAO CẤP SIÊU TIẾT KIỆM: TP.HCM - BANGKOK - PATTAYA 5N4D', 'HCM - TL52', 'azbooking.vn', 'Giá cả hợp lý, ks tốt, HDV nhiệt tình, công ty chuẩn bị dịch vụ rất chu đáo', '2018-04-09', 1, 9, 0, 9, 0, 0, 0, 9, 0, 8, 0, 10, 0, 9, 'Chuyến đi rất thú vị và vui vẻ', 0, 'chắc chắn sẽ quay trở lại và đồng hàng cùng AZ Booking', '2018-04-18 17:11:49', '2018-04-19 20:43:03', 0),
